INSERT INTO static_template VALUES ('19', '0', '982350343', '942004100', 'frameset; top / left-page', '', '  # cat=basic/dims; type=small; label=Top Frame: The height of the topframe\r\ntopFrameHeight = 150\r\n  # cat=basic/dims; type=small; label=Left Frame: The width of the left frame\r\nleftFrameWidth = 150\r\n', 'frameset = PAGE\r\nframeset2 = PAGE\r\npage = PAGE\r\nleft = PAGE\r\ntop = PAGE\r\nframeset.typeNum = 0\r\nframeset2.typeNum = 4\r\npage.typeNum = 1\r\nleft.typeNum = 2\r\ntop.typeNum = 3\r\n\r\nframeset.frameSet {\r\n  rows = {$topFrameHeight},*\r\n  params = {$_stdFramesetParams}\r\n  1 = FRAME\r\n  1.obj = top\r\n  1.params = {$_stdFrameParams} scrolling="NO"\r\n  2 = FRAME\r\n  2.obj = frameset2\r\n  2.params = {$_stdFrameParams} scrolling="NO"\r\n}\r\n\r\nframeset2.frameSet  {\r\n  params = {$_stdFramesetParams}\r\n  cols = {$leftFrameWidth},*\r\n  1 = FRAME\r\n  1.obj = left\r\n  1.params = {$_stdFrameParams} scrolling="NO"\r\n  2 = FRAME\r\n  2.obj = page\r\n  2.params = {$_stdFrameParams} scrolling="AUTO"\r\n}', '', '');

