INSERT INTO static_template VALUES ('24', '0', '982350343', '942101940', 'frameset (+); top / left-adr-page', '19', '  # cat=basic/dims; type=small; label=Top Frame: The height of the topframe\r\ntopFrameHeight = 150\r\n  # cat=basic/dims; type=small; label=Left Frame: The width of the left frame\r\nleftFrameWidth = 150\r\n  # cat=basic/dims; type=small; label="Address Frame": The height of the addressframe\r\nadrFrameHeight = 100', '# PLEASE NOTE, this is an extension to the \r\n# static template  "frameset; top / left-page"\r\n\r\n\r\nadr = PAGE\r\nadr.typeNum = 10\r\n\r\nframeset2.frameSet.cols = {$leftFrameWidth},*\r\nframeset2.frameSet.params = {$_stdFramesetParams}\r\nframeset2.frameSet.1 = FRAMESET\r\nframeset2.frameSet.1 {\r\n  params = {$_stdFramesetParams}\r\n  rows = *,{$adrFrameHeight}\r\n  1 = FRAME\r\n  1.obj = left\r\n  1.params = {$_stdFrameParams} scrolling="NO"\r\n  2 = FRAME\r\n  2.obj = adr\r\n  2.params = {$_stdFrameParams} scrolling="NO"\r\n}\r\nframeset2.frameSet.2 = FRAME\r\nframeset2.frameSet.2.obj = page\r\nframeset2.frameSet.2.params = {$_stdFrameParams} scrolling="AUTO" \r\n', 'PLEASE NOTE, this is an extension to the static template  "frameset; top / left-page"', '');

INSERT INTO static_template VALUES ('27', '0', '982350343', '942108726', 'frameset; page-bottom', '', '  # cat=basic/dims; type=small; label=Bottom Frame: The height of the bottom frame\r\nbottomFrameHeight = 65', 'frameset = PAGE\r\npage = PAGE\r\nbottom = PAGE\r\nframeset.typeNum = 0\r\npage.typeNum = 1\r\nbottom.typeNum = 3\r\n\r\nframeset.frameSet.rows = *,{$bottomFrameHeight}\r\nframeset.frameSet.params = {$_stdFramesetParams}\r\nframeset.frameSet {\r\n  1 = FRAME\r\n  1.obj = page\r\n  1.params = {$_stdFrameParams} scrolling="AUTO"\r\n  2 = FRAME\r\n  2.obj = bottom\r\n  2.params = {$_stdFrameParams} scrolling="NO"\r\n}\r\n', '', '');

INSERT INTO static_template VALUES ('49', '0', '997726582', '956910541', 'template; FIRST', '50,43,68', 'frame {\r\n    # cat=basic/dims; type=int+; label=Frame, "page" width: Enter the width of the frame with the page content (name="page"). This also applies to the "top" and "bottom" frame\r\n  pageW = 700\r\n    # cat=basic/dims; type=int+; label=Frame, "page+top" height: Enter the total height of the "page" and "top" frames\r\n  pageH = 380\r\n    # cat=basic/dims; type=int+; label=Frame, "top" height: Enter the height of the "top" frame (menu)\r\n  menuH = 70\r\n    # cat=basic/dims; type=int+; label=Frame, "bottom" height: Enter the height of the "bottom" frame\r\n  bottomH = 20\r\n}\r\n\r\n\r\n// This is backGround colors defined:\r\n  # cat=basic/color/a; type=color; label=Background color, page frame: Background color of the page in the right frame.\r\nbgCol = white\r\nbgCol {\r\n    # cat=basic/color/b; type=color; label=Background color, outer frames: Background color of the blank pages in the outer frames that surround the content and menu frames in the center.\r\n  outer = #62748e\r\n    # cat=basic/color/b; type=color; label=Background color, bottom frame: Background color of the bottom frame with the special menu.\r\n  bottom = #7b8ca5\r\n    # cat=basic/color/b; type=color; label=Background color, top frame: Background color of the top frame with the main menu and site logo.\r\n  top = #7b8ca5\r\n}\r\n\r\n\r\n  # cat=basic/color/l; type=color; label=Link-color: Link color defined in <body>-tag\r\nlink = \r\n  # cat=basic/color/l; type=color; label=Active Link-color: Active link color defined in <body>-tag\r\nalink = \r\n  # cat=basic/color/l; type=color; label=Visited Link-color: Visited link color defined in <body>-tag\r\nvlink = \r\n\r\nfile {\r\n    # cat=basic/file; type=file[IMAGE_EXT]; label=Logo image, top frame: Logo image above the menu in the top frame.\r\n  toplogo = \r\n   # cat=basic/file; type=file[IMAGE_EXT]; label=Background image, page frame\r\n  bgImg_page = \r\n   # cat=basic/file; type=file[IMAGE_EXT]; label=Background image, top frame\r\n  bgImg_top = \r\n   # cat=basic/file; type=file[IMAGE_EXT]; label=Background image, bottom frame\r\n  bgImg_bottom = \r\n   # cat=basic/file; type=file[IMAGE_EXT]; label=Background image, outer frames\r\n  bgImg_outer = \r\n}\r\n\r\n  # cat=basic/color; type=color; label=Top frame, line color: Color of the separator line between the level-1 and level-2 menu in the top frame.\r\nsepLineColor = black\r\n  # cat=basic/dims; type=int[1-1000]; label=Top frame, left margin: The distance in pixels from left edge to the menu and logo image in the top frame. 2-3 pixels are added for the table-borders.\r\nleftMarginTop = 20\r\n\r\nrightCol {\r\n    #cat=right/enable; type=options[getNews,getBorder]; label=Column content: Choose "getNews" to select content from a specific page (defined below here) or "getBorder" to display the "Border"-content of the current page.\r\n  borderContent = getNews\r\n    #cat=right/dims; type=int+; label=Max Image Width: This indicates that maximum number of pixels (width) a block of images inserted as content is allowed to consume. NOTE: This is specific for the right column!\r\n  maxImageWidth = 150\r\n    #cat=right/typo; type=small; label=Bodytext font face\r\n  textStyle_face =\r\n    #cat=right/typo; type=int[1-5]; label=Bodytext font size\r\n  textStyle_size =\r\n    #cat=right/typo; type=small; label=Bodytext font color\r\n  textStyle_color =\r\n    #cat=right/typo; type=int[1-5]; label=Default Header type: Enter the number of the header layout to be used by default. \r\n  defaultHeaderType =\r\n}\r\n\r\n// PID\'s...\r\n  # cat=right/enable/z2; type=int+; label=News Content PID: If you selected "getNews" above, this is where you enter the page-id of the page from which to fetch the "news" content for the column.\r\nstyles.content.getNews.newsPid = \r\n\r\n  # cat=basic//a; type=int+; label=Bottom menu PID: Enter the page-id of the page that holds the pages, that should appear as a menu in the bottom frame\r\nbottomMenuPid = \r\n\r\n  # cat=basic/dims/a; type=int+; label=Width, main content: Width of the main content on the page.\r\nwidth.content = 400\r\n  # cat=basic,right/dims/a; type=int+; label=Width, border content: Width of the border content cell to the right on the page. NOTE: This option also appears in the category RIGHT, that specifically deals with the characteristics of the border-frame to the right on the page.\r\nwidth.rightCol = 160\r\n  # cat=basic/dims/a; type=int+; label=Width, gap: The gap is the distance between the main- and border-content in the page frame.\r\nwidth.gap = 105\r\n\r\n\r\nstyles.content.imgtext.maxW = 400\r\n\r\nPAGE_TARGET = page\r\ncontent.pageFrameObj = page\r\n\r\n\r\n\r\n\r\n\r\n\r\n## TSConstantsEditor Config\r\nTSConstantEditor.basic {\r\n  header = Standard Template "FIRST"\r\n  description = FIRST is a frame-based template where the menu, bottom and pageframe is encasulated in another frameset that centers the page in the browser. // The menus are graphical and in two levels with a separate general menu in the bottom-frame. You can also define content for the second column to the right on the page. \r\n  bulletlist = Image in the top. (9) // 2-level graphical menu: Select TrueType-font, color, size, optional bullet, letter-spacing. (14) // 1-level graphical menu for general pages: Select TrueType-font, color, size, optional bullet, letter-spacing. (14) // Frameset: Select background-color and dimensions of the page-frame, bottom-frame and topframe. (11,12,13) // Optional static column with news from a page, you define. (2)\r\n  image = gfx/FIRST_basic.gif\r\n  1=width.content,styles.content.imgtext.maxW\r\n  2=width.rightCol\r\n  3=width.gap\r\n  4=frame.pageW\r\n  5=frame.pageH\r\n  6=frame.menuH\r\n  7=frame.bottomH\r\n  8=leftMarginTop\r\n  9=file.toplogo\r\n  10=bgCol,file.bgImg_page\r\n  11=bgCol.outer,file.bgImg_outer\r\n  12=bgCol.bottom,file.bgImg_bottom\r\n  13=bgCol.top,file.bgImg_top\r\n  14=sepLineColor\r\n  15=bottomMenuPid\r\n}\r\n', '# ****************************\r\n# Definition of the menus\r\n# ****************************\r\n\r\ntemp.topMenu = HMENU\r\ntemp.topMenu {\r\n  1 < styles.gmenu.first\r\n  1.target = frameset2\r\n}\r\n\r\ntemp.topSubmenu = HMENU\r\ntemp.topSubmenu {\r\n  entryLevel = 1\r\n  1 < styles.gmenu.first\r\n  1.target = page\r\n}\r\n\r\ntemp.bottomSubmenu = HMENU\r\ntemp.bottomSubmenu {\r\n  special = directory\r\n  special.value = {$bottomMenuPid}\r\n  1 < styles.gmenu.first\r\n  1.target = page\r\n}\r\n\r\n\r\n# ****************************\r\n# Definition of the framesets\r\n# ****************************\r\n\r\nframeset = PAGE\r\nframeset2 = PAGE\r\npage = PAGE\r\nbottom = PAGE\r\ntop = PAGE\r\nback = PAGE\r\n\r\nframeset.typeNum = 0\r\nframeset2.typeNum = 5\r\npage.typeNum = 1\r\nbottom.typeNum = 2\r\ntop.typeNum = 3\r\nback.typeNum = 4\r\n\r\nframeset.frameSet.cols = *,{$frame.pageW},*\r\nframeset.frameSet.params = {$_stdFramesetParams}\r\nframeset.frameSet.1 = FRAME\r\nframeset.frameSet.1.obj = back\r\nframeset.frameSet.1.params = {$_stdFrameParams} scrolling="NO"\r\nframeset.frameSet.3 < frameset.frameSet.1\r\nframeset.frameSet.2 = FRAMESET\r\nframeset.frameSet.2 {\r\n  params = {$_stdFramesetParams}\r\n  rows = *,{$frame.pageH},{$frame.bottomH},*\r\n  1 = FRAME\r\n  1.obj = back\r\n  1.params = {$_stdFrameParams} scrolling="NO"\r\n\r\n  2 = FRAME\r\n  2.obj = frameset2\r\n  2.params = {$_stdFrameParams} scrolling="NO"\r\n  \r\n  3 = FRAME\r\n  3.obj = bottom\r\n  3.params = {$_stdFrameParams} scrolling="NO"\r\n\r\n  4 < .1\r\n}\r\n\r\nframeset2.frameSet {\r\n  params = {$_stdFramesetParams}\r\n  rows = {$frame.menuH},*\r\n\r\n  1 = FRAME\r\n  1.obj = top\r\n  1.params = {$_stdFrameParams} scrolling="NO"\r\n\r\n  2 = FRAME\r\n  2.obj = page\r\n  2.params = {$_stdFrameParams} scrolling="AUTO" \r\n}\r\n\r\n\r\n# ****************************\r\n# Definition of the pages\r\n# ****************************\r\n\r\nback.bodyTag = <body bgColor="{$bgCol.outer}">\r\nback.bgImg = {$file.bgImg_outer}\r\n\r\n\r\ntop.bodyTag = <body bgColor="{$bgCol.top}">\r\ntop.bodyTagMargins = 0\r\ntop.bgImg = {$file.bgImg_top}\r\ntop {\r\n  wrap = <table border=0 cellspacing=2 cellpadding=0> | </table>\r\n  10 = IMAGE\r\n  10.file = {$file.toplogo}\r\n  10.wrap = <tr><td><img src="clear.gif" width={$leftMarginTop} height=1></td><td>|</td></tr>\r\n  20 < temp.topMenu\r\n  20.wrap = <tr><td></td><td>|</td></tr>\r\n  30 = TEXT\r\n  30.value = <tr><td></td><td bgColor="{$sepLineColor}">{$_clear}</td></tr>\r\n  40 < temp.topSubmenu\r\n  40.wrap = <tr><td></td><td>|</td></tr>\r\n}\r\n\r\n\r\nbottom.bodyTag = <body bgColor="{$bgCol.bottom}">\r\nbottom.bodyTagMargins = 0\r\nbottom.bgImg = {$file.bgImg_bottom}\r\nbottom {\r\n  wrap = <div align=right><table border=0 cellspacing=2 cellpadding=0 align=right> | </table></div>\r\n  40 < temp.bottomSubmenu\r\n  40.wrap = <tr><td></td><td>|</td></tr>\r\n}\r\n\r\npage.bodyTag < styles.content.bodyTag\r\npage.bodyTagMargins = 0\r\npage.bgImg = {$file.bgImg_page}\r\npage.10 = CTABLE\r\npage.10 {\r\n  offset = 20,20\r\n  tableParams = border=0 cellspacing=0 cellpadding=0 width="400"\r\n  cWidth = {$width.content}\r\n  cMargins = 0,0,{$width.gap},0\r\n  c.5 < styles.content.editPanelPage\r\n  c.10 < styles.insertContent\r\n  c.20  < styles.content.lastUpdate\r\n  c.30  < styles.content.typo3_powered\r\n  c.TDParams = valign=top\r\n\r\n  rm.9 = LOAD_REGISTER\r\n  rm.9.maxImageWidth = {$rightCol.maxImageWidth}\r\n  rm.9.textStyle_face = {$rightCol.textStyle_face}\r\n  rm.9.textStyle_size = {$rightCol.textStyle_size}\r\n  rm.9.textStyle_color = {$rightCol.textStyle_color}\r\n  rm.9.defaultHeaderType = {$rightCol.defaultHeaderType}\r\n\r\n  rm.10 < styles.content.{$rightCol.borderContent}\r\n  rm.20 = CLEARGIF\r\n  rm.20.width={$width.rightCol}\r\n  rm.30 = RESTORE_REGISTER\r\n  rm.TDParams = valign=top\r\n}\r\n\r\n\r\npage.config.admPanel = 1\r\nconfig.intTarget = {$PAGE_TARGET}\r\npage.headerData.999 < plugin.meta\r\nframeset.headerData.999 < plugin.meta\r\n\r\n', 'Standard-template\r\n\r\nPage-centeret Frames-based template with a logoimage in the top connected to a 2-level graphical menu below. \r\nContent is in two columns.', '');

INSERT INTO static_template VALUES ('40', '0', '1017228407', '955295691', 'styles.gmenu.bug', '', 'styles.gmenu.bug >\r\nstyles.gmenu.bug {\r\n  pageFrameObj = page\r\n  menuFrameObj = left\r\n\r\n    # cat=menu/color; type=color; label=Menu background color\r\n  bgCol = #cccccc\r\n\r\n    # cat=menu/dims; type=int+; label=Height of menu items (pixels)\r\n  itemH = 20\r\n    # cat=menu/dims; type=int+; label=Width of menu items/menu (pixels)\r\n  itemW = 180\r\n    # cat=menu/dims; type=int+; label=Lenght of total menu: Enter the total lenght of the menu (pixels). This should be the same as the height of any background image under the total menu. (See styles.gmenu.bug.bgImg !)\r\n  length = 300\r\n\r\n    # cat=menu/typo; type=file[ttf]; label=Font file: TrueType (ttf) font for the menu.\r\n  file.fontFile = \r\n    # cat=menu/typo; type=int[1-40]; label=Font size: Font size on menu items\r\n  fontSize = 15\r\n    # cat=menu/typo; type=color; label=Font color: Font color on menu items\r\n  fontColor = white\r\n    # cat=menu/typo; type=offset; label=Text offset: Enter the offset of the text from the upper-left corner on the menu items\r\n  fontOffset = 20,15\r\n    # cat=menu/typo; type=options[,upper,lower]; label=Text case: You can force the text on the menu items to be either lowercase or uppercase.\r\n  case =\r\n\r\n    # cat=menu/typo; type=boolean[SHADOW]; label=Drop Shadow: Click this option if you want a drop shadow from the text on the menu items.\r\n  shadow = \r\n    # cat=menu/typo; type=int[1-100]; label=Drop Shadow opacity: If you enabled the Drop Shadow, you can adjust the opacity of the shadow to make it more or less dominant.\r\n  shadow.opacity = 100\r\n\r\n    # cat=menu/typo; type=boolean; label=Rollover: Click this option if you want a rollover (mouseover) effect on the menu items.\r\n  RO = 0\r\n    # cat=menu/typo; type=color; label=Rollover, font color: Enter the rollover font color of the text on the menu item.\r\n  RO.fontColor =\r\n\r\n\r\n    # cat=menu/file/n_bgimg; type=boolean[IMAGE]; label=Background image: By enabling this option, you can choose a background image for the whole menu. The image will expand downwards over the background of all the items!\r\n  bgImg = \r\n    # cat=menu/file/n_bgimg; type=file[IMAGE_EXT]; label=Background image, file: Choose the background image. NOTE: You should probably set the value of ".length" to the height in pixels of this image!\r\n  bgImg.file.image = \r\n    # cat=menu/file/n_bgimg; type=file[IMAGE_EXT]; label=Background image, mask: Choose an optional mask for the background image. By this mask, the background image is "superimposed" onto the background color of the menu. White areas make the image visible, dark areas hide the image.\r\n  bgImg.file.mask = \r\n\r\n\r\n\r\n    # cat=menu/file/o_impose; type=boolean[IMAGE]; label=Color impose: You can impose a color through a mask on every single menu item. The color is imposed underneath the text but on top of the background image, if any.\r\n  maskImg = \r\n    # cat=menu/file/o_impose; type=color; label=Color impose, color: Enter the color\r\n  maskImg.imposeCol = #165D89\r\n    # cat=menu/file/o_impose; type=file[IMAGE_EXT]; label=Color impose, mask: Grayscale imagefile to mask the color through. White areas make the color visible, dark areas hide the color\r\n  maskImg.file.mask = \r\n\r\n\r\n    # cat=menu/file/p_rimpose; type=boolean[IMAGE]; label=Rollover, impose: You can impose a color through a mask on every single rollover menu item, if you like. The color is imposed just underneath the text. NOTE: You must select the rollover-option in the Typography-section!\r\n  RO.imposeImg = IMAGE\r\n    # cat=menu/file/p_rimpose; type=color; label=Rollover, impose color: In addition to changing the color of the text on the menu item, you can also impose a color through a mask. You could use this to make a line or box appear on the menu item when the mouse cursor moves over it.\r\n  RO.imposeCol = white\r\n    # cat=menu/file/p_rimpose; type=file[IMAGE_EXT]; label=Rollover, mask: Grayscale imagefile to mask the impose color through. White areas make the color visible, dark areas hide the color.\r\n  RO.file.mask = \r\n\r\n\r\n\r\n    # cat=menu//a; type=wrap; label=Wrap, menu items: This HTML-code is wrapped around every item in the menu. Normally you would use a <br>-tag here.\r\n  itemWrap = | <br>\r\n\r\n    # cat=menu//x; type=int[0-256]; label=Reduce colors: Number of indexed colors on menu items. If your menu items end up with weird colors, you can try to enter a higher number of colors. But the graphics-files will also increase their filesize.\r\n  reduceColors = 16\r\n\r\n}\r\n\r\n\r\n## TSConstantsEditor Config\r\nTSConstantEditor.menu {\r\n  header = styles.gmenu.bug\r\n  description = This graphical menu is powerful in many aspects. Especially because you can put a background image in the menu which will expand over all the items. See the image below.\r\n  bulletlist = Background image for the whole menu (4) // Dropshadow on Truetype font page titles (5) // Rollover (mouseover) color (6) // You can use grayscale masks to mask (impose) a color onto the menu item. Use this to generate geometric icons and alike on the items. (7)\r\n  image = gfx/BUG_menu.gif\r\n  1=styles.gmenu.bug.itemH\r\n  2=styles.gmenu.bug.itemW\r\n  3=styles.gmenu.bug.length\r\n  4=styles.gmenu.bug.bgImg\r\n  5=styles.gmenu.bug.shadow,styles.gmenu.bug.file.fontFile,styles.gmenu.bug.fontSize,styles.gmenu.bug.fontColor,styles.gmenu.bug.fontOffset,styles.gmenu.bug.case\r\n  6=styles.gmenu.bug.RO,styles.gmenu.bug.RO.fontColor\r\n  7=styles.gmenu.bug.RO.imposeImg\r\n}\r\n', 'styles.gmenu.bug >\r\nstyles.gmenu.bug = HMENU\r\nstyles.gmenu.bug {\r\n  1 = GMENU\r\n  1.target = {$styles.gmenu.bug.pageFrameObj}\r\n  1.applyTotalH = 10\r\n  1.removeObjectsOfDummy = 20,23\r\n  1.min = ,{$styles.gmenu.bug.length}\r\n  1.max = ,{$styles.gmenu.bug.length}\r\n  1.NO {\r\n    wrap = {$styles.gmenu.bug.itemWrap}\r\n    XY = {$styles.gmenu.bug.itemW},{$styles.gmenu.bug.itemH}\r\n    backColor = {$styles.gmenu.bug.bgCol}\r\n    reduceColors={$styles.gmenu.bug.reduceColors}\r\n\r\n    10 = {$styles.gmenu.bug.bgImg}\r\n    10.file = {$styles.gmenu.bug.bgImg.file.image}\r\n    10.mask = {$styles.gmenu.bug.bgImg.file.mask}\r\n\r\n    20 = {$styles.gmenu.bug.maskImg}\r\n    20.file = GIFBUILDER\r\n    20.file {\r\n      XY = {$styles.gmenu.bug.itemW},{$styles.gmenu.bug.length}\r\n      backColor = {$styles.gmenu.bug.maskImg.imposeCol}\r\n    }\r\n    20.mask = {$styles.gmenu.bug.maskImg.file.mask}\r\n\r\n    25 = {$styles.gmenu.bug.shadow}\r\n    25 {\r\n      textObjNum = 30\r\n      offset = 1,1\r\n      color = black\r\n      blur = 60\r\n      opacity = {$styles.gmenu.bug.shadow.opacity}\r\n    }\r\n\r\n    30 = TEXT\r\n    30.text.field = nav_title // title\r\n    30.text.case = {$styles.gmenu.bug.case}\r\n    30.offset = {$styles.gmenu.bug.fontOffset}\r\n    30.fontFile = {$styles.gmenu.bug.file.fontFile}\r\n    30.fontSize = {$styles.gmenu.bug.fontSize}\r\n    30.fontColor = {$styles.gmenu.bug.fontColor}\r\n    30.niceText = 1.5\r\n  }\r\n  1.RO < .1.NO\r\n  1.RO = {$styles.gmenu.bug.RO}\r\n  1.RO {\r\n    23 = {$styles.gmenu.bug.RO.imposeImg}\r\n    23.file = GIFBUILDER\r\n    23.file {\r\n      XY = {$styles.gmenu.bug.itemW},{$styles.gmenu.bug.itemH}\r\n      backColor = {$styles.gmenu.bug.RO.imposeCol}\r\n    }\r\n    23.mask = {$styles.gmenu.bug.RO.file.mask}\r\n    30.fontColor = {$styles.gmenu.bug.RO.fontColor}\r\n  }\r\n}', 'Graphical menu, one level.\r\n\r\nFeatures a background image expanding over all items plus rollover fontcolor and normal + rollover color overlay.\r\n\r\nUsed with template BUG', '');

INSERT INTO static_template VALUES ('41', '0', '1032385945', '955297796', 'template: BUG', '7,51,40,43,68', '  # cat=basic/color/a; type=color; label=Background color, all frames: This is the background color of all frames.\r\nbgCol = #eeeeff\r\n  # cat=basic/color/l; type=color; label=Link-color: Link color defined in <body>-tag\r\nlink = \r\n  # cat=basic/color/l; type=color; label=Active Link-color: Active link color defined in <body>-tag\r\nalink = \r\n  # cat=basic/color/l; type=color; label=Visited Link-color: Visited link color defined in <body>-tag\r\nvlink = \r\n\r\n\r\ntop {\r\n    # cat=basic/file/t; type=file[IMAGE_EXT]; label=Top frame image: This image is put into the top frame of the site. The images is linked to the frontpage by default.\r\n  file.image =\r\n    # cat=basic/file/t; type=string; label=Top frame image, parameters: This is <img>-tag parameters for the top frame image. "width", "height" and "src" is already inserted, any others may be entered here.\r\n  imgParams = hspace="5" vspace="5"\r\n}\r\n\r\n  # cat=basic; type=comment; label=Text menu, page frame: Click this option to enable the text-based menu in the top of the page frame.\r\ncomment_textMenu = \r\n\r\n\r\n// Dimensions\r\ntopFrameHeight = 107\r\nleftFrameWidth = 200\r\nstyles.content.imgtext.maxW = 400\r\n\r\n  # cat=basic/dims; type=int+; label=Content width: This value defines the width in pixels of the table where the content is displayed in the page-frame\r\ncontentWidth = 400\r\n\r\n// Targets\r\nstyles.tmenu.pagetop.target = page\r\nPAGE_TARGET = page\r\ncontent.pageFrameObj = page\r\n\r\n\r\n\r\n\r\n## TSConstantsEditor Config\r\nTSConstantEditor.basic {\r\n  header = Standard Template "BUG"\r\n  description = BUG is a frame-based template. There are 3 frames; a topframe, leftframe (menu) and a page-frame. You can define an image for the topframe and setup the layout of the menu in the leftframe. //The menu lets you define a background-image and rollover-effects. \r\n  bulletlist = Topframe image // Leftframe graphical menu of gif-images: Select TrueType-font, color, size, shadow-opacity, offset. Optional backgroundimage of the menuitems. Optional mouseover-effekt with changed font-color and/or image-overlay (like an arrow).  //  Define size and background-color of the 3 frames, top, left and page. \r\n  image = gfx/BUG_basic.gif\r\n  1=topFrameHeight,bgCol\r\n  2=leftFrameWidth,bgCol\r\n  3=contentWidth,bgCol,styles.content.imgtext.maxW\r\n  4=top.file.image,top.imgParams\r\n  5=comment_textMenu\r\n}\r\n', '# Top frame is defined. Content is an image.\r\n\r\ntop.bodyTag = <body bgColor="{$bgCol}">\r\ntop.10 = IMAGE\r\ntop.10.linkWrap = <a HREF="index.php?{0}" target="_top"> | </a>\r\ntop.10.params = {$top.imgParams}\r\ntop.10.file = {$top.file.image}\r\n\r\n\r\n# Left frame is defined. Content is a menu\r\n\r\nleft.bodyTag = <body bgColor="{$bgCol}">\r\nleft.10 < styles.gmenu.bug\r\n\r\n\r\n# Page frame is defined. Content is a menu\r\n\r\npage.bodyTag < styles.content.bodyTag\r\npage.noLinkUnderline = 0\r\npage.bodyTagMargins = 0\r\n\r\npage.10 = CTABLE\r\npage.10 {\r\n  offset = 5, 0\r\n  tableParams = border=0 width={$contentWidth}\r\n  cWidth={$contentWidth}\r\n  c.5 < styles.content.editPanelPage\r\n  c.10 < styles.insertContent\r\n  c.20 < styles.content.lastUpdate\r\n  c.30 < styles.content.typo3_powered\r\n\r\n\r\n{$comment_textMenu}  tm.10 < styles.tmenu.pagetop\r\n{$comment_textMenu}  tm.TDParams = valign=top\r\n}\r\n\r\n\r\npage.config.admPanel = 1\r\nconfig.intTarget = {$PAGE_TARGET}\r\npage.headerData.999 < plugin.meta\r\nframeset.headerData.999 < plugin.meta\r\n', 'Standard-template\r\n\r\nThis template is a frame-based template with a graphical menu (1 level) to the left. \r\nIn the topframe you can place a picture/logo.\r\nContent may be displayed in one column in the page-frame. \r\n\r\n"styles.gmenu.bug": menu in the left frame\r\n', '');

INSERT INTO static_template VALUES ('51', '0', '996690228', '958662553', 'styles.tmenu.pagetop', '', 'styles.tmenu.pagetop >\r\nstyles.tmenu.pagetop {\r\n    # cat=menu-2; type=int[0-5]; label=Advanced, entry level: This number defines which level in the pagetree is reflected as the first level in the menu. When this number is 1 (one), the menu will display pages on the second level in the pagetree.\r\n  entryLevel = 1\r\n    # cat=menu-2/typo; type=wrap; label=Wrap, items: This HTML-code is wrapped around every single menu item.\r\n  fontWrap =\r\n    # cat=menu-2/typo; type=wrap; label=Wrap, active items: This HTML-code is wrapped around the "active" menu item, if any.\r\n  ACT.fontWrap =\r\n    # cat=menu-2/; type=wrap; label=Wrap, whole menu: This HTML-code is finally wrapped around all the elements (the whole menu)\r\n  wrap =\r\n    # cat=menu-2/links; type=small; label=Target: Enter the link target for the menu. If your site is using frames, "page" would probably be correct. If not, leave blank (or "_top")\r\n  target = \r\n}\r\n\r\n\r\n\r\n## TSConstantsEditor Config\r\nTSConstantEditor.menu-2 {\r\n  header = styles.tmenu.pagetop\r\n  description = This is a little, simple menu of text-links. It\'s designed to be included in the top of pages thus working as a level-2 menu.\r\n  image = gfx/BUG_menu2.gif\r\n  1=styles.tmenu.pagetop.fontWrap\r\n  2=styles.tmenu.pagetop.ACT.fontWrap\r\n}\r\n', 'styles.tmenu.pagetop >\r\nstyles.tmenu.pagetop = HMENU\r\nstyles.tmenu.pagetop.stdWrap.required = 1\r\nstyles.tmenu.pagetop.stdWrap.wrap = {$styles.tmenu.pagetop.wrap}\r\nstyles.tmenu.pagetop.entryLevel = {$styles.tmenu.pagetop.entryLevel}\r\nstyles.tmenu.pagetop.1 = TMENU\r\nstyles.tmenu.pagetop.1 {\r\n  target = {$styles.tmenu.pagetop.target}\r\n  NO.afterImg = media/bullets/dots2.gif |*||*| _\r\n  NO.afterImgTagParams = hspace="4"\r\n  NO.linkWrap = {$styles.tmenu.pagetop.fontWrap}\r\n  NO.ATagBeforeWrap = 1\r\n\r\n  ACT < .NO\r\n  ACT = 1\r\n  ACT.linkWrap = {$styles.tmenu.pagetop.ACT.fontWrap}\r\n}\r\n', 'Textmenu\r\n\r\nThis is a simple horizontal level-2 textmenu that\'s used in the top of pages.\r\n\r\nUsed with template BUG (in pageframe)', '');

INSERT INTO static_template VALUES ('42', '0', '996690228', '955318074', 'styles.img.logo', '', 'styles.img.logo >\r\nstyles.img.logo {\r\n    # cat=logo/dims; type=int[1-1000]; label=Pixeldimensions, X: Pixeldimensions of topframe image (pixels)\r\n  X = 625\r\n    # cat=logo/dims; type=int[1-300]; label=Pixeldimensions, Y: Pixeldimensions of topframe image (pixels)\r\n  Y = 92\r\n\r\n    # cat=logo/color; type=color; label=Image background color\r\n  bgCol = #CBB99C\r\n    # cat=logo/color; type=color; label=Logo color\r\n  logoCol = #165D89\r\n    # cat=logo/color; type=color; label=Image border color: Color of the image-border\r\n  boxColor = black\r\n\r\n    # cat=logo/dims; type=int[0-20]; label=Thickness of border\r\n  boxThickness = 2\r\n\r\n    # cat=logo/dims; type=offset; label=Logo offset: Offset of logo from upper-left corner of the image.\r\n  logoOffset = 10,5\r\n\r\n    # cat=logo; type=options[gif,jpg]; label=Image file format\r\n  format = gif\r\n\r\n    # cat=logo/file; type=file[IMAGE_EXT]; label=Imagefile\r\n  file.image = media/uploads/topimage.jpg\r\n    # cat=logo/file; type=file[IMAGE_EXT]; label=Imagefile Mask; Grayscale mask used to mask the Imagefile onto the colored background of the image. White areas make the image visible, dark areas hide the image.\r\n  file.mask = media/uploads/topmask.jpg\r\n    # cat=logo/file; type=file[IMAGE_EXT]; label=Imagefile, Logo mask: Grayscale mask used to impose the logo-colors onto the image. NOTE: The mask is inverted here!: DARK areas make the color visible, WHITE areas hide the color.\r\n  file.logo = media/uploads/toplogo.jpg\r\n}\r\n', 'styles.img.logo >\r\nstyles.img.logo = GIFBUILDER\r\nstyles.img.logo {\r\n  XY = {$styles.img.logo.X},{$styles.img.logo.Y}\r\n  format = {$styles.img.logo.format}\r\n  quality = 85\r\n  reduceColors = 65\r\n  backColor = {$styles.img.logo.bgCol}\r\n  10 = IMAGE\r\n  10.file = {$styles.img.logo.file.image}\r\n  10.mask = {$styles.img.logo.file.mask}\r\n\r\n  20 = IMAGE\r\n  20.file = GIFBUILDER\r\n  20.file {\r\n    XY = {$styles.img.logo.X},{$styles.img.logo.Y}\r\n    backColor = {$styles.img.logo.logoCol}\r\n  }\r\n  20.mask = GIFBUILDER\r\n  20.mask {\r\n    XY = {$styles.img.logo.X},{$styles.img.logo.Y}\r\n    backColor = white\r\n    10 = IMAGE\r\n    10.file = {$styles.img.logo.file.logo}\r\n    10.offset = {$styles.img.logo.logoOffset}\r\n    20 = EFFECT\r\n    20.value = invert\r\n  }\r\n\r\n  100 = BOX\r\n  100.dimensions = 0,0,{$styles.img.logo.X},{$styles.img.logo.boxThickness}\r\n  100.color = {$styles.img.logo.boxColor}\r\n  110 < .100\r\n  110.align = ,b \r\n \r\n  120 = BOX\r\n  120.dimensions = 0,0,{$styles.img.logo.boxThickness},{$styles.img.logo.Y}\r\n  120.color = {$styles.img.logo.boxColor}\r\n  130 < .120\r\n  130.align = r\r\n}\r\n\r\n\r\n', 'imgResource (using GIFBUILDER)\r\nThe images has a background color onto which "image" is overlayed through "mask". On top of this some color is masked by "logo"\r\n', '');

INSERT INTO static_template VALUES ('43', '0', '1032385577', '955393897', 'content (default)', '45,46,44', '  # cat=advanced/enable; type=boolean; label=Render content RTE compliant?: When this is set, the content will be rendered with each line wrapped in <p>-tags regardless of using stylesheets or not. This is necessary in order to make the content compliant with the Rich Text Editors align-ment functions.\r\ncontent.RTE_compliant = 1\r\ncontent.RTE_compliant.mode = fontwrap\r\n\r\ncontent {\r\n    # cat=content/cHeader/h0; type=int[1-5]; label=Default Header type: Enter the number of the header layout to be used by default. \r\n  defaultHeaderType = 1\r\n    # cat=content/cHeader/a; type=wrap; label=Header wrap: This is the wrap around the headers regardless of individual wraps.\r\n  wrap.header = |<br>\r\n    # cat=content/cHeader/h1; type=wrap; label=Header "Layout1" \r\n  wrap.header1 = <font face="verdana" size=3><b> | </b></font>\r\n    # cat=content/cHeader/h2; type=wrap; label=Header "Layout2"\r\n  wrap.header2 = <font face="verdana" size=2 color="red"><b> | </b></font>\r\n    # cat=content/cHeader/h3; type=wrap; label=Header "Layout3"\r\n  wrap.header3 = <font face="verdana" size=4><b> | </b></font>\r\n    # cat=content/cHeader/h4; type=wrap; label=Header "Layout4"\r\n  wrap.header4 = <font face="verdana" size=5><b> | </b></font>\r\n\r\n  case.header1 = \r\n  case.header2 = \r\n\r\n  wrap2.header1 = \r\n  wrap2.header2 = \r\n  wrap2.header3 = \r\n\r\n    # cat=content/cHeader/x; type=wrap; label=Headerdate: The optional date shown above the header\r\n  wrap.headerDate = <font face="verdana" size=1>|:</font><br>\r\n\r\n    # cat=content/cHeader/s0; type=int[1-3]; label=Default Subheader type: Enter the number of the subheader layout to be used by default.\r\n  defaultSubHeaderType = 3\r\n    # cat=content/cHeader/s1; type=wrap; label=Subheader "Layout 1"\r\n  wrap.subheader1 = <font face="verdana" size=2> | </font>\r\n    # cat=content/cHeader/s2; type=wrap; label=Subheader "Layout 2"\r\n  wrap.subheader2 = <font face="verdana" size=1><b> | </b></font>\r\n    # cat=content/cHeader/s3; type=wrap; label=Subheader "Layout 3"\r\n  wrap.subheader3 = <font face="verdana" size=1 color="navy"><b> | </b></font>\r\n\r\n    # cat=content/cBullets; type=; label=Bulletlist "Layout 2"\r\n  bulletImages.2 = <img src="media/bullets/mark.gif" vspace="2" hspace="2" border="0">\r\n    # cat=content/cBullets; type=; label=Bulletlist "Layout 3"\r\n  bulletImages.3 = <img src="media/bullets/bullet1_n.gif" vspace="2" hspace="2" border="0">\r\n\r\n  headerSpace = 0|5\r\n  space = 0|10\r\n\r\n    # cat=content/cText/b; type=wrap; label=Bodytext wrap: This is the wrap around the whole bodytext part of a content element\r\n  wrap.bodytext = |<br>\r\n    # cat=content/cText/a; type=small; label=Bodytext linebreak: This is the tag used for linebreaks at the end of every paragraph in the bodytext.\r\n  break.bodytext = <br>\r\n    # An alternative body text linebreak code, because this will substitue nl-chars ONLY in the plain bodytext whereas the above will substituted it in all content including the returned content from any typo-tags.\r\n  break.bodytext_parseFunc = \r\n\r\n    # cat=content/cTable; type=color; label=Tables Cellcolor: The color of the tablecells of ordinary tables\r\n  tableCellColor = silver\r\n  tableCellWrap = \r\n    # cat=content/cSplash; type=color; label="Textbox" Background color: This is the background color of the graphical elements in the "Textbox" content element. Normally set this to the color of the page background\r\n  splash.bgCol = white\r\n    # cat=content/cMenu; type=wrap; label="Menu" content element: Elementwrap for the items in the content element type "Menu"\r\n  menuWrap = <b> | </b>\r\n    # cat=content/cMenu; type=wrap; label="Menu" content element, sub: Elementwrap for the subitems in the content element type "Menu". Used by the links to page-sections.\r\n  subMenuWrap = <b> | </b>\r\n  menuAllWrap = <b> | </b><br>\r\n\r\n    # cat=content/language; type=; label="To top" Link: HTML-code used to create a link to the top of the page, if that is enabled for a content element\r\n  linkToTop = <a href="#top">To top</a><br>\r\n\r\n    # cat=advanced/links; type=small; label=Pageframe object: The name of the "contentframe". Normally set to "page" if the site has a frameset. Otherwise it should be an empty value. This is important, as it determines the target of internal links!\r\n  pageFrameObj = page\r\n\r\n  shortcut.tables = tt_content,tt_address,tt_links,tt_guest,tt_board,tt_calender,tt_products,tt_news,tt_rating,tt_poll\r\n}\r\n\r\n', '#\r\n# Object preserved from unsetting after template parsing\r\n# These are meant for referencing\r\n#\r\n\r\nlib.stdheader = COA\r\nlib.stdheader {\r\n  stdWrap.wrapAlign.field = header_position\r\n  stdWrap.typolink.parameter.field = header_link\r\n  stdWrap.fieldRequired = header\r\n  stdWrap.editIcons = tt_content : header, [header_layout | header_position], [header_link|date]\r\n  stdWrap.editIcons.beforeLastTag=1\r\n  stdWrap.editIcons.iconTitle=Edit header\r\n\r\n  stdWrap.if {\r\n    equals.field = header_layout\r\n    value = 100\r\n    negate = 1\r\n  }\r\n\r\n  5 = TEXT\r\n  5.field = date\r\n  5.if.isTrue.field = date\r\n  5.date = d/m Y\r\n  5.wrap = {$content.wrap.headerDate}\r\n  \r\n  10 = CASE\r\n  10.setCurrent {\r\n    field = header\r\n    parseFunc < styles.content.parseFunc\r\n    parseFunc.if.value = 5\r\n    parseFunc.if.equals {\r\n      field = header_layout\r\n      ifEmpty = {$content.defaultHeaderType}\r\n      ifEmpty.override.data = register: defaultHeaderType\r\n    }\r\n    parseFunc.if.negate = 1\r\n  }\r\n  10.key.field = header_layout\r\n  10.key.ifEmpty = {$content.defaultHeaderType}\r\n  10.key.ifEmpty.override.data = register: defaultHeaderType\r\n  10.stdWrap.wrap = {$content.wrap.header}\r\n\r\n  10.1 = TEXT\r\n  10.1.current = 1\r\n  10.1.fontTag = {$content.wrap.header1}\r\n  10.1.case =