.type = *user=input\r\n    20.label = Password:\r\n    20.type = *pass=password\r\n    30.type = logintype=hidden\r\n    30.value = login\r\n    40.type = submit=submit\r\n    40.value = Login\r\n  }\r\n  type.field = pages\r\n  type.listNum = 0\r\n  target = {$styles.content.loginform.target}\r\n  locationData = 0\r\n  hiddenFields.pid = TEXT\r\n  hiddenFields.pid {\r\n    value = {$styles.content.loginform.pid}\r\n    override.field = pages\r\n    override.listNum = 1\r\n  }\r\n}\r\n\r\n[loginUser = *]\r\nstyles.content.loginform {\r\n  dataArray >\r\n  dataArray {\r\n    10.label = Username: <!--###USERNAME###--> \r\n    20 >\r\n    30.type = logintype=hidden\r\n    30.value = logout\r\n    40.type = submit=submit\r\n    40.value = Logout\r\n  }\r\n}\r\n[global]\r\n\r\n  # searchform\r\nstyles.content.searchform < styles.content.mailform\r\nstyles.content.searchform {\r\n  goodMess = {$styles.content.searchform.goodMess}\r\n  redirect >  \r\n  recipient > \r\n  data >\r\n  dataArray {\r\n    10.label = Searchword:\r\n    10.type = sword=input\r\n    20.label = Search in:\r\n    20.type = scols=select\r\n    20.valueArray {\r\n      10.label = Headers and keywords\r\n      10.value = pages.title-subtitle-keywords-description:tt_content.header\r\n      20.label = Pagecontent\r\n      20.value = tt_content.header-bodytext-imagecaption\r\n    }\r\n    30.type = stype=hidden\r\n    30.value = L0\r\n    40.type = submit=submit\r\n    40.value = Search now!\r\n  }\r\n  type.field = pages\r\n  type.listNum = 0\r\n  locationData = HTTP_POST_VARS\r\n  no_cache = 1\r\n}\r\n\r\n  # searchresult\r\nstyles.content.searchresult = SEARCHRESULT\r\nstyles.content.searchresult {\r\n  allowedCols = pages.title-subtitle-keywords-description : tt_content.header-bodytext-imagecaption : tt_address.name-title-address-email-company-city-country : tt_links.title-note-note2-url : tt_board.subject-message-author-email : tt_calender.title-note : tt_products.title-note-itemnumber\r\n  renderObj = COA\r\n  renderObj {\r\n    10 = TEXT\r\n    10.field = pages_title\r\n    10.textStyle {\r\n      face.default = {$styles.content.searchresult.head.face}\r\n      size.default = {$styles.content.searchresult.head.size}\r\n      color.default = {$styles.content.searchresult.head.color}\r\n    }\r\n    10.fontTag = <b> | </b>\r\n    10.typolink.parameter.field = uid\r\n    10.typolink.target = {$styles.content.searchresult.resultTarget}\r\n    10.typolink.additionalParams.data = register:SWORD_PARAMS\r\n    10.typolink.additionalParams.required = 1\r\n    10.typolink.additionalParams.wrap = &no_cache=1\r\n    10.wrap = <tr><td>|</td></tr>\r\n    10.spaceBefore = 6\r\n    20 = COA\r\n    20 {\r\n      10 = TEXT\r\n      10.field = tt_content_bodytext\r\n      10.htmlSpecialChars = 1\r\n      10.stripHtml = 1\r\n    }\r\n    20.stdWrap.crop = 200 | ...\r\n    20.stdWrap.wrap = <tr><td>|</td></tr>\r\n    20.stdWrap.textStyle {\r\n      face.default = {$styles.content.searchresult.face}\r\n      size.default = {$styles.content.searchresult.size}\r\n      color.default = {$styles.content.searchresult.color}\r\n    }\r\n  }\r\n  renderWrap = <table border=0 cellspacing=3> | </table>\r\n  layout = COA\r\n  layout {\r\n    wrap = <table border=0 cellspacing=0 cellpadding=2><tr> | </tr></table> ###RESULT###\r\n    10 = TEXT\r\n    10.value = Searchresult: ###RANGELOW###-###RANGEHIGH### of ###TOTAL###\r\n    10.fontTag = {$styles.content.searchresult.header.wrap}\r\n    10.wrap = <td width=95% bgColor="{$styles.content.searchresult.header.bgCol}"> | </td>\r\n    20 = TEXT\r\n    20.value = ###PREV###&nbsp;&nbsp;&nbsp;###NEXT###\r\n    20.fontTag = {$styles.content.searchresult.header.wrap}\r\n    20.wrap = <td bgColor="{$styles.content.searchresult.header.bgCol}">|</td>\r\n  }\r\n  noResultObj = COA\r\n  noResultObj {\r\n    wrap = <table border=0 cellspacing=0 cellpadding=2><tr> | </tr></table>\r\n    10 = TEXT\r\n    10.value = Sorry, you search was empty!\r\n    10.fontTag = {$styles.content.searchresult.header.wrap}\r\n    10.wrap = <td width=95% bgColor="{$styles.content.searchresult.header.bgCol}"> | </td>\r\n  }\r\n  next = TEXT\r\n  next.value = Next\r\n  prev = TEXT\r\n  prev.value = Prev\r\n  target = {$styles.content.searchresult.target}\r\n  range = 20\r\n}\r\n\r\n\r\nstyles.content.editPanelPage = COA\r\nstyles.content.editPanelPage {\r\n  10 = EDITPANEL\r\n  10 {\r\n# move,new,edit,hide\r\n    allow = toolbar,move,hide\r\n    label = Page: <b>%s</b>\r\n  }\r\n/*\r\n  20 = EDITPANEL\r\n  20 {\r\n    newRecordFromTable = tt_content\r\n    line = 5\r\n    label = New content...\r\n  }\r\n*/\r\n}\r\n\r\n\r\nstyles.content.RTE_encaps_stylesheets {\r\n  encapsTagList = div,p,pre\r\n  remapTag.DIV = P\r\n  nonWrappedTag = P\r\n  innerStdWrap_all.ifEmpty = &nbsp;\r\n  defaultAlign.field = text_align\r\n}\r\n\r\nstyles.content.RTE_encaps_fontwrap < styles.content.RTE_encaps_stylesheets\r\nstyles.content.RTE_encaps_fontwrap {\r\n    # Forcing these attributes onto the encapsulation-tags if any\r\n  addAttributes.P.style=margin:0 0 0 0;\r\n  addAttributes.PRE.style=margin:0 0 0 0; color:navy;\r\n  encapsLinesStdWrap.P.textStyle < styles.content.textStyle\r\n  encapsLinesStdWrap.P.textStyle.align>\r\n}\r\n', 'This is a "library" of standard-objects commonly used - at least in the standard-templates and "Content (default)".\r\n', '');

INSERT INTO static_template VALUES ('76', '0', '1024790859', '997664283', 'language.dk (danish) [DEPRECIATED]', '', '# ***********************\r\n#  Newsletter subscription\r\n# ***********************\r\nplugin.feadmin.dmailsubscription.file.templateFile = media/scripts/fe_admin_dmailsubscrip_dk.tmpl', '## Setting the TypoScript language to danish:\r\nconfig.language = dk\r\n\r\n\r\n# ***********************************\r\n#  Login form as used in \'content (default)\'\r\n# ***********************************\r\ntt_content.login.20.dataArray {\r\n    10.label.lang.dk = Brugernavn:\r\n    20.label.lang.dk = Password:\r\n    40.value.lang.dk = Login\r\n}\r\n\r\n[usergroup = *]\r\ntt_content.login.20.dataArray {\r\n    10.label.lang.dk = Brugernavn: <!--###USERNAME###--> \r\n    20 >\r\n    40.value.lang.dk = Logout\r\n}\r\n[global]\r\n\r\n\r\n\r\n\r\n# ***********************\r\n#  Search dialog object\r\n# ***********************\r\n\r\ntt_content.search.30.dataArray {\r\n    10.label.lang.dk = Sgeord:\r\n    20.label.lang.dk = Sg i:\r\n    20.valueArray {\r\n      10.label.lang.dk = Overskrifter og ngleord\r\n      20.label.lang.dk = Sideindhold\r\n    }\r\n    40.value.lang.dk = Start sgning!\r\n}\r\n\r\n\r\n\r\n# ***********************\r\n#  Search result page object\r\n# ***********************\r\ntt_content.search.20 {\r\n  layout.10.lang.dk = Sgeresultat: ###RANGELOW###-###RANGEHIGH### ud af ###TOTAL###\r\n  noResultObj.10.lang.dk = Beklager, din sgning var tom!\r\n  next.lang.dk = Nste\r\n  prev.lang.dk = Forrige\r\n}\r\n\r\n\r\n\r\n# ***********************\r\n#  Default Guestbook form\r\n# ***********************\r\nplugin.tt_guest.postform.dataArray {\r\n  10.label.lang.dk = Oversk:\r\n  20.label.lang.dk = Din kommentar:\r\n  30.label.lang.dk = Navn:\r\n  40.label.lang.dk = Email:\r\n  50.label.lang.dk = Hjemmeside:\r\n  60.value.lang.dk = Tilfj dig selv\r\n}\r\n\r\n\r\n# ***********************\r\n#  Default List Board form\r\n# ***********************\r\nplugin.tt_board_list.postform.dataArray {\r\n  10.label.lang.dk = Emne:\r\n  20.label.lang.dk = Tekst:\r\n  30.label.lang.dk = Navn:\r\n  40.label.lang.dk = Email:\r\n  50.label.lang.dk = Send email<br>ved svar: \r\n  60.value.lang.dk = Send svar\r\n}\r\nplugin.tt_board_list.postform_newThread.dataArray {\r\n  10.label.lang.dk = Emne:\r\n  20.label.lang.dk = Tekst:\r\n  30.label.lang.dk = Navn:\r\n  40.label.lang.dk = Email:\r\n  50.label.lang.dk = Send email<br>ved svar: \r\n  60.value.lang.dk = Opret ny trd\r\n}\r\nplugin.tt_board_list.postform.badMess = Du skal udfylde disse felter:\r\nplugin.tt_board_list.postform_newThread.badMess = Du skal udfylde disse felter:\r\n\r\n# ***********************\r\n#  Default Tree Board form\r\n# ***********************\r\nplugin.tt_board_tree.postform.dataArray {\r\n  10.label.lang.dk = Emne:\r\n  20.label.lang.dk = Tekst:\r\n  30.label.lang.dk = Navn:\r\n  40.label.lang.dk = Email:\r\n  50.label.lang.dk = Send email<br>ved svar: \r\n  60.value.lang.dk = Send svar\r\n}\r\nplugin.tt_board_tree.postform_newThread.dataArray {\r\n  10.label.lang.dk = Emne:\r\n  20.label.lang.dk = Tekst:\r\n  30.label.lang.dk = Navn:\r\n  40.label.lang.dk = Email:\r\n  50.label.lang.dk = Send email<br>ved svar: \r\n  60.value.lang.dk = Opret ny trd\r\n}\r\nplugin.tt_board_tree.postform.badMess = Du skal udfylde disse felter:\r\nplugin.tt_board_tree.postform_newThread.badMess = Du skal udfylde disse felter:\r\n\r\n\r\n\r\n# ***********************\r\n#  Newsletter subscription\r\n# ***********************\r\nplugin.feadmin.dmailsubscription.evalErrors.email {\r\n  uniqueLocal = Tilsyneladende er du allerede registreret med denne email adresse!\r\n  email = Dette er ikke en korrekt email adresse!\r\n}\r\n', 'This implements danish labels on these standard elements \r\n\r\nFrom \'content (default)\':\r\n\r\n- login form \r\n- search form\r\n- search result\r\n\r\nFrom \'tt_guest\':\r\n\r\n- submit form.\r\n\r\n\r\nFrom \'tt_board\':\r\n\r\n- submit forms.\r\n\r\n\r\n', '');

INSERT INTO static_template VALUES ('62', '0', '1024790859', '983439200', 'plugin.tt_calender [DEPRECIATED]', '', 'plugin.tt_calender >\r\nplugin.tt_calender {\r\n    # cat=plugin.calendar/file; type=file[html,htm,tmpl,txt]; label= Template File: This is a simple htmlfile where each part of the calendar is defined with HTML-code. To see an example with explanations, take a look at media/scripts/calender_template.tmpl which is the default.\r\n  file.templateFile = media/scripts/calendar_template.tmpl\r\n    # cat=plugin.calendar/typo; type=short; label=Font face\r\n  fontFace = verdana\r\n    # cat=plugin.calendar//; type=int+; label=Max age (days): If set, the items will automatically disappear from the calendar after this number of days.\r\n  maxAge =\r\n}', '\r\n## Rendering of calenders\r\n## See TSref and/or tslib/media/scripts/calendar.inc for details about the TypoScript config. \r\nplugin.tt_calender >\r\nplugin.tt_calender = PHP_SCRIPT\r\nplugin.tt_calender {\r\n  file = media/scripts/calendar.inc\r\n  templateFile = {$plugin.tt_calender.file.templateFile}\r\n  pid_list.field = pages\r\n  recursive.field = recursive\r\n  fontFace = {$plugin.tt_calender.fontFace}\r\n  maxAge = {$plugin.tt_calender.maxAge}\r\n  parseFunc < styles.content.parseFunc\r\n}\r\n\r\n\r\n## This enables the record item to display calendar items\r\ntt_calender >\r\ntt_calender < plugin.tt_calender\r\ntt_calender.displayCurrentRecord = 1\r\n', 'PLUGIN: Calendar\r\nAUTHOR: Kasper Skrhj, kasper@typo3.com\r\n\r\nHow to activate this:\r\n- Include this static template in your template.', '');

INSERT INTO static_template VALUES ('46', '0', '996690228', '956069576', 'styles.sitemap.text', '', 'styles.sitemap.text >\r\nstyles.sitemap.text {\r\n    # cat=content/cMenu/sitemap; type=wrap; label=Sitemap, Item Wrap: This wrap is placed around the every item in the sitemap.\r\n  itemWrap = \r\n    # cat=content/cMenu/sitemap; type=wrap; label=Sitemap, Total Wrap: This wrap is placed around the whole sitemap.\r\n  totalWrap = <font face="verdana" size=1> | </font>\r\n    # cat=content/cMenu/sitemap; type=small; label=Sitemap, Target: The target of the links in the sitemap. \r\n  target = _top\r\n}\r\n', 'styles.sitemap.text >\r\nstyles.sitemap.text = HMENU\r\nstyles.sitemap.text.stdWrap.wrap = {$styles.sitemap.text.totalWrap}\r\nstyles.sitemap.text {\r\n  1 = TMENU\r\n  1.NO.allWrap = |<br>\r\n  1.NO.linkWrap = {$styles.sitemap.text.itemWrap}\r\n  1.NO.ATagBeforeWrap = 1\r\n  1.target = {$styles.sitemap.text.target}\r\n  1.expAll = 1\r\n  2 = TMENU\r\n  2.NO.allWrap = &nbsp;&nbsp;&nbsp;&nbsp;|<br>\r\n  2.NO.linkWrap = {$styles.sitemap.text.itemWrap}\r\n  2.NO.ATagBeforeWrap = 1\r\n  2.target = {$styles.sitemap.text.target}\r\n  2.expAll = 1\r\n  3 = TMENU\r\n  3.NO.allWrap = &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;|<br>\r\n  3.NO.linkWrap = {$styles.sitemap.text.itemWrap}\r\n  3.NO.ATagBeforeWrap = 1\r\n  3.target = {$styles.sitemap.text.target}\r\n  3.expAll = 1\r\n  4 = TMENU\r\n  4.NO.allWrap = &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;|<br>\r\n  4.NO.linkWrap = {$styles.sitemap.text.itemWrap}\r\n  4.NO.ATagBeforeWrap = 1\r\n  4.target = {$styles.sitemap.text.target}\r\n  4.expAll = 1\r\n}\r\n\r\n', 'Sitemap object.\r\n\r\nHMENU textbased, used to render a kind of treelike sitemap. \r\n\r\nUsed with "content (default)" for the sitemap-feature.', '');

INSERT INTO static_template VALUES ('47', '0', '999776117', '956072821', 'records (example)', '', '', 'tt_address >\r\ntt_address = COA\r\ntt_address {\r\n  10 = TEXT\r\n  10.field = name\r\n  10.wrap = ADDRESS: | <br>\r\n\r\n  20 = TEXT\r\n  20.field = email\r\n  20.wrap = | <br>\r\n\r\n  30 = TEXT\r\n  30.field = address\r\n  30.wrap = | <br>\r\n}\r\n\r\n\r\ntt_links >\r\ntt_links = COA\r\ntt_links {\r\n  10 = TEXT\r\n  10.field = title\r\n  10.wrap = LINKS: | <br>\r\n}\r\n\r\n\r\ntt_guest >\r\ntt_guest = CASE\r\ntt_guest.key.field = type\r\ntt_guest.1 = COA\r\ntt_guest.1 {\r\n  10 = TEXT\r\n  10.field = title\r\n  10.wrap = | <br>\r\n\r\n  20 = TEXT\r\n  20.value = Nummer 1\r\n  20.wrap = | <br>\r\n}\r\n\r\n\r\ntt_calender >\r\ntt_calender = COA\r\ntt_calender {\r\n  10 = TEXT\r\n  10.field = title\r\n  10.wrap = | <br>\r\n}\r\n\r\n\r\ntt_products >\r\ntt_products = COA\r\ntt_products {\r\n  10 = TEXT\r\n  10.field = title\r\n  10.wrap = | <br>\r\n}\r\n\r\ntt_news >\r\ntt_news = COA\r\ntt_news {\r\n  10 = TEXT\r\n  10.field = title\r\n  10.wrap = | <br>\r\n}\r\n\r\n', 'Provides a very primitive example of how to make render code for tt_* tables. It should only inspire you to get started coding your own content rendering.', '');

INSERT INTO static_template VALUES ('48', '0', '1017228421', '956688832', 'template: MM', '43,68', '  #cat=basic/enable; type=boolean; label=Gap 1, left: Check this option to enable the gap on the left side of the page table\r\nenableGap1 = 1\r\n  #cat=basic/enable; type=boolean; label=Gap 2, between: Check this option to enable the gap between the page- and news-content columns in the page table\r\nenableGap2 = 1\r\n  #cat=basic/enable; type=boolean; label=Gap 3, right: Check this option to enable the gap on the right side of the page table\r\nenableGap3 = 1\r\n\r\n// This excludes the constant "styles.content.imgtext.maxW" from being displayed in the constant editor. The reason why this is desirable is that the REGISTER is used to override this value in this template.\r\n  # cat=\r\nstyles.content.imgtext.maxW =\r\n\r\npageCol {\r\n    #cat=basic/color; type=color; label=Content column bgColor: This is the background color of the table cell where the content is displayed. You would normally regard this as being the "page color".\r\n  bgColor = #cccccc\r\n    # cat=content,basic/cImage/a_maxw1; type=int+; label=Max Image Width: This indicates that maximum number of pixels (width) a block of images inserted as content is allowed to consume.\r\n  maxImageWidth = 450\r\n    #cat=page/dims; type=small; label=Content table width: This is the \'width=\' parameter in the table that holds the content in the content cell of the page table.\r\n  contentTableWidth = 95%\r\n    #cat=page/dims; type=offset; label=Content table offset: This is the offset of the content from the upper-left corner of the content cell of the page table.\r\n  offset = 15,8\r\n}\r\n\r\nrightCol {\r\n    #cat=right,basic/enable; type=boolean; label=Enable right column: Click this option to enable the right column in the page table. This column can be used for "news" from a certain page, but inserted on all pages. You can also configure the column to hold "secondary" content specific for each page. Then content of the column-type "Border" is inserted. NOTE: This option is also found in the dedicated category "RIGHT" that configures the characteristics of this column.\r\n  enable = 1\r\n    #cat=right/enable; type=options[getNews,getBorder]; label=Column content: Choose "getNews" to select content from a specific page (defined below here) or "getBorder" to display the "Border"-content of the current page.\r\n  borderContent = getNews\r\n    #cat=right/color; type=color; label=Right column bgColor: This is the background color of content in the right column.\r\n  bgColor = white\r\n    #cat=right/dims; type=int+; label=Max Image Width: This indicates that maximum number of pixels (width) a block of images inserted as content is allowed to consume. NOTE: This is specific for the right column! The figure should approx. match the width of the image in the top of this column!\r\n  maxImageWidth = 150\r\n    #cat=right/dims; type=offset; label=Content offset: This is the offset of the content from the upper-left corner of the table cell.\r\n  offset = 5,8\r\n    #cat=right/typo; type=small; label=Bodytext font face\r\n  textStyle_face =\r\n    #cat=right/typo; type=int[1-5]; label=Bodytext font size\r\n  textStyle_size =\r\n    #cat=right/typo; type=color; label=Bodytext font color\r\n  textStyle_color =\r\n    #cat=right/typo; type=int[1-5]; label=Default Header type: Enter the number of the header layout to be used by default. \r\n  defaultHeaderType =\r\n}\r\n  # cat=right/enable/zz; type=int+; label=News Content PID: If you selected "getNews" above, this is where you enter the page-id of the page from which to fetch the "news" content for the column.\r\nstyles.content.getNews.newsPid = 0\r\n\r\n\r\n  # cat=basic/color; type=color; label=<body>-tag, Background color: This is the <body>-tag bgColor of the page. NOTE: It\'s not behind the content IN the table, but behind the table that makes up the page in this template. Just like "file.background"\r\nbgCol = black\r\n  # cat=basic/color; type=color; label=Gap, Background color: If there is no background image in the gaps, this color will appear\r\ngapColor = olive\r\n  # cat=basic/color; type=color; label=Border, Color: Color of the border between areas\r\nborderColor = #cccccc\r\n\r\n  # cat=basic/color/l; type=color; label=Link-color: Link color defined in <body>-tag\r\nlink = \r\n  # cat=basic/color/l; type=color; label=Active Link-color: Active link color defined in <body>-tag\r\nalink = \r\n  # cat=basic/color/l; type=color; label=Visited Link-color: Visited link color defined in <body>-tag\r\nvlink = \r\n\r\nfile {\r\n    #cat=basic/file; type=file[IMAGE_EXT]; label=Background Image: This is the body-tag background image of the page. NOTE: It\'s not behind the content in the table, but behind the table that makes up the page in this template.\r\n  background = \r\n    #cat=basic,menu/file; type=file[IMAGE_EXT]; label=Menu imagemap Image: This is the image onto which the menu rendered. NOTE: This image also appears in the MENU-category.\r\n  menuimage = \r\n    #cat=basic,right/file; type=file[IMAGE_EXT]; label=Right Column Image: This is the image in the top-right corner of the page table. NOTE: This image is displayed only if the right column of the template is enabled! NOTE: This image can also be selected in the dedicated RIGHT-category.\r\n  sideimage = \r\n    #cat=basic/file; type=file[IMAGE_EXT]; label=Gap image: Image in the gap between columns.\r\n  gapIcon = \r\n}\r\n\r\n  # cat=basic/dims; type=int+; label=Minimum pageheight: If there is no content on the page to expand it\'s length, this ensures that the page table doesn\'t collapse totally but will always be at least this long (pixels).\r\nminPageH = 100\r\n  # cat=basic/dims; type=int+; label=Gap Width: Width of gaps on the page\r\ngapIcon_width = 10\r\n  # cat=basic/dims; type=int+; label=Border, Thickness: Thickness of the border between areas\r\nborderThickness = 1\r\n\r\n  # cat=basic; type=options[,left,right,center]; label=Alignment of the page: Alignment of the page-table\r\npageAlign = center\r\n\r\nimageMap {\r\n    #cat=menu/typo; type=file[ttf]; label=Font file for the menu\r\n  file.fontFile =\r\n    #cat=menu/typo; type=int[1-50]; label=Font size for the menu\r\n  fontSize = 11\r\n    #cat=menu/typo; type=color; label=Font color for the menu\r\n  fontColor = black\r\n    #cat=menu/dims; type=offset; label=Text offset: Text offset from upper-left corner of the image\r\n  dWorkArea = 7,68\r\n    #cat=menu//x; type=int[1-256]; label=Reduce colors: Number of indexed colors in the menuimage. If your imagemap image ends up with weird colors, you can try to enter a higher number of colors. But the file will also increase in filesize.\r\n  colors = 16\r\n    #cat=menu; type=int[1-15]; label=Max items: Enter the maximum number of menu items in the image map. If there are many items to generate, the server will take a lot of time to generate the menu image. So keep this number low. Recommended: 6-10\r\n  maxItems=\r\n}\r\n\r\nPAGE_TARGET =\r\ncontent.pageFrameObj = \r\n\r\n\r\n\r\n\r\n\r\n## TSConstantsEditor Config\r\nTSConstantEditor.basic {\r\n  header = Standard Template "MM"\r\n  description = MM is a single-page template with a imagemap-based menu in the top. You can select background-images, colors, borderwidths and colors and so on. Furthermore you can define content in a second column. \r\n  bulletlist = Optional right column: Select background color, other standard header- and text-properties. (4) // Background color or optional background image. (6) // Image as background for the image-map menu: Select TrueType-font, color, size, offset on the image (7) // Optional image in the right column (8) // Optional gap between the two columns. Define background color OR background image for the gap. (1,2,3)\r\n\r\n  image = gfx/MM_basic.gif\r\n  1=enableGap1,gapIcon_width,file.gapIcon,gapColor\r\n  2=enableGap2,gapIcon_width,file.gapIcon,gapColor\r\n  3=enableGap3,gapIcon_width,file.gapIcon,gapColor\r\n  4=rightCol.enable\r\n  5=borderThickness,borderColor\r\n  6=file.background,bgCol\r\n  7=file.menuimage\r\n  8=file.sideimage\r\n  9=pageCol.bgColor\r\n}\r\nTSConstantEditor.right {\r\n  description = \r\n  image = gfx/MM_right.gif\r\n  1=rightCol.maxImageWidth\r\n  2=rightCol.offset\r\n  3=file.sideimage\r\n  4=rightCol.borderContent,rightCol.textStyle_face,rightCol.textStyle_size,rightCol.textStyle_color\r\n  5=rightCol.borderContent,rightCol.defaultHeaderType\r\n  6=rightCol.bgColor\r\n}\r\nTSConstantEditor.menu {\r\n  description = \r\n  image = gfx/MM_menu.gif\r\n  1=imageMap.dWorkArea\r\n  2=file.menuimage,imageMap.colors\r\n  3=imageMap.file.fontFile,imageMap.fontSize,imageMap.fontColor,imageMap.maxItems\r\n}\r\n', '# border:\r\ntemp.gap = CLEARGIF\r\ntemp.gap.width = {$gapIcon_width}\r\ntemp.gap.height = {$minPageH}\r\ntemp.gap.wrap = <td bgColor="{$gapColor}" background="{$file.gapIcon}"> | </td>\r\n\r\n# Menu:\r\ntemp.topmenu = HMENU\r\ntemp.topmenu.maxItems = {$imageMap.maxItems}\r\ntemp.topmenu.entryLevel = 0\r\ntemp.topmenu.1 = IMGMENU\r\ntemp.topmenu.1 {\r\n  wrap = |<br>\r\n  main.XY = [10.w] , [10.h]\r\n  main.10 = IMAGE\r\n  main.10.file = {$file.menuimage}\r\n  main.reduceColors = {$imageMap.colors}\r\n\r\n  dWorkArea = {$imageMap.dWorkArea}\r\n\r\n  NO.distrib = textX+10, 0, textX+10, textY+5\r\n  NO.10 = TEXT\r\n  NO.10 {\r\n    text.field = nav_title // title\r\n    fontFile = {$imageMap.file.fontFile}\r\n    fontSize = {$imageMap.fontSize}\r\n    fontColor = {$imageMap.fontColor}\r\n    niceText = 1\r\n    offset = 4,9\r\n    imgMap.explode = 3,2\r\n  }\r\n}\r\n\r\n\r\n\r\n\r\n# ******************\r\n# *** Page Setup ***\r\n# ******************\r\n\r\npage = PAGE\r\npage.typeNum = 0\r\npage.bodyTag < styles.content.bodyTag\r\npage.bgImg = {$file.background}\r\npage.wrap = <div align="{$pageAlign}"><table border="0" cellpadding="0" cellspacing="0" width="1%"><tr bgcolor="{$borderColor}"><td> <table align="center" border="0" cellpadding="0" cellspacing="{$borderThickness}"><tr> | </tr></table> </td></tr></table></div>\r\n\r\npage.10 < temp.gap\r\npage.10.stdWrap.if.isTrue = {$enableGap1}\r\n\r\npage.20 = COA\r\npage.20.wrap = <td bgcolor="{$pageCol.bgColor}" valign="top"> | </td>\r\npage.20 {\r\n  10 < temp.topmenu \r\n  20 = OTABLE\r\n  20.offset = {$pageCol.offset}\r\n  20.tableParams = border=0 cellpadding=0 cellspacing=0 width="{$pageCol.contentTableWidth}"\r\n  20.5 <  styles.content.editPanelPage\r\n  20.9 = LOAD_REGISTER\r\n  20.9.maxImageWidth = {$pageCol.maxImageWidth}\r\n  20.10 < styles.content.get \r\n  20.20  < styles.content.lastUpdate\r\n  20.30 < styles.content.typo3_powered\r\n  20.40 = RESTORE_REGISTER\r\n}\r\n\r\npage.25 < temp.gap\r\npage.25.stdWrap.if.isTrue = {$enableGap2}\r\n\r\npage.30 = COA\r\npage.30.if.isTrue = {$rightCol.enable}\r\npage.30.wrap = <td bgcolor="{$rightCol.bgColor}" valign="top"> | </td>\r\npage.30 {\r\n  10 = IMAGE\r\n  10.file = {$file.sideimage}\r\n  10.wrap = | <br>\r\n  20 = OTABLE\r\n  20.offset = {$rightCol.offset}\r\n  20.tableParams = border=0 cellpadding=0 cellspacing=0 width=95%\r\n  20.9 = LOAD_REGISTER\r\n  20.9.maxImageWidth = {$rightCol.maxImageWidth}\r\n  20.9.textStyle_face = {$rightCol.textStyle_face}\r\n  20.9.textStyle_size = {$rightCol.textStyle_size}\r\n  20.9.textStyle_color = {$rightCol.textStyle_color}\r\n  20.9.defaultHeaderType = {$rightCol.defaultHeaderType}\r\n  20.9.pageColor = {$rightCol.bgColor}\r\n\r\n  20.10 < styles.content.{$rightCol.borderContent}\r\n  20.11 = RESTORE_REGISTER\r\n}\r\n\r\npage.35 < temp.gap\r\npage.35.stdWrap.if.isTrue = {$enableGap3}\r\n\r\n\r\n\r\n\r\npage.config.admPanel = 1\r\nconfig.intTarget = {$PAGE_TARGET}\r\npage.headerData.999 < plugin.meta\r\n\r\n', 'Standard-template\r\n\r\nThis template is a no-frame-template with a image-map menu in the top. \r\nContent may be displayed in a main column and a right column optionally. \r\nYou can define background-images and images in the border between columns.\r\n\r\nBest viewed in 4+ browsers.\r\n\r\n\r\n\r\n\r\n\r\n', '');

INSERT INTO static_template VALUES ('50', '0', '1017228432', '956915453', 'styles.gmenu.first', '', 'styles.gmenu.first >\r\nstyles.gmenu.first {\r\n    # cat=menu/color; type=color; label=Menu background color1: (See below)\r\n  bgCol = #7b8ca5\r\n    # cat=menu/color; type=color; label=Menu background color2: You can define two background colors for this simple graphical menu. The menu items will be alternating between Color1 and Color2 as background color. But if these colors are the same, all menu items will have the same background of course.\r\n  bgCol2 < .bgCol\r\n\r\n    # cat=menu/typo; type=file[ttf]; label=Font file: TrueType (ttf) font for the menu.\r\n  file.fontFile = \r\n    # cat=menu/typo; type=int[1-40]; label=Font size: Font size on menu items\r\n  fontSize = 12\r\n    # cat=menu/typo; type=color; label=Font color: Font color on menu items\r\n  fontColor = white\r\n    # cat=menu/typo; type=offset; label=Text offset: Enter the offset of the text from the upper-left corner on the menu items\r\n  offset = 6,10\r\n    # cat=menu/typo; type=int[0-20]; label=Letter spacing: Enter the spacing between characters\r\n  spacing=1\r\n\r\n    # cat=menu/typo; type=boolean; label=Rollover: Click this option if you want a rollover (mouseover) effect on the menu items.\r\n  RO = 0\r\n    # cat=menu/typo; type=color; label=Rollover, font color: Enter the rollover font color of the text on the menu item.\r\n  RO.fontColor =\r\n    # cat=menu/color; type=color; label=Rollover, Menu background color: Background color of the menu-items when the mouse cursor moves over the item.\r\n  RO.bgCol = #608ca5\r\n\r\n    # cat=menu/typo; type=boolean; label=Active: Click this option if you want a \'Active\' effect on the menu items. \'Active\' means how the item looks when you\'re on that page or a subpage to that page.\r\n  ACT = 0\r\n    # cat=menu/typo; type=color; label=Active, font color: Enter the \'Active\' font color of the text on the menu item.\r\n  ACT.fontColor =\r\n    # cat=menu/color; type=color; label=Active, Menu background color:  Background color of the menu-items when \'Active\'.\r\n  ACT.bgCol = #7b7392\r\n\r\n    # cat=menu/file; type=file[IMAGE_EXT]; label=Bullet image: You can define an imagefile to use as a bullet aligned to the right/top of all menu items except from the last item. This image should probably be a little gif-file with dimensions less than 20 pixels.\r\n  file.bullet =\r\n\r\n    # cat=menu//x; type=int[1-256]; label=Reduce colors: Number of indexed colors on menu items. If your menu items end up with weird colors, you can try to enter a higher number of colors. But the graphics-files will also increase their filesize.\r\n  reduceColors = 64\r\n    # cat=menu/dims; type=int+; label=Item Height: The height in pixels of the menu items\r\n  height = 14\r\n}\r\n\r\n\r\n## TSConstantsEditor Config\r\nTSConstantEditor.menu {\r\n  header = styles.gmenu.first\r\n  description = This is a simple GMENU object with a background color and some text on it. In addition you can add a bullet-image to the right of all items, except the last one.\r\n  image = gfx/FIRST_menu.gif\r\n  1=styles.gmenu.first.bgCol\r\n  2=styles.gmenu.first.bgCol2\r\n  3=styles.gmenu.first.file.bullet\r\n}\r\n', 'styles.gmenu.first >\r\nstyles.gmenu.first = GMENU\r\nstyles.gmenu.first.NO {\r\n  XY = [10.w]+[20.w]+12 , {$styles.gmenu.first.height}\r\n  backColor = |*| {$styles.gmenu.first.bgCol} || {$styles.gmenu.first.bgCol2} |*| \r\n  reduceColors = {$styles.gmenu.first.reduceColors}\r\n  10 = TEXT\r\n  10.text.field = nav_title // title\r\n  10.offset = {$styles.gmenu.first.offset} \r\n  10.fontColor = {$styles.gmenu.first.fontColor}\r\n  10.niceText = 1\r\n  10.fontSize = {$styles.gmenu.first.fontSize}\r\n  10.spacing = {$styles.gmenu.first.spacing}\r\n  10.fontFile = {$styles.gmenu.first.file.fontFile}\r\n\r\n  20 = IMAGE |*||*| _x_\r\n  20.file = {$styles.gmenu.first.file.bullet}\r\n  20.align = r,t\r\n}\r\n\r\nstyles.gmenu.first.RO < styles.gmenu.first.NO\r\nstyles.gmenu.first.RO = {$styles.gmenu.first.RO}\r\nstyles.gmenu.first.RO {\r\n  backColor = {$styles.gmenu.first.RO.bgCol}\r\n  10.fontColor = {$styles.gmenu.first.RO.fontColor}\r\n}\r\n\r\nstyles.gmenu.first.ACT < styles.gmenu.first.NO\r\nstyles.gmenu.first.ACT = {$styles.gmenu.first.ACT}\r\nstyles.gmenu.first.ACT {\r\n  backColor = {$styles.gmenu.first.ACT.bgCol}\r\n  10.fontColor = {$styles.gmenu.first.ACT.fontColor}\r\n}\r\n\r\n', 'This is a GMENU object. \r\nIt features the ability to add a bullet to the right side of the items, except the last item.\r\n\r\nUsed with template FIRST', '');

INSERT INTO static_template VALUES ('52', '0', '1032386037', '960231290', 'template; TU', '53,43,68', '  # cat=basic/file; type=file[IMAGE_EXT]; label=Top image: An image is placed as a banner all the way across the page in the top.\r\nfile.topImage = \r\n\r\n  # cat=basic/color/a; type=color; label=Background color: This is the <body>-tag background color of the page.\r\nbgCol = white\r\n  # cat=basic/color/l; type=color; label=Link-color: Link color defined in <body>-tag\r\nlink = \r\n  # cat=basic/color/l; type=color; label=Active Link-color: Active link color defined in <body>-tag\r\nalink = \r\n  # cat=basic/color/l; type=color; label=Visited Link-color: Visited link color defined in <body>-tag\r\nvlink = \r\n\r\n  # cat=basic/dims; type=offset; label= Page offset: Offset of the whole page-table from upper-left corner of the browser-window\r\npage.offset = 5,5\r\n  # cat=basic/dims; type=int+; label= Page content X-offset: Distance between the menu and the content\r\npage.contentOffset.X = 15\r\n  # cat=basic/dims; type=int+; label= Page content Y-offset: Distance between the top image and the content\r\npage.contentOffset.Y = 10\r\n  # cat=basic/dims; type=int+; label=Content width: This value defines the width in pixels of the table cell where the content is displayed\r\npage.contentWidth = 430\r\n\r\n\r\nPAGE_TARGET = \r\ncontent.pageFrameObj = \r\nstyles.content.imgtext.maxW = 400\r\n\r\n\r\n\r\n\r\n\r\n## TSConstantsEditor Config\r\nTSConstantEditor.basic {\r\n  header = Standard Template "TU"\r\n  description = TU is a single-page template. You can define a topimage and background-images for the 2-level graphical menu to the left on the page. \r\n  bulletlist = Topimage (4) // Define the background-color of the page. (5)\r\n  image = gfx/TU_basic.gif\r\n\r\n  1=page.offset\r\n  2=page.contentOffset.X,page.contentOffset.Y\r\n  3=page.contentWidth,styles.content.imgtext.maxW\r\n  4=file.topImage\r\n  5=bgCol\r\n}\r\n', 'page = PAGE\r\npage.typeNum = 0\r\npage.bodyTagMargins = 0\r\npage.bodyTag < styles.content.bodyTag\r\n\r\npage.10 = OTABLE\r\npage.10 {\r\n  offset = {$page.offset}\r\n  10 = HTML\r\n  10.value = <table border=0 cellspacing=0 cellpadding=0 width=1><tr><td colspan=3>\r\n  20 = IMAGE\r\n  20.file = {$file.topImage}\r\n  20.linkWrap = <a href="index.php?{0}"> |</a>\r\n  30 = HTML\r\n  30.value = </td></tr><tr><td width=1 valign="top" align=left>\r\n\r\n  40 < styles.hmenu.tu\r\n\r\n  45 = HTML\r\n  45.value = </td><td width=1 align=left>\r\n\r\n  47 = CLEARGIF\r\n  47.width = {$page.contentOffset.X}\r\n\r\n  50 = HTML\r\n  50.value = </td><td valign="top">\r\n\r\n  55 = CLEARGIF\r\n  55.height = {$page.contentOffset.Y}\r\n  55.wrap = | <br>\r\n\r\n  59 < styles.content.editPanelPage\r\n  60 < styles.content.get\r\n  65  < styles.content.lastUpdate\r\n  67  < styles.content.typo3_powered\r\n\r\n  70 = HTML\r\n  70.value = </td></tr>\r\n\r\n\r\n  75 = CLEARGIF\r\n  75.width = {$page.contentWidth}\r\n  75.wrap = <tr><td></td><td></td><td> | </td></tr></table>\r\n\r\n}\r\n\r\n\r\n\r\n\r\n\r\npage.config.admPanel = 1\r\nconfig.intTarget = {$PAGE_TARGET}\r\npage.headerData.999 < plugin.meta\r\n\r\n', 'Standard-template\r\n\r\nNo-frames template with a logoimage in the top connected to a 2-level graphical menu to the left. \r\nContent is in one column only.\r\nBackground-images of each menu item - one for each level - can be defined as well as images in the top and bottom of the menubar.', '');

INSERT INTO static_template VALUES ('53', '0', '1017228461', '960231947', 'styles.hmenu.tu', '', 'styles.hmenu.tu >\r\nstyles.hmenu.tu {\r\n    # cat=menu; type=int[0-5]; label=Advanced, entry level: This number defines which level in the pagetree is reflected as the first level in the menu. If 0 (zero) the menu will start with pages from the root of the website.\r\n  entryLevel = 0\r\n    # cat=menu/file/3; type=file[IMAGE_EXT]; label=Image before: The imagefile inserted before the items of the menu.\r\n  file.top = media/buttons/menutop.gif\r\n    # cat=menu/file/4; type=file[IMAGE_EXT]; label=Image after: The imagefile inserted after the items of the menu.\r\n  file.bottom = media/buttons/menubottom.gif\r\n\r\n    # cat=menu//x; type=int[0-256]; label=Reduce colors: Number of indexed colors on menu items. If your menu items end up with weird colors, you can try to enter a higher number of colors. But the graphics-files will also increase their filesize.\r\n  reduceColors = 64\r\n}\r\n\r\nstyles.hmenu.tu.L1 {\r\n    # cat=menu/file/1; type=file[IMAGE_EXT]; label=Level-1, Background image\r\n  file.bgImg = media/buttons/menulevel1.gif\r\n    # cat=menu/typo/1; type=file[ttf]; label=Level-1, Font file\r\n  file.fontFile =\r\n    # cat=menu/typo/1; type=color; label=Level-1, Font color\r\n  fontColor = black\r\n    # cat=menu/typo/1; type=int[1-40]; label=Level-1, Font size\r\n  fontSize = 12\r\n    # cat=menu/typo/1; type=offset; label=Level-1, Text offset: Enter the offset of the text from the upper-left corner of the menu items\r\n  offset = 15,15\r\n    # cat=menu/typo/1; type=color; label=Level-1, Rollover/Active: Enter the color of the text on the menu item when mouseover OR when the item is "active".\r\n  RO.fontColor = navy\r\n}\r\n\r\nstyles.hmenu.tu.L2 {\r\n    # cat=menu/file/2; type=file[IMAGE_EXT]; label=Level-2, Background image\r\n  file.bgImg = media/buttons/menulevel2.gif\r\n    # cat=menu/typo/2; type=file[ttf]; label=Level-2, Font file\r\n  file.fontFile =\r\n    # cat=menu/typo/2; type=color; label=Level-2, Font color\r\n  fontColor = black\r\n    # cat=menu/typo/2; type=int[1-40]; label=Level-2, Font size\r\n  fontSize = 10\r\n    # cat=menu/typo/2; type=offset; label=Level-2, Text offset: Enter the offset of the text from the upper-left corner of the menu items\r\n  offset = 30,8\r\n    # cat=menu/typo/2; type=color; label=Level-2, Rollover/Active: Enter the color of the text on the menu item when mouseover OR when the item is "active".\r\n  RO.fontColor = navy\r\n}\r\n\r\n\r\n\r\n## TSConstantsEditor Config\r\nTSConstantEditor.menu {\r\n  header= styles.hmenu.tu\r\n  description = The graphical menu has two levels with support for shifting font color when the mouse is rolled over the item. The rollover color is also used for the text on the item, when the page is "active" (you\'re on the page).\r\n  bulletlist = Background image for the 1st-level menuitems. Select Truetype-font, color, size and offset. Optionally select Rollover color. (1,5) // Background image for the 2nd-level menuitems. Select Truetype-font, color, size and offset. Optionally select Rollover color. (2,6) // Image in the top of the menu. This adds space from the topimage to the first menuitems. (3) // Image in the bottom of the menu. This ends the menu. (4)\r\n  image = gfx/TU_menu.gif\r\n  1=styles.hmenu.tu.L1.file.bgImg\r\n  2=styles.hmenu.tu.L2.file.bgImg\r\n  3=styles.hmenu.tu.file.top\r\n  4=styles.hmenu.tu.file.bottom\r\n  5=styles.hmenu.tu.L1.file.fontFile,styles.hmenu.tu.L1.fontColor,styles.hmenu.tu.L1.fontSize,styles.hmenu.tu.L1.offset,styles.hmenu.tu.L1.RO.fontColor\r\n  6=styles.hmenu.tu.L2.file.fontFile,styles.hmenu.tu.L2.fontColor,styles.hmenu.tu.L2.fontSize,styles.hmenu.tu.L2.offset,styles.hmenu.tu.L2.RO.fontColor\r\n}\r\n', '\r\nstyles.hmenu.tu >\r\n\r\n# LEVEL 1:\r\nstyles.hmenu.tu = HMENU\r\nstyles.hmenu.tu.entryLevel = {$styles.hmenu.tu.entryLevel}\r\nstyles.hmenu.tu.1 = GMENU\r\nstyles.hmenu.tu.1.NO {\r\n  wrap = |<br>\r\n  XY = [10.w],[10.h]\r\n  reduceColors = {$styles.hmenu.tu.reduceColors}\r\n  10 = IMAGE\r\n  10.file = {$styles.hmenu.tu.L1.file.bgImg}\r\n  20 = TEXT\r\n  20 {\r\n    text.field = nav_title // title\r\n    fontFile = {$styles.hmenu.tu.L1.file.fontFile}\r\n    fontColor = {$styles.hmenu.tu.L1.fontColor}\r\n    fontSize = {$styles.hmenu.tu.L1.fontSize}\r\n    offset = {$styles.hmenu.tu.L1.offset}\r\n    niceText = 1\r\n  }\r\n}\r\nstyles.hmenu.tu.1.RO < styles.hmenu.tu.1.NO\r\nstyles.hmenu.tu.1.RO = 1\r\nstyles.hmenu.tu.1.RO.20.fontColor = {$styles.hmenu.tu.L1.RO.fontColor}\r\nstyles.hmenu.tu.1.ACT < styles.hmenu.tu.1.RO\r\nstyles.hmenu.tu.1.ACT = 1\r\n\r\n# LEVEL 2:\r\nstyles.hmenu.tu.2 = GMENU\r\nstyles.hmenu.tu.2.NO {\r\n  wrap = |<br>\r\n  XY = [10.w],[10.h]\r\n  reduceColors = {$styles.hmenu.tu.reduceColors}\r\n  10 = IMAGE\r\n  10.file = {$styles.hmenu.tu.L2.file.bgImg}\r\n  20 = TEXT\r\n  20 {\r\n    text.field = nav_title // title\r\n    fontFile = {$styles.hmenu.tu.L2.file.fontFile}\r\n    fontColor = {$styles.hmenu.tu.L2.fontColor}\r\n    fontSize = {$styles.hmenu.tu.L2.fontSize}\r\n    offset = {$styles.hmenu.tu.L2.offset}\r\n    niceText = 1\r\n  }\r\n}\r\nstyles.hmenu.tu.2.RO < styles.hmenu.tu.2.NO\r\nstyles.hmenu.tu.2.RO = 1\r\nstyles.hmenu.tu.2.RO.20.fontColor = {$styles.hmenu.tu.L2.RO.fontColor}\r\nstyles.hmenu.tu.2.ACT < styles.hmenu.tu.2.RO\r\nstyles.hmenu.tu.2.ACT = 1\r\n\r\n# Wrapping:\r\nstyles.hmenu.tu.stdWrap.innerWrap {\r\n  cObject = COA\r\n  cObject {\r\n    10 = IMAGE\r\n    10.file = {$styles.hmenu.tu.file.top}\r\n    10.wrap = |<br>\r\n    20 = TEXT\r\n    20.value = |\r\n    30 = IMAGE\r\n    30.file = {$styles.hmenu.tu.file.bottom}\r\n    30.wrap = |<br>\r\n  }\r\n}\r\n', 'Graphical menu, two levels.\r\n\r\nFeatures a simple menu with a rollover font color change and background image for each menu item on both levels.\r\n\r\nUsed with template TU', '');

INSERT INTO static_template VALUES ('54', '0', '997726582', '960237041', 'template; BUSINESS', '23,43,68', 'leftFrameWidth = 150\r\n\r\n  # cat=basic/color/a; type=color; label=Background color, page frame: This is the background color of the page in the right frame.\r\nbgCol = #dddddd\r\n  # cat=basic/color/l; type=color; label=Link-color: Link color defined in <body>-tag\r\nlink = black\r\n  # cat=basic/color/l; type=color; label=Active Link-color: Active link color defined in <body>-tag\r\nalink = gray\r\n  # cat=basic/color/l; type=color; label=Visited Link-color: Visited link color defined in <body>-tag\r\nvlink = black\r\n\r\nmenu {\r\n    # cat=basic/file; type=file[IMAGE_EXT]; label=Background image, left frame:  Background image of the page in the left frame (menu frame)\r\n  file.bgImg = \r\n    # cat=basic/color/a; type=color; label=Background color, menu frame: This is the background color of the page in the left frame (menu frame).\r\n  bgCol = #c0c0c0\r\n\r\n    # cat=menu/file; type=file[IMAGE_EXT]; label=Bullet on level-2\r\n  file.bullet = media/bullets/bullet1_n.gif\r\n    # cat=menu/file; type=file[IMAGE_EXT]; label=Bullet on level-2, Rollover\r\n  file.bullet_RO = media/bullets/bullet1_h.gif\r\n\r\n    # cat=menu/typo; type=wrap; label=Wrap, level-1: This is the wrap-code around the menu page-titles on level-1\r\n  L0.fontWrap = <font face=Arial size=2 color=black><b> |</b></font>\r\n    # cat=menu/typo; type=wrap; label=Wrap, level-2: This is the wrap-code around the menu page-titles on level-2\r\n  L1.fontWrap = <font face=Verdana size=1 color=black> | </font>\r\n    # cat=menu/links; type=boolean; label=Link level-1?: You can enable links on the level-1 menu items, if you want these links to show a page. By default they don\'t.\r\n  L0.noLink = 0\r\n}\r\n\r\npage {\r\n   # cat=basic/file; type=file[IMAGE_EXT]; label=Background image, page frame\r\n  file.bgImg  =\r\n   # cat=basic/color; type=color; label=Color of horizontal line\r\n  lineCol = #666666\r\n   # cat=basic/typo; type=wrap; label=Wrap, Level-1 title: In the top of the page, the title of Level-1 in the menu is always shown. This is the wrap around that title.\r\n  L0.titleWrap = <b><font FACE=Verdana size=5 color=#000066><i> | </i></font></b>\r\n   # cat=basic/typo; type=wrap; label=Wrap, Level-2 title: In the top of the page, the title of Level-2 in the menu is always shown. This is the wrap around that title.\r\n  L1.titleWrap = <font FACE=Verdana size=2 color=#000066><b> | </b></font>\r\n   # cat=basic/dims/z1; type=int+; label=Thickness of horizontal line: Pixels\r\n  lineHeight = 2\r\n   # cat=basic/dims/z2; type=int[1-1000]; label=Menu, X-offset: Space between menu and left border of browserwindow\r\n  menuXDistance = 5\r\n   # cat=basic/dims/z2; type=int+; label=Menu, Y-offset: Space between menu and logo in left frame\r\n  menuYDistance = 0\r\n}\r\n\r\n # cat=basic/file; type=file[IMAGE_EXT]; label=Logo-file, menu frame: This is the logo-file inserted in the top of the menu frame.\r\nfile.logo = media/uploads/typologo.gif\r\n # cat=advanced; type=boolean; label=Logo-file, GIFBUILDER-obj: If this flag is true, the logo is rendered as a GIFBUILDER-gif and is fixed at 150x80 pixels. Else the image is just inserted (must be 150x80 pixels in order to fit nicely). Although the default value is "true" you probably want to disable this and make sure yourself that the logofile keeps the dimensions.\r\nGIFBUILDER_logo = 1\r\n\r\n # cat=basic/dims; type=int+; label=Content width: Enter the width of the table where the content is put. This should approx. be that same (little less) as the Max Image Width!\r\ncontentWidth = 500\r\n # cat=basic/dims; type=offset; label=Content Offset: Enter the offset in pixels by which the content is offset from top-left. \r\ncontentOffset = 10,10\r\n\r\nstyles.content.imgtext.maxW = 500\r\nstyles.content.typo3_powered.number=1\r\n\r\nPAGE_TARGET = page\r\ncontent.pageFrameObj = page\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n## TSConstantsEditor Config\r\nTSConstantEditor.basic {\r\n  header = Standard Template "BUSINESS"\r\n  description = BUSINESS is a framebased template in a very simple layout, based on text. There are 2 frames; a menuframe (to the left) and a page-frame.   \r\n  bulletlist = Left-frame image in the top. The dimensions are fixed to 150 x 80 pixels (normally you are free to choose the dimensions yourself!) (4) //2-level textual menu. Select: font-teg-properties like face, size and color for each level. Select: graphical bullet for the 2nd level and optionally a mouseover-bullet. //The pagetitles of the 1st and 2nd level for a given page is displayed in the top. Select font-tag-properties for each title (like with the menus)  (5 and 6) //Define background-images of each frame, the left and page frame. Also select the width of the left frame. (1) //Optional ruler across the page. Select the color. (8)\r\n  image = gfx/BUSINESS_basic.gif\r\n  1=leftFrameWidth,menu.file.bgImg,menu.bgCol\r\n  2=page.file.bgImg,bgCol\r\n  3=contentOffset\r\n  4=file.logo\r\n  5=page.L0.titleWrap\r\n  6=page.L1.titleWrap\r\n  7=contentWidth,styles.content.imgtext.maxW\r\n  8=page.lineCol\r\n}\r\nTSConstantEditor.menu {\r\n  description = \r\n  image = gfx/BUSINESS_menu.gif\r\n  1=menu.L0.fontWrap,menu.L0.noLink\r\n  2=menu.L1.fontWrap\r\n  3=menu.file.bullet\r\n  4=menu.file.bullet_RO\r\n}\r\n', 'temp.leftmenu = HMENU\r\ntemp.leftmenu.1 = TMENU\r\ntemp.leftmenu.1.target = page\r\ntemp.leftmenu.1 {\r\n  expAll = 1\r\n  wrap = <table width="300" border=0 cellspacing=0 cellpadding=0><tr><td><img src="clear.gif" width=1 hspace=200 height=1 vspace=3 border=0></td></tr> | </table>\r\n  NO.linkWrap = {$menu.L0.fontWrap}\r\n  NO.doNotLinkIt = {$menu.L0.noLink}\r\n  NO.allWrap = <tr><td><img src="clear.gif" width=1 height=1 vspace=3 border=0><br> | </td></tr>\r\n}\r\ntemp.leftmenu.2 = TMENU\r\ntemp.leftmenu.2 {\r\n  wrap = <tr><td nowrap> | </td></tr>\r\n  target = page\r\n  NO {\r\n    beforeImg =  {$menu.file.bullet}\r\n    beforeROImg = {$menu.file.bullet_RO}\r\n    beforeImgTagParams = hspace=2\r\n    RO = 1\r\n    after = <br>\r\n    ATagBeforeWrap = 1\r\n    linkWrap= {$menu.L1.fontWrap}\r\n  }\r\n}\r\n\r\n\r\n\r\nleft.bgImg = {$menu.file.bgImg}\r\nleft.bodyTag = <body bgColor="{$menu.bgCol}" alink="{$alink}" vlink="{$vlink}" link="{$link}">\r\nleft.noLinkUnderline = 1\r\n\r\nleft.70.if.isTrue = {$GIFBUILDER_logo}\r\nleft.70 = IMAGE\r\nleft.70.file = GIFBUILDER\r\nleft.70.file {\r\n  XY = 150,80\r\n  backColor = {$menu.bgCol} \r\n  10 = IMAGE\r\n  10.file = {$file.logo}\r\n}\r\nleft.70.params = border=0\r\nleft.70.wrap = <table border=0 cellpadding="0" cellspacing="0"><tr><td colspan=2> | </td></tr>\r\nleft.70.linkWrap = <a href="index.php?{0}" target="_top">\r\n\r\nleft.75.if.isTrue = {$GIFBUILDER_logo}\r\nleft.75.if.negate = 1\r\nleft.75 = IMAGE\r\nleft.75.file = {$file.logo}\r\nleft.75.params = border=0\r\nleft.75.wrap = <table border=0 cellpadding="0" cellspacing="0"><tr><td colspan=2> | </td></tr>\r\nleft.75.linkWrap = <a href="index.php?{0}" target="_top">\r\n\r\nleft.80 = HTML\r\nleft.80.value = <tr><td></td><td bgcolor={$page.lineCol}><img src="clear.gif" width=1 height={$page.lineHeight} border=0></td></tr>    \r\nleft.80.value.if.isTrue = {$page.lineHeight}\r\n\r\nleft.90 = HTML\r\nleft.90.value = <tr><td><img src="clear.gif" width={$page.menuXDistance} height=1 border=0></td><td>\r\nleft.95 = CLEARGIF\r\nleft.95.height={$page.menuYDistance}\r\nleft.100 < temp.leftmenu\r\nleft.110 = HTML\r\nleft.110.value = </td></tr></table>\r\n\r\n\r\npage.bgImg = {$page.file.bgImg}\r\npage.bodyTag < styles.content.bodyTag\r\npage.10 = COBJ_ARRAY\r\npage.10 {\r\n  wrap = <table border="0" cellpadding="0" cellspacing="0" width="2"><tr><td><img src="clear.gif" width=1 height=1 border=0></td><td><img src="clear.gif" hspace=200 width=1 height=1 border=0></td></tr> | </table>\r\n  1 = COBJ_ARRAY\r\n  1 {\r\n    wrap = <tr><td><img src="clear.gif" width=1 vspace=39 height=1 border=0></td><td valign="top"> | </td></tr>\r\n    1 = HTML\r\n    1.value = <table border="0" cellpadding="0" cellspacing="0"><tr><td><img src="clear.gif" width=1 hspace=10 vspace=7 height=1 border=0></td><td><img src="clear.gif" width=1 hspace=40 height=1 border=0></td><td><img src="clear.gif" width=1 height=1 border=0></td></tr><tr><td></td><td colspan=2>\r\n\r\n    5 = TEXT\r\n    5.data = levelTitle:1\r\n    5.wrap = {$page.L0.titleWrap}\r\n\r\n    10 = HTML\r\n    10.value = </td></tr><tr><td></td><td></td><td>\r\n\r\n    15 = TEXT\r\n    15.data = levelTitle:2\r\n    15.wrap = {$page.L1.titleWrap}\r\n    20 = HTML\r\n    20.value = </td></tr></table>\r\n  }\r\n\r\n  10 = HTML\r\n  10.value = <tr><td colspan=2 bgcolor={$page.lineCol}><img src="clear.gif" width=1 height=2 border=0></td></tr>\r\n  10.value.if.isTrue = {$page.lineHeight}\r\n\r\n  15 = TEXT\r\n  15.value = <tr><td colspan=2 valign="top">\r\n\r\n  20 = CTABLE\r\n  20.c.5 < styles.content.editPanelPage\r\n  20.c.10 < styles.content.get\r\n  20.c.20  < styles.content.lastUpdate\r\n  20.c.30  < styles.content.typo3_powered\r\n  20.cWidth={$contentWidth}\r\n  20.offset = {$contentOffset}\r\n  \r\n  30 = TEXT\r\n  30.value = </td></tr>\r\n}\r\n\r\n\r\n\r\npage.config.admPanel = 1\r\nconfig.intTarget = {$PAGE_TARGET}\r\npage.headerData.999 < plugin.meta\r\nframeset.headerData.999 < plugin.meta\r\n', 'Standard-template\r\n\r\nThis template is a frame-based template with a textbased menu (2 levels) in the left frame. \r\nIn the top of the left frame you can place a logo. The top of the page-frame displays the title of the current page.\r\n\r\nContent is displayed in only one column.\r\nYou can define background-images in the frames.\r\n', '');

INSERT INTO static_template VALUES ('57', '0', '984935803', '961515307', 'cSet (default)', '', '# *******************************\r\n# *** Overriding constants\r\n# *******************************\r\n\r\n\r\ncontent {\r\n  defaultHeaderType = 1\r\n  wrap.header1 = <font face="{$cSet.fontFace}" size={$cSet.size3} color="{$cSet.color}"><b> | </b></font>\r\n  wrap.header2 = <font face="{$cSet.fontFace}" size={$cSet.size2} color="{$cSet.color1}"><b> | </b></font>\r\n  wrap.header3 = <font face="{$cSet.fontFace}" size=4 color="{$cSet.color}"><b> | </b></font>\r\n  wrap.header4 = <font face="{$cSet.fontFace}" size=5 color="{$cSet.color}"><b> | </b></font>\r\n\r\n  case.header1 = \r\n  case.header2 = \r\n\r\n  wrap2.header1 = \r\n  wrap2.header2 = \r\n  wrap2.header3 = \r\n\r\n  wrap.headerDate = <font face="{$cSet.fontFace}" size={$cSet.size1} color="{$cSet.color2}"><b>|:</b></font><br>\r\n\r\n  defaultSubHeaderType = 3\r\n  wrap.subheader1 = <font face="{$cSet.fontFace}" size={$cSet.size2} color="{$cSet.color}"> | </font>\r\n  wrap.subheader2 = <font face="{$cSet.fontFace}" size={$cSet.size1} color="{$cSet.color}"><b> | </b></font>\r\n  wrap.subheader3 = <font face="{$cSet.fontFace}" size={$cSet.size1} color="{$cSet.color2}"><b> | </b></font>\r\n\r\n  headerSpace = 0|5\r\n  space = 0|10\r\n  wrap.bodytext = |<br>\r\n  linkToTop = <div align="right"><a href="#top"><font color="{$cSet.color2}" face="{$cSet.fontFace}" size={$cSet.size1}><b>To top</b></font></a></div><br>\r\n  menuWrap = <b><font color="{$cSet.color2}" face="{$cSet.fontFace}" size={$cSet.size2}> | </font></b>\r\n  subMenuWrap = <b><font color="{$cSet.color1}" face="{$cSet.fontFace}" size={$cSet.size1}> | </font></b>\r\n  tableCellColor = {$cSet.tableCellColor}\r\n  tableCellWrap = <b>|</b>\r\n  splash.bgCol = {$cSet.pageColor}\r\n}\r\n\r\n\r\nstyles.content.textStyle {\r\n  face = {$cSet.fontFace.text}\r\n  size={$cSet.size2}\r\n  color =  {$cSet.color}\r\n  color1 = {$cSet.color1}\r\n  color2 = {$cSet.color2}\r\n}\r\n\r\nstyles.content.tableStyle {\r\n  color1 = {$cSet.color1}\r\n  color2 = {$cSet.color2}\r\n}\r\n\r\nstyles.content.links.wrap = <b>|</b>\r\nstyles.content.links.sword = <font color="red"><b>|</b></font>\r\n\r\nstyles.content.uploads.wrap  < content.menuWrap\r\n\r\nstyles.content.frames {\r\n  1.bgCol = {$cSet.tableCellColor}\r\n  2.bgCol = {$cSet.tableCellColor}\r\n  2.borderCol = {$cSet.color}\r\n  2.borderThickness = 1\r\n  ruler.lineColor = {$cSet.color}\r\n}\r\n\r\nstyles.content.imgFrames {\r\n  bgCol = {$cSet.pageColor}\r\n  bottomCol = black\r\n}\r\n\r\nstyles.content.imgtext {\r\n  captionWrap = <font face="{$cSet.fontFace}" size={$cSet.size1} color="{$cSet.color1}"><b> | </b></font>\r\n}\r\n\r\nstyles.content.mailform {\r\n  fieldWrap = \r\n  labelWrap = <font face="{$cSet.fontFace}" size={$cSet.size2} color="{$cSet.color}"><b> | </b></font>\r\n  radioWrap = <font face="{$cSet.fontFace}" size={$cSet.size1} color="{$cSet.color}"><b> | </b></font>\r\n  REQ.labelWrap = <font face="{$cSet.fontFace}" size={$cSet.size2} color={$cSet.color2}><b> | </b></font>\r\n  border=5\r\n}\r\n\r\nstyles.content.searchresult {\r\n  face = {$cSet.fontFace}\r\n  size={$cSet.size2}\r\n  color = {$cSet.color2}\r\n  head.face = {$cSet.fontFace}\r\n  head.size={$cSet.size3}\r\n  header.wrap = <font face="{$cSet.fontFace}" size={$cSet.size3} color={$cSet.color2}><b> | </b></font>\r\n  header.bgCol = {$cSet.tableCellColor}\r\n}\r\n\r\nstyles.sitemap.text {\r\n  target = \r\n  itemWrap < content.menuWrap\r\n}\r\n\r\n\r\nstyles.header.gfx1 {\r\n  bgCol = {$cSet.pageColor}\r\n  fontColor = {$cSet.color1}\r\n}\r\n\r\n\r\n\r\n\r\n## Main configuration:\r\n\r\n  # cat=cSet/color/1; type=color; label=Background color of page: This also affects the background color of graphical headers and frames on images.\r\ncSet.pageColor = white\r\n # cat=cSet/color/2; type=color; label=Tablecells: Color of tablecells in content element "Tables", Background color for the frameoption of content elements, Searchresult header background color\r\ncSet.tableCellColor = yellow\r\n # cat=cSet/color/3; type=color; label= "black"-color: Defines the color of all kinds of things, that normally have a dark color. Like headers, borderlines and such. Should be a dark color.\r\ncSet.color = black\r\n # cat=cSet/color/4; type=color; label= Primary alternative color: Used for eg. header layout 2 and 5, Image captions and as "Color 1" in color selectors\r\ncSet.color1 = green\r\n # cat=cSet/color/5; type=color; label= Secondary alternative color: Used for searchresults, "To top"-link, Headerdate, Subheader 3, content element "Menu", "Required" color in forms, and as "Color 2" in color selectors\r\ncSet.color2 = red\r\n # cat=cSet/typo/1; type=small; label= Primary FontFace: The fontface used almost everywhere in <font>-tags. Exceptions are bodytext. Examples: "Times", "Verdana", "Arial"\r\ncSet.fontFace = Arial\r\n # cat=cSet/typo/2; type=small; label= Text FontFace: Used for the content bodytext\r\ncSet.fontFace.text = Arial\r\n\r\n # cat=cSet/typo/3; type=int[1-5]; label= Fontsize 1: Used for Headerdate, Subheader 2 and 3, "Link to top", Image captions and text for radiobuttons in forms\r\ncSet.size1 = 1\r\n # cat=cSet/typo/4; type=int[1-5]; label= Fontsize 2: Used for Bodytext, Header 2, Subheader 1, content element "Menu", Labels for forms and searchresults.\r\ncSet.size2 = 2\r\n # cat=cSet/typo/5; type=int[1-5]; label= Fontsize 3: Used for Header 1 and headers in search-results.\r\ncSet.size3 = 3\r\n\r\n\r\n\r\nTSConstantEditor.cset {\r\n  header = Constant definitions\r\n  description = CSET is a small collection of constants that substitutes other constants throughout the templates. // When you include the "cSet" static_template, you can focus on a few major configurations like the general colors and font faces used. These values are then globally substituted by the "cSet"-constants.// //"cSet" Contains ONLY definition of constants and is meant to be included in templates AFTER the content-rendering-template (eg. "Content (default)") is included. Often it\'s included in the final template after inclusion of the standard template. These constants overrides constants in static template "content (default)" and the included templates of this template. // The point of this template is to simplify configuration of content-rendering by providing a collection of constants which is used in other constants. Makes it easy to choose the same color or font throughout a template.\r\n}\r\n', '', 'Constant definitions\r\n\r\nContains ONLY definition of constants and is meant to be included in templates AFTER the content-rendering-template (eg. "Content (default)") is included. Often it\'s included in the final template after inclusion of the standard template.\r\n\r\nThese constants overrides constants in static template "content (default)" and the included templates of this template.\r\n\r\nThe point of this template is to simplify configuration of content-rendering by providing a collection of constants which is used in other constants. Makes it easy to chose the same color or font throughout a template\r\n\r\nSee the constants present AFTER  "## Main configuration:" in the constants-field!\r\n\r\nStatus:\r\nChanged 28-06-2000\r\n', '');

INSERT INTO static_template VALUES ('58', '0', '996663712', '983214718', 'styles.gmenu.first (EXT1)', '', 'styles.gmenu.first.button.file {\r\n    # cat=menu/file/x1; type=file[IMAGE_EXT]; label=EXTENSION, Left image: This image is placed to the left on the menu items. NOTE: This image decides the height of the menu items!\r\n  left = media/buttons/left.gif\r\n    # cat=menu/file/x1; type=file[IMAGE_EXT]; label=EXTENSION, Tiled image: This image is tiled horizontally over the background on the menu items.\r\n  middle = media/buttons/middle.gif\r\n    # cat=menu/file/x1; type=file[IMAGE_EXT]; label=EXTENSION, Right image: This image is placed to the right on the menu items.\r\n  right = media/buttons/right.gif\r\n}\r\n\r\nstyles.gmenu.first.RO.button.file {\r\n    # cat=menu/file/x2; type=file[IMAGE_EXT]; label=EXTENSION, Rollover, Left image: This image is placed to the left on the menu items. NOTE: This image decides the height of the menu items!\r\n  left = media/buttons/left_ro.gif\r\n    # cat=menu/file/x2; type=file[IMAGE_EXT]; label=EXTENSION, Rollover, Tiled image: This image is tiled horizontally over the background on the menu items.\r\n  middle = media/buttons/middle_ro.gif\r\n    # cat=menu/file/x2; type=file[IMAGE_EXT]; label=EXTENSION, Rollover, Right image: This image is placed to the right on the menu items.\r\n  right = media/buttons/right_ro.gif\r\n}\r\n\r\nstyles.gmenu.first.ACT.button.file {\r\n    # cat=menu/file/x3; type=file[IMAGE_EXT]; label=EXTENSION, Active, Left image: This image is placed to the left on the menu items. NOTE: This image decides the height of the menu items!\r\n  left = media/buttons/left_ro.gif\r\n    # cat=menu/file/x3; type=file[IMAGE_EXT]; label=EXTENSION, Active, Tiled image: This image is tiled horizontally over the background on the menu items.\r\n  middle = media/buttons/middle_ro.gif\r\n    # cat=menu/file/x3; type=file[IMAGE_EXT]; label=EXTENSION, Active, Right image: This image is placed to the right on the menu items.\r\n  right = media/buttons/right_ro.gif\r\n}\r\n\r\n    # cat=\r\nstyles.gmenu.first.height = \r\n\r\n\r\n  # cat=basic/enable; type=comment; label=[FIRST-template] GMENU EXTENSION: Click this to enable the gmenu-extension for the "FIRST" static template. Only do this, if you have included "styles.gmenu.first (EXT1)" with the FIRST template!\r\npage.first_gmenu_extension = #', 'styles.gmenu.first = GMENU\r\nstyles.gmenu.first.NO {\r\n  XY = [10.w]+[20.w]+[5.w]+[6.w] , [5.h]\r\n\r\n  4 = IMAGE\r\n  4.file = {$styles.gmenu.first.button.file.middle}\r\n  4.tile = 10,1\r\n  5 = IMAGE\r\n  5.file = {$styles.gmenu.first.button.file.left}\r\n  6 = IMAGE\r\n  6.file = {$styles.gmenu.first.button.file.right}\r\n  6.align = r,t\r\n  6.offset = -[20.w]\r\n}\r\n\r\nstyles.gmenu.first.RO {\r\n  XY = [10.w]+[20.w]+[5.w]+[6.w] , [5.h]\r\n\r\n  4 = IMAGE\r\n  4.file = {$styles.gmenu.first.RO.button.file.middle}\r\n  4.tile = 10,1\r\n  5 = IMAGE\r\n  5.file = {$styles.gmenu.first.RO.button.file.left}\r\n  6 = IMAGE\r\n  6.file = {$styles.gmenu.first.RO.button.file.right}\r\n  6.align = r,t\r\n  6.offset = -[20.w]\r\n}\r\n\r\nstyles.gmenu.first.ACT {\r\n  XY = [10.w]+[20.w]+[5.w]+[6.w] , [5.h]\r\n\r\n  4 = IMAGE\r\n  4.file = {$styles.gmenu.first.ACT.button.file.middle}\r\n  4.tile = 10,1\r\n  5 = IMAGE\r\n  5.file = {$styles.gmenu.first.ACT.button.file.left}\r\n  6 = IMAGE\r\n  6.file = {$styles.gmenu.first.ACT.button.file.right}\r\n  6.align = r,t\r\n  6.offset = -[20.w]\r\n}\r\n\r\n\r\n\r\n{$page.first_gmenu_extension} top.20.1 < styles.gmenu.first\r\n{$page.first_gmenu_extension} top.40.1 < styles.gmenu.first\r\n{$page.first_gmenu_extension} bottom.40.1 < styles.gmenu.first\r\n', 'styles.gmenu.first - extension:\r\n\r\nThis modification adds support for a graphics in the back of the buttons. This works by incorporating 3 gif-files. One for the left end, one for the right end and finally one for the background. The background gif MUST be tileable in the horizontal direction.\r\n\r\nCHANGES:\r\n- the height of the buttons is now defined by the height of the gif-file at the left-end.\r\n- before the button had an extra 12 pixels width in addition to the width of the text and end-bullet. This is discarded. This just means, that you should probably adjust the offset of the text again.\r\n- You may need to re-create the end-bullet because the tiled background may appear "below" it.\r\n\r\nHOW-TO:\r\n- create 3 gif-files, one for left, right and middle. Upload them as resources to the template and enter their filenames as the constants you see above.\r\n- remember that the "middle"-file is tiled 10 times in the horizontal direction!\r\n\r\n\r\nUSE THE NEW GMENU-object with the FIRST template:\r\n\r\nIf you want to use this with the standard template FIRST, you should put this in the "Setup" field of your template. These will copy the new styles.gmenu.first-object into the right positions in the object-tree of the template:\r\n\r\n\r\n', '');

INSERT INTO static_template VALUES ('59', '0', '1017228472', '983217378', 'styles.hmenu.tu (EXT1)', '', 'styles.hmenu.tu.L3 {\r\n    # cat=menu/file/3; type=file[IMAGE_EXT]; label=Level-3, Background image\r\n  file.bgImg = media/buttons/menulevel2.gif\r\n    # cat=menu/typo/3; type=file[ttf]; label=Level-3, Font file\r\n  file.fontFile =\r\n    # cat=menu/typo/3; type=color; label=Level-3, Font color\r\n  fontColor = black\r\n    # cat=menu/typo/3; type=int[1-40]; label=Level-3, Font size\r\n  fontSize = 10\r\n    # cat=menu/typo/3; type=offset; label=Level-3, Text offset: Enter the offset of the text from the upper-left corner of the menu items\r\n  offset = 40,8\r\n    # cat=menu/typo/3; type=color; label=Level-3, Rollover/Active: Enter the color of the text on the menu item when mouseover OR when the item is "active".\r\n  RO.fontColor = navy\r\n}\r\n\r\n  # cat=basic/enable; type=comment; label=[TU-template] 3rd LEVEL EXTENSION: Click this to enable the 3rd level extension for the "TU" static template. Only do this, if you have included "styles.hmenu.tu (EXT1)" with the TU template!\r\npage.tu_thirdLevel_extension = #', '# LEVEL 3:\r\nstyles.hmenu.tu.3 = GMENU\r\nstyles.hmenu.tu.3.NO {\r\n  wrap = |<br>\r\n  XY = [10.w],[10.h]\r\n  reduceColors = {$styles.hmenu.tu.reduceColors}\r\n  10 = IMAGE\r\n  10.file = {$styles.hmenu.tu.L3.file.bgImg}\r\n  20 = TEXT\r\n  20 {\r\n    text.field = nav_title // title\r\n    fontFile = {$styles.hmenu.tu.L3.file.fontFile}\r\n    fontColor = {$styles.hmenu.tu.L3.fontColor}\r\n    fontSize = {$styles.hmenu.tu.L3.fontSize}\r\n    offset = {$styles.hmenu.tu.L3.offset}\r\n    niceText = 1\r\n  }\r\n}\r\nstyles.hmenu.tu.3.RO < styles.hmenu.tu.3.NO\r\nstyles.hmenu.tu.3.RO = 1\r\nstyles.hmenu.tu.3.RO.20.fontColor = {$styles.hmenu.tu.L3.RO.fontColor}\r\nstyles.hmenu.tu.3.ACT < styles.hmenu.tu.3.RO\r\nstyles.hmenu.tu.3.ACT = 1\r\n\r\n\r\n\r\n\r\n{$page.tu_thirdLevel_extension} page.10.40 < styles.hmenu.tu\r\n', 'This is an extension to styles.hmenu.tu. This extension includes a third level on the menu. Normally there are only two levels.\r\n', '');

INSERT INTO static_template VALUES ('60', '0', '999684095', '983227081', 'plugin.postit1', '', '', '\r\n## POST-IT\r\nplugin.postit1 >\r\nplugin.postit1 = PHP_SCRIPT\r\nplugin.postit1 {\r\n  file = media/scripts/postit.inc\r\n  data.field = bodytext\r\n  images.1 = IMAGE\r\n  images.1.file = GIFBUILDER\r\n  images.1.file {\r\n    XY = [5.w],[5.h]\r\n    5 = IMAGE\r\n    5.file = media/uploads/postit_1.gif\r\n    100 = TEXT\r\n    100.text = Testing\r\n    100.offset = -5,60\r\n    100.fontFile = fileadmin/fonts/arial_bold.ttf\r\n    100.fontSize = 15\r\n    100.align=center\r\n  }\r\n  images.2 < .images.1\r\n  images.2.file.5.file = media/uploads/postit_2.gif\r\n  images.2.file.100.angle = 11\r\n  images.2.file.100.offset = -2,79\r\n  images.3 < .images.1\r\n  images.3.file.5.file = media/uploads/postit_3.gif\r\n  images.3.file.100.angle = -13\r\n  images.3.file.100.offset = -7,81\r\n\r\n  cols = 3\r\n  textBox {\r\n    chars = 16\r\n    lineDist = 18\r\n    tmplObjNumber = 100\r\n    Valign = center\r\n    maxLines = 5\r\n  }\r\n  typolink {\r\n    parameter.current = 1\r\n    extTarget = {$styles.content.links.extTarget}\r\n    target = {$styles.content.links.target}\r\n  }\r\n}\r\n', 'PLUGIN: Post-it notes\r\nAUTHOR: Kasper Skrhj, kasper@typo3.com\r\n\r\n\r\nHow to activate this:\r\n- Just include this static template in your template provided that the static template "content (default)" is already included ranging higher in the list. That\'s all.\r\n\r\nHow to put a post-it note on a page:\r\n- Create a content element of the type "Textbox"\r\n- Select "the other" type to be "postit 1"\r\n- Now, every line in the bodytext-field represents a postit note.\r\n- The lines are splitted by a "|" (like tablecells are); The first part is the text, the second part is the kind (1-3) and the last part is the link (typolink).\r\n\r\nExample:\r\n\r\nThis is a postit note! | 2 | 77\r\n\r\n... this line would create a postit note of type 2 (rotated left), write the text "This is a postit note" on it and let it be a link to the page with id=77.', '');

INSERT INTO static_template VALUES ('61', '0', '1024790859', '983438903', 'plugin.tt_products [DEPRECIATED]', '', 'plugin.tt_products {\r\n    # cat=plugin.products/file; type=file[html,htm,tmpl,txt]; label= Template File: This is a simple textfile where each part of the shopping basket is defined with HTML-code. To see an example with explanations, take a look at media/scripts/products_template.tmpl which is the default.\r\n  file.templateFile = media/scripts/products_template.tmpl\r\n    # cat=plugin.products/file; type=file[IMAGE_EXT]; label= Alternative Image: This image is used if there is no image present for the product\r\n  file.noImageAvailable =\r\n\r\n    # cat=plugin.products/typo; type=wrap; label= Wrap 1: This wrap is used in the template-file.\r\n  wrap1 = <font face="verdana" size=2 color=black> | </font>\r\n    # cat=plugin.products/typo; type=wrap; label= Wrap 2: This wrap is used in the template-file.\r\n  wrap2 = <font face="verdana" size=1 color=black> | </font>\r\n\r\n    # cat=plugin.products/color; type=color; label= Color 1: This bgcolor is used in the template-file.\r\n  color1 = #cccccc\r\n    # cat=plugin.products/color; type=color; label= Color 2: This bgcolor is used in the template-file.\r\n  color2 = #999999\r\n    # cat=plugin.products/color; type=color; label= Color 3: This bgcolor is used in the template-file.\r\n  color3 = #333333\r\n\r\n    # cat=plugin.products//; type=boolean; label= Click Enlarge, Single display: If this option is set, users can click the image of a product and it\'ll pop-up in a separate window at a larger size.\r\n  clickEnlarge = 1\r\n    # cat=plugin.products//; type=boolean; label= Click Enlarge, List display: As above, but for the images in the product-lists and search-results\r\n  clickEnlarge_list = 1\r\n    # cat=plugin.products//; type=boolean; label= Click Enlarge, Basket display: As above, but for the images in the basket\r\n  clickEnlarge_basket = 1\r\n\r\n    # cat=plugin.products//; type=boolean; label= Display Category Header in list: If this option is set, the category headers (page titles) will automatically be displayed in the product lists. This is not always convenient because you might have chosen a header-title for the "list" content element.\r\n  displayListCatHeader = 1\r\n    # cat=plugin.products//; type=boolean; label= Display Category Header in basket: If this option is set, the category headers (page titles) will automatically be displayed in the basket lists.\r\n  displayBasketCatHeader = 0\r\n\r\n    # cat=plugin.products//; type=int+; label= Max items displayed: The maximum number of items displayed on one page. \r\n  limit = 50\r\n\r\n    # cat=plugin.products/dims; type=int+; label= Max image width, single display: The maximum width in pixels of an image when displayed with a single product.\r\n  maxW_single = 200\r\n    # cat=plugin.products/dims; type=int+; label= Max image width, list display: The maximum width in pixels of an image when displayed in lists and searchresults.\r\n  maxW_list = 50\r\n    # cat=plugin.products/dims; type=int+; label= Max image width, basket display: The maximum width in pixels of an image when displayed in the shopping-basket.\r\n  maxW_basket = 100\r\n\r\n    # cat=plugin.products//; type=int+; label=PID for single item display: If you want a certain page to be used for display of item details, please enter the PID (page-uid) here.\r\n  PIDitemDisplay =\r\n    # cat=plugin.products//; type=int+; label=PID for search page: If you want all product searches to go to a specific page, enter the PID it here! NOTE: If you set this PID, all searchqueries will (must) be handled with a list content element with the code "search" on that page.\r\n  PIDsearch =\r\n    # cat=plugin.products//; type=int+; label=PID for basket page: If you want every change in the number of items anywhere to go to a specific page (eg. the shopping basket page), enter the PID here.\r\n  PIDbasket =\r\n    # cat=plugin.products//; type=int+; label=PID for store root: This is the PID of the rootPage of the store. If not set the store will operate over all pages from the root of the site. But if the site has many pages, performance may improve\r\n  PIDstoreRoot = \r\n\r\n    # cat=plugin.products//; type=small; label=TAX percentage: Type= Double (means, "use . as decimal point")\r\n  TAXpercentage = 25\r\n\r\n    # TAX included in database prices?\r\n  TAXincluded = 1\r\n    # Price decimals\r\n  priceDec = 2\r\n\r\n    # cat=plugin.products//; type=small; label=Price decimal point\r\n  priceDecPoint = .\r\n    # cat=plugin.products//; type=small; label=Price Thousand point\r\n  priceThousandPoint = \r\n}\r\n\r\n', '## Includes the productsLib:\r\nincludeLibs.ts_products = media/scripts/productsLib.inc\r\n\r\n\r\n## This enables the tt_content.list item to display the guestbook:\r\nplugin.tt_products >\r\nplugin.tt_products = USER\r\nplugin.tt_products {\r\n  userFunc = user_products->main_products\r\n\r\n  templateFile = {$plugin.tt_products.file.templateFile}\r\n  pid_list.field = pages\r\n  recursive.field = recursive\r\n  code.field = select_key\r\n\r\n  limit = {$plugin.tt_products.limit}\r\n  image {\r\n    file.maxW = {$plugin.tt_products.maxW_single}\r\n    imageLinkWrap = {$plugin.tt_products.clickEnlarge}\r\n    imageLinkWrap {\r\n      enable = 1\r\n      bodyTag = <body bgColor=black>\r\n      wrap = <a href="javascript:close();"> | </a>\r\n      width = 400m\r\n      height = 400\r\n\r\n      JSwindow = 1\r\n      JSwindow.newWindow = 1\r\n      JSwindow.expand = 17,20\r\n    }\r\n  }\r\n  listImage < .image\r\n  listImage.file.maxW = {$plugin.tt_products.maxW_list}\r\n  listImage.imageLinkWrap = {$plugin.tt_products.clickEnlarge_list}\r\n\r\n  basketImage < .image\r\n  basketImage.file.maxW = {$plugin.tt_products.maxW_basket}\r\n  basketImage.imageLinkWrap = {$plugin.tt_products.clickEnlarge_basket}\r\n\r\n  image.wrap = | <br>\r\n\r\n  noImageAvailable = {$plugin.tt_products.file.noImageAvailable}\r\n\r\n  wrap1.wrap = {$plugin.tt_products.wrap1}\r\n  wrap2.wrap = {$plugin.tt_products.wrap2}\r\n  color1.wrap = {$plugin.tt_products.color1}\r\n  color2.wrap = {$plugin.tt_products.color2}\r\n  color3.wrap = {$plugin.tt_products.color3}\r\n\r\n  displayListCatHeader = {$plugin.tt_products.displayListCatHeader}\r\n  displayBasketCatHeader = {$plugin.tt_products.displayBasketCatHeader}\r\n\r\n  PIDitemDisplay = {$plugin.tt_products.PIDitemDisplay}\r\n  PIDsearch = {$plugin.tt_products.PIDsearch}\r\n  PIDbasket = {$plugin.tt_products.PIDbasket}\r\n  PIDstoreRoot = {$plugin.tt_products.PIDstoreRoot}\r\n  PID_sys_products_orders = 0\r\n#  PIDinfo = 209\r\n#  PIDfinalize = 210\r\n#  PIDthanks = 211\r\n\r\n  parseFunc < styles.content.parseFunc\r\n  categoryHeader = TEXT\r\n  categoryHeader.current = 1\r\n\r\n  priceDec = {$plugin.tt_products.priceDec}\r\n  priceDecPoint = {$plugin.tt_products.priceDecPoint}\r\n  priceThousandPoint = {$plugin.tt_products.priceThousandPoint}\r\n  currencySymbol = USD\r\n\r\n  TAXpercentage = {$plugin.tt_products.TAXpercentage}\r\n  TAXincluded = {$plugin.tt_products.TAXincluded}\r\n\r\n  lockLoginUserInfo = 1\r\n  requiredInfoFields = name, address, telephone\r\n  orderNumberPrefix = order_\r\n  orderEmail_from = shop@myTypo3Shop.com\r\n  orderEmail_fromName = My Typo3 Shop\r\n  orderEmail_to = shop@myTypo3Shop.com\r\n  orderEmail_htmlmail = 0\r\n  email_notify_default = 1\r\n  statusCodes {\r\n    1 = Order submitted by user\r\n    2 = Order is recieved and accepted by store\r\n    10 = Shop is awaiting goods from third-party\r\n    11 = Shop is awaiting customer payment\r\n    12 = Shop is awaiting material from customer\r\n    20 = Goods shipped to customer\r\n    30 = Other message from store\r\n    50 = Customer request for cancelling\r\n    51 = Message from customer to shop\r\n    100 = Order shipped and closed\r\n    101 = Order closed\r\n    200 = Ordre cancelled\r\n  }\r\n  update_code = password\r\n  statusDate_stdWrap.strftime= %e-%m-%y %H:%M\r\n  orderDate_stdWrap.strftime= %e-%m-%y\r\n}\r\n\r\n## EXAMPLE payment config:\r\nplugin.tt_products.payment {\r\n  radio = 1\r\n  10.title = MasterCard\r\n  10.image.file = media/logos/mastercard.gif\r\n  10.priceTax = \r\n  10.priceNoTax =  \r\n  10.calculationScript = media/scripts/products_comp_calcScript.inc\r\n\r\n  40.title = Money transfer\r\n  40.image.file = media/logos/money.gif\r\n  40.priceTax = \r\n  40.priceNoTax = \r\n  40.percentOfGoodstotal = 1.45\r\n\r\n  50.title = Credit in-store\r\n  50.image.file = \r\n  50.priceTax = \r\n  50.priceNoTax = \r\n}\r\n\r\n## EXAMPLE shipping config:\r\nplugin.tt_products.shipping {\r\n  10.title = Ground\r\n  10.image.file = media/logos/pakketrans.gif\r\n  10.image.params = align=absmiddle\r\n  10.priceTax = 40\r\n  10.priceNoTax = 32\r\n\r\n  20.title = Airmail\r\n  20.image.file = media/logos/postdanmark.gif\r\n  10.image.params = align=absmiddle\r\n  20.priceTax = 120\r\n  20.priceNoTax = 120\r\n\r\n  40.title = Pick up in store\r\n  40.excludePayment = 10,40\r\n}\r\n\r\n\r\n\r\n## This enables the record item to display product items\r\ntt_products >\r\ntt_products = < plugin.tt_products\r\ntt_products.displayCurrentRecord = 1\r\n', 'PLUGIN: Shopping module, Products\r\nAUTHOR: Kasper Skrhj, kasper@typo3.com\r\n\r\n', '');

INSERT INTO static_template VALUES ('65', '0', '1024790859', '985276040', 'content.tt_address [DEPRECIATED]', '', 'content.tt_address {\r\n    # cat=address/typo; type=wrap; label=Wrap, name\r\n  name.wrap = <h3> |</h3>\r\n    # cat=address/typo; type=options[,upper,lower]; label=Case, name\r\n  name.case = upper\r\n    # cat=address/typo; type=wrap; label=Wrap, address information\r\n  info.wrap = <font color=maroon> |</font>\r\n    # cat=address/dims; type=int+; label=Width, information field\r\n  info.width = 300\r\n    # cat=address/dims; type=int+; label=Width, image\r\n  image.width = 120\r\n    # cat=address//; type=options[name,company,phone,city]; label=SQL "ORDER BY"\r\n  orderBy = name\r\n    # cat=address//; type=options[,DESC]; label=Reverse order (DESC)?\r\n  desc = \r\n}', '\r\n## Introducing rendering information for tt_address:\r\ntt_address = COA\r\ntt_address {\r\n  wrap = <table border=0 cellpadding=0 cellspacing=0><tr><td valign=top> | </tr></table><br>\r\n\r\n  10 = TEXT\r\n  10.field = name\r\n  10.case = {$content.tt_address.name.case}\r\n  10.wrap = {$content.tt_address.name.wrap}\r\n\r\n  20 = COA\r\n  20.wrap = {$content.tt_address.info.wrap}\r\n  20 {\r\n    20 = TEXT\r\n    20.if.isTrue.field = company\r\n    20.dataWrap = Company: {field:company}<br>\r\n\r\n    21 = TEXT\r\n    21.dataWrap = {field:address}<br>{field:zip} {field:city}\r\n\r\n    22 = TEXT\r\n    22.if.isTrue.field = phone\r\n    22.dataWrap = <br>Phone: {field:phone}\r\n\r\n    23 = TEXT\r\n    23.if.isTrue.field = fax\r\n    23.dataWrap = <br>Fax: {field:fax}\r\n\r\n    25 = TEXT\r\n    25.if.isTrue.field = email\r\n    25.dataWrap = <br><a href="mailto:{field:email}">{field:email}</a>\r\n\r\n    27 = TEXT\r\n    27.if.isTrue.field = www\r\n    27.dataWrap = <br><a href="{field:www}" target="_top">Hjemmeside</a>\r\n  }\r\n\r\n  40 = TEXT\r\n  40.value = <br><img src="clear.gif" width={$content.tt_address.info.width} height=1></td><td>&nbsp;</td>\r\n\r\n  60 = IMAGE\r\n  60.file.import = uploads/pics/\r\n  60.file.import.field = image\r\n  60.file.import.listNum = 0\r\n  60.file.width = {$content.tt_address.image.width}\r\n  60.wrap = <td valign=top>|</td>\r\n}\r\n\r\n\r\n## Transfering to the tt_content.list object:\r\ntt_content.list.20.0 >\r\ntt_content.list.20.0 = CASE\r\ntt_content.list.20.0.key.field = layout\r\ntt_content.list.20.0.0 = CONTENT\r\ntt_content.list.20.0.0 {\r\n  table = tt_address\r\n  select.pidInList.field = pages\r\n  select.orderBy = {$content.tt_address.orderBy} {$content.tt_address.desc}\r\n}\r\n\r\n', 'Introduces rendering code for tt_address records. \r\n\r\nHow to activate this:\r\n- Just include this static template in your template provided that the static template "content (default)" is already included ranging higher in the list. \r\n\r\nCreates a toplevel TS object, tt_address.\r\nConstants are in \'content.tt_address\'\r\nSets up \'tt_content.list.20.0.0\' to render the address-items\r\n', '');

INSERT INTO static_template VALUES ('66', '0', '1024790859', '985276070', 'plugin.tt_board_list [DEPRECIATED]', '81', 'plugin.tt_board_list{\r\n    # cat=plugin.board/file; type=file[html,htm,tmpl,txt]; label= Template File, list style: This is a simple textfile where the board is defined with HTML-code. To see an example with explanations, take a look at media/scripts/board_template.tmpl which is the default.\r\n  file.templateFile = media/scripts/board_template1.tmpl\r\n\r\n    # cat=plugin.board/typo; type=wrap; label= List-style, Wrap 1: This wrap is used in the template-file.\r\n  wrap1 = <font face="verdana" size=2 color=black> | </font>\r\n    # cat=plugin.board/typo; type=wrap; label= List-style, Wrap 2: This wrap is used in the template-file.\r\n  wrap2 = <font face="verdana" size=1 color=black> | </font>\r\n    # cat=plugin.board/typo; type=wrap; label= List-style, Wrap 3: This wrap is used in the template-file.\r\n  wrap3 = <font face="verdana" size=2 color=white> | </font>\r\n\r\n    # cat=plugin.board/color; type=color; label= List-style, Color 1: This bgcolor is used in the template-file.\r\n  color1 = #cccccc\r\n    # cat=plugin.board/color; type=color; label= List-style, Color 2: This bgcolor is used in the template-file.\r\n  color2 = #999999\r\n    # cat=plugin.board/color; type=color; label= List-style, Color 3: This bgcolor is used in the template-file.\r\n  color3 = #333333\r\n    # cat=plugin.board/color; type=color; label= List-style, Color 4: This bgcolor is used in the template-file.\r\n  color4 = #333366\r\n}\r\n', '\r\n## This enables the tt_content.list item to display the forum, list style:\r\nplugin.tt_board_list < temp.tt_board.shared\r\nplugin.tt_board_list {\r\n  templateFile = {$plugin.tt_board_list.file.templateFile}\r\n\r\n  # Global wraps\r\n  wrap1.wrap = {$plugin.tt_board_list.wrap1}\r\n  wrap2.wrap = {$plugin.tt_board_list.wrap2}\r\n  wrap3.wrap = {$plugin.tt_board_list.wrap3}\r\n  color1.wrap = {$plugin.tt_board_list.color1}\r\n  color2.wrap = {$plugin.tt_board_list.color2}\r\n  color3.wrap = {$plugin.tt_board_list.color3}\r\n  color4.wrap = {$plugin.tt_board_list.color4}\r\n}\r\n\r\n\r\n## This enables the record item to display guestbook items\r\ntt_board >\r\ntt_board = < plugin.tt_board_list\r\ntt_board.displayCurrentRecord = 1\r\n', 'PLUGIN: Board/Forum List style\r\nAUTHOR: Kasper Skrhj, kasper@typo3.com\r\n\r\n', '');

INSERT INTO static_template VALUES ('82', '0', '1004963038', '999775222', 'plugin.alt.plaintext (99)', '', 'plugin.alt.plaintext {\r\n    # cat=plugin.plaintext/file; type=file[html,htm,tmpl,txt]; label= Template File for plaintext version: &type=99 defines a printer friendly design for page content. This template file lets you define the position of page titles and the content.\r\n  file.template = media/scripts/plaintext_content.tmpl\r\n\r\n  siteUrl = http://www.example.com.test/example/\r\n}', 'lib.alt_plaintext.renderObj = USER\r\nlib.alt_plaintext.renderObj.userFunc = user_plaintext->main_plaintext\r\nlib.alt_plaintext.renderObj {\r\n  header.defaultType = 1\r\n  header.date = D-m-Y\r\n  header.datePrefix = |Date: |\r\n  header.linkPrefix = | - Headerlink: |\r\n  header.1.preLineLen = 76\r\n  header.1.postLineLen = 76\r\n  header.1.preBlanks=1\r\n  header.1.stdWrap.case = upper\r\n\r\n  header.2 < .header.1\r\n  header.2.preLineChar=*\r\n  header.2.postLineChar=*\r\n\r\n  header.3.preBlanks=2\r\n  header.3.postBlanks=1\r\n  header.3.stdWrap.case = upper\r\n  \r\n  header.4 < .header.1\r\n  header.4.preLineChar= =\r\n  header.4.postLineChar= =\r\n  header.4.preLineBlanks= 1\r\n  header.4.postLineBlanks= 1\r\n\r\n  header.5.preBlanks=1\r\n  header.5.autonumber=1\r\n  header.5.prefix = |: >> |\r\n\r\n\r\n  siteUrl = {$plugin.alt.plaintext.siteUrl}\r\n  defaultOutput (\r\n|\r\n[Unrendered Content Element; ###CType### ]\r\n|\r\n  )\r\n\r\n  uploads.header = |DOWNLOADS:|\r\n\r\n  images.header = |IMAGES:|\r\n  images.linkPrefix = | - Imagelink: |\r\n  images.captionHeader = |CAPTION:|\r\n\r\n  bulletlist.0.bullet = |*  |\r\n\r\n  bulletlist.1.bullet = |#  |\r\n\r\n  bulletlist.2.bullet = | - |\r\n\r\n  bulletlist.3.bullet = |>  |\r\n  bulletlist.3.secondRow = |.  |\r\n  bulletlist.3.blanks = 1\r\n\r\n  menu = <tt_content.menu.20\r\n  shortcut = <tt_content.shortcut.20\r\n  shortcut.0.conf.tt_content = <lib.alt_plaintext.renderObj\r\n  shortcut.0.tables = tt_content\r\n\r\n  bodytext.stdWrap.parseFunc.tags {\r\n    link >< styles.content.parseFunc.tags.link\r\n    typolist = USER\r\n    typolist.userFunc = user_plaintext->typolist\r\n    typolist.siteUrl = {$plugin.alt.plaintext.siteUrl} \r\n    typolist.bulletlist =< lib.alt_plaintext.renderObj.bulletlist\r\n    typohead = USER\r\n    typohead.userFunc = user_plaintext->typohead\r\n    typohead.siteUrl = {$plugin.alt.plaintext.siteUrl}\r\n    typohead.header =< lib.alt_plaintext.renderObj.header\r\n    typocode = USER\r\n    typocode.userFunc = user_plaintext->typocode\r\n    typocode.siteUrl = {$plugin.alt.plaintext.siteUrl}\r\n  }\r\n}\r\n\r\n\r\n\r\n## Insert DMailer Boundaries for all elements.\r\nconfig.insertDmailerBoundaries = 1\r\n\r\nincludeLibs.plaintextLib = media/scripts/plaintextLib.inc\r\n\r\n## Set up page/type number:\r\nalt_plaintext >\r\nalt_plaintext = PAGE\r\nalt_plaintext.typeNum=99\r\n\r\nalt_plaintext.config.disableAllHeaderCode = 1\r\nalt_plaintext.10 = TEMPLATE\r\nalt_plaintext.10 {\r\n  template = FILE\r\n  template.file = {$plugin.alt.plaintext.file.template}\r\n  marks.CONTENT < styles.content.get\r\n  marks.CONTENT.renderObj = < lib.alt_plaintext.renderObj\r\n  marks.DATE = TEXT\r\n  marks.DATE.data = date:U\r\n  marks.DATE.strftime = %e. %B %Y\r\n}\r\n\r\n## Setting up a testpage at type 199\r\nalt_plaintext_test < alt_plaintext\r\nalt_plaintext_test {\r\n  wrap = <pre>|</pre>\r\n  typeNum=199\r\n  config.no_cache=1\r\n}\r\n', 'Creates a PAGE object, alt_plaintext, with typeNum = 99. This page displays the page content in plain text layout designed for Direct Mail purposes.\r\n\r\nJust include this static template directly in your template, and you\'re up\'n\'running.', '');

INSERT INTO static_template VALUES ('83', '0', '999790535', '999781305', 'plugin.alt.xml (96)', '', 'plugin.alt.xml >\r\nplugin.alt.xml {\r\n\r\n}\r\n', '## Set up page/type number:\r\nalt_xml >\r\nalt_xml = PAGE\r\nalt_xml {\r\n  typeNum=96\r\n  config.disableAllHeaderCode = 1\r\n  config.additionalHeaders = Content-type: text/xml\r\n\r\n  ## Includes the newsLib:\r\n  includeLibs.alt_xml = media/scripts/xmlversionLib.inc\r\n\r\n  ## Inserting the USER cObject for XML rendering\r\n  10 = USER\r\n  10 {\r\n    userFunc = user_xmlversion->main_xmlversion\r\n  }\r\n}\r\n', 'Creates a PAGE object, alt_xml, with typeNum = 96. This page displays the \'normal\' page content along with trivial page information in XML format.\r\n\r\nStill experimental.\r\n\r\nJust include this static template directly in your template, and you\'re up\'n\'running.', '');

INSERT INTO static_template VALUES ('86', '0', '1000283575', '999808177', 'plugin.alt.pda (95)', '', 'plugin.alt.pda >\r\nplugin.alt.pda {\r\n    # cat=plugin.pda/file; type=file[html,htm,tmpl,txt]; label= Template File for PDA version: &type=95 defines a PDA friendly design for page content. \r\n  file.template = media/scripts/pdaversion_content.tmpl\r\n}\r\n', '## Defining temporary object:\r\ntemp.alt_pda.mainMenu = HMENU\r\ntemp.alt_pda.mainMenu {\r\n  1 = TMENU\r\n  1 {\r\n    expAll = 1\r\n    target = \r\n    NO.allWrap = <h3>|</h3>\r\n  }\r\n  2 < .1\r\n  2.NO.allWrap = &nbsp;&nbsp;<b>|</b><br>\r\n  3 < .1\r\n  3.NO.allWrap = &nbsp;&nbsp;&nbsp;&nbsp;|<br>\r\n}\r\n\r\n\r\ntemp.alt_pda.pageNav = HMENU\r\ntemp.alt_pda.pageNav {\r\n  special = browse\r\n  special {\r\n    items = prev | up | next\r\n    items.prevnextToSection = 1\r\n    up.fields.title = UP\r\n    prev.fields.title = < PREV\r\n    next.fields.title = NEXT >\r\n  }\r\n  1 = TMENU\r\n  1.target = \r\n  1.NO {\r\n    linkWrap = [ | ]\r\n  }\r\n}\r\n\r\n\r\ntemp.alt_pda.rootLine = HMENU\r\ntemp.alt_pda.rootLine {\r\n  special = rootline\r\n  1 = TMENU\r\n  1.target = \r\n  1.wrap =  | <br>\r\n  1.NO {\r\n    linkWrap = | &nbsp;>&nbsp; |*||*|   |\r\n  }\r\n}\r\n\r\n\r\n\r\n\r\n## Set up page\r\nalt_pda >\r\nalt_pda = PAGE\r\nalt_pda.typeNum=95\r\nalt_pda.config.forceTypeValue=95\r\n\r\nalt_pda.10 = TEMPLATE\r\nalt_pda.10 {\r\n  template = FILE\r\n  template.file = {$plugin.alt.pda.file.template}\r\n  marks {\r\n    PAGE_TITLE = TEXT\r\n    PAGE_TITLE.field = title\r\n    PAGE_SUBTITLE = TEXT\r\n    PAGE_SUBTITLE.field = subtitle\r\n    PAGE_AUTHOR = TEXT\r\n    PAGE_AUTHOR.field = author\r\n    PAGE_AUTHOR.required=1\r\n    PAGE_AUTHOR.typolink.parameter.field = author_email\r\n    PAGE_UID = TEXT\r\n    PAGE_UID.field = uid\r\n    CONTENT < styles.content.get\r\n\r\n    MAIN_MENU = TEXT\r\n    MAIN_MENU.value =\r\n    NAVIGATION = TEXT\r\n    NAVIGATION.value =\r\n  }\r\n  workOnSubpart = DOCUMENT_BODY\r\n}\r\n\r\n\r\n## Inserting the temporary objects:\r\n[treeLevel = 0]\r\nalt_pda.10.marks.MAIN_MENU < temp.alt_pda.mainMenu\r\n[global]\r\n\r\n[treeLevel = 1,2,3,4,5]\r\nalt_pda.10.marks.NAVIGATION = COA \r\nalt_pda.10.marks.NAVIGATION {\r\n  10 < temp.alt_pda.rootLine\r\n  20 < temp.alt_pda.pageNav\r\n}\r\n[global]\r\n\r\n\r\n## If the browser is a PDA-device, eg. AvantGo\r\n[device=pda]\r\n\r\nalt_pda.typeNum=0\r\nalt_pda.config.forceTypeValue=0\r\n\r\n[global]\r\n', 'Creates a PAGE object, alt_pda, with typeNum = 95. \r\n\r\nJust include this static template directly in your template, and you\'re up\'n\'running.\r\n\r\nDepends on \'content (default)\' or similar to be already included.', '');

INSERT INTO static_template VALUES ('67', '0', '996690228', '985353522', 'styles.sitemap.gs', '', 'styles.sitemap.gs >\r\nstyles.sitemap.gs {\r\n  entryLevel = 0\r\n    # cat=gs-sitemap; type=small; label=exclude PID: If there\'s certain page-id number you would like to exclude from the sitemap (eg. the sitemap-page itself), then enter it here (comma-list)\r\n  excludeUids =\r\n\r\n    # cat=gs-sitemap/links; type=small; label=Target: The target of the links in the sitemap. \r\n  target = _top\r\n    # cat=gs-sitemap/color; type=color; label=Background color, headers\r\n  color = olive\r\n\r\n    # cat=gs-sitemap/typo; type=wrap; label=Sitemap, Header Wrap: This wrap is placed around the header of the sitemap.\r\n  headerWrap = <div align="center"><b><font size="1" face="Verdana, Arial, Helvetica, sans-serif" color="white"> | </font></b></div>\r\n    # cat=gs-sitemap/typo; type=options[,upper,lower]; label=Header case\r\n  headerCase = upper\r\n\r\n    # cat=gs-sitemap/typo; type=wrap; label=Sitemap, Head Wrap 1: This wrap is placed around the every head item in the sitemap.\r\n  wrap1 = <b><font size="1" face="Verdana, Arial, Helvetica, sans-serif" color="white"> | </font></b>\r\n    # cat=gs-sitemap/typo; type=wrap; label=Sitemap, Second Wrap 2: This wrap is placed around the every second-level item in the sitemap.\r\n  wrap2 = <font face="Verdana, Arial, Helvetica, sans-serif" size="1">|</font>\r\n    # cat=gs-sitemap/typo; type=wrap; label=Sitemap, Third Wrap 3: This wrap is placed around the every third-level item in the sitemap.\r\n  wrap3 = <font face="Verdana, Arial, Helvetica, sans-serif" size="1">|</font>\r\n\r\n    # cat=gs-sitemap/; type=string; label=Bullet, Second Level\r\n  bullet2 = <img src="media/bullets/blackdot.gif" width="5" height="9">\r\n    # cat=gs-sitemap/; type=string; label=Bullet, Third Level\r\n  bullet3 = <img src="media/bullets/blackdot.gif" width="3" height="6" hspace=4 vspace=2>\r\n\r\n    # cat=gs-sitemap; type=boolean; label=Level 3?: If you would like the sitemap to render only two levels, un-check this option\r\n  level3 = 1\r\n}\r\n', '\r\n# SITE MAP\r\n\r\nstyles.sitemap.gs >\r\nstyles.sitemap.gs = COA\r\nstyles.sitemap.gs.wrap = <table border="0" cellspacing="0" cellpadding="0" align="center"> | </table>\r\nstyles.sitemap.gs {\r\n  10 = TEXT\r\n  10.data = leveltitle : {$styles.sitemap.gs.entryLevel}\r\n  10.case = {$styles.sitemap.gs.headerCase}\r\n  10.wrap = {$styles.sitemap.gs.headerWrap}\r\n  10.wrap2 = <tr><td><table border="0" cellpadding="2" cellspacing="1" align="center"><tr bgcolor="{$styles.sitemap.gs.color}"><td> | </td></tr></table></td></tr>\r\n  15 = TEXT\r\n  15.value = <tr align="center" valign="middle"><td><img src="media/bullets/blackclear.gif" width="1" height="10"></td></tr><tr bgcolor="{$styles.sitemap.gs.color}"><td><img src="clear.gif" width="1" height="1"></td></tr>\r\n  19 = TEXT\r\n  19.value = <tr><td>\r\n\r\n  # Here comes the sitemap below...\r\n  \r\n  21 = TEXT\r\n  21.value =  </td></tr>\r\n}\r\n\r\n\r\n# The sitemap itself:\r\nstyles.sitemap.gs.20 = COA\r\nstyles.sitemap.gs.20.wrap = <table border="0" cellspacing="0" cellpadding="0" align="center"> | </table>\r\nstyles.sitemap.gs.20.10 = HMENU\r\nstyles.sitemap.gs.20.10 {\r\n  entryLevel = {$styles.sitemap.gs.entryLevel}\r\n  excludeUidList = {$styles.sitemap.gs.excludeUids}\r\n  wrap = <tr align="center" valign="middle"> |</tr>\r\n  1 = TMENU\r\n  1.NO {\r\n    doNotShowLink = 1\r\n    before = <td><img src="media/bullets/blackclear.gif" width="1" height="10"></td>\r\n  }\r\n}\r\nstyles.sitemap.gs.20.20 = HMENU\r\nstyles.sitemap.gs.20.20 {\r\n  excludeUidList = {$styles.sitemap.gs.excludeUids}\r\n  entryLevel = {$styles.sitemap.gs.entryLevel}\r\n  wrap = <tr align="left" valign="top"> |</tr>\r\n  1 = TMENU\r\n  1.target = {$styles.sitemap.gs.target}\r\n  1.expAll = 1\r\n  1.NO {\r\n    wrapItemAndSub = <td><table border="0" cellpadding="1" cellspacing="1">|</table></td>\r\n    allWrap = <tr><td bgcolor="{$styles.sitemap.gs.color}" colspan="3"><div align="center">|</div></td></tr>\r\n    linkWrap = {$styles.sitemap.gs.wrap1}\r\n    ATagBeforeWrap = 1\r\n  }\r\n  2 = TMENU\r\n  2.expAll = {$styles.sitemap.gs.level3}\r\n  2.target = {$styles.sitemap.gs.target}\r\n  2.NO {\r\n    allWrap = <tr><td align="center" valign="top">{$styles.sitemap.gs.bullet2}</td><td align="left" valign="top" colspan=2>|</td></tr>\r\n    linkWrap = {$styles.sitemap.gs.wrap2}\r\n    ATagBeforeWrap = 1\r\n  }\r\n  3 = TMENU\r\n  3.target = {$styles.sitemap.gs.target}\r\n  3.NO {\r\n    allWrap = <tr><td></td><td align="center" valign="top">{$styles.sitemap.gs.bullet3}</td><td align="left" valign="top">|</td></tr>\r\n    linkWrap = {$styles.sitemap.gs.wrap3}\r\n    ATagBeforeWrap = 1\r\n  }\r\n}', '', '');

INSERT INTO static_template VALUES ('64', '0', '1024790859', '985004512', 'plugin.tt_guest [DEPRECIATED]', '', 'plugin.tt_guest >\r\nplugin.tt_guest {\r\n    # cat=plugin.guestbook/file; type=file[html,htm,tmpl,txt]; label= Template File: This is a simple textfile where the guestbook is defined with HTML-code. To see an example with explanations, take a look at media/scripts/guest_template1.tmpl which provides a good example\r\n  file.templateFile = media/scripts/guest_template1.tmpl\r\n  notifyEmail = \r\n\r\n    # cat=plugin.guestbook/typo; type=wrap; label= Wrap 1: This wrap is used in the template-file.\r\n  wrap1 = <font face="verdana" size=2 color=black> | </font>\r\n    # cat=plugin.guestbook/typo; type=wrap; label= Wrap 2: This wrap is used in the template-file.\r\n  wrap2 = <font face="verdana" size=1 color=black> | </font>\r\n\r\n    # cat=plugin.guestbook/color; type=color; label= Color 1: This bgcolor is used in the template-file.\r\n  color1 = #cccccc\r\n    # cat=plugin.guestbook/color; type=color; label= Color 2: This bgcolor is used in the template-file.\r\n  color2 = #999999\r\n    # cat=plugin.guestbook/color; type=color; label= Color 3: This bgcolor is used in the template-file.\r\n  color3 = #333333\r\n}\r\n', '## Includes the guestLib:\r\nincludeLibs.ts_guest = media/scripts/guestLib.inc\r\n\r\n\r\n## Sets up the fields, that may be submitted in order to create items. Mandatory!\r\nFEData.tt_guest >\r\nFEData.tt_guest {\r\n  processScript = media/scripts/guest_submit.inc\r\n  processScript.notifyEmail = {$plugin.tt_guest.notifyEmail}\r\n  default {\r\n    hidden = 0\r\n  }\r\n  allowNew {\r\n    title = 1\r\n    note = 1\r\n    cr_name=1\r\n    cr_email=1\r\n    www=1\r\n  }\r\n  autoInsertPID = 1\r\n  doublePostCheck = doublePostCheck\r\n}\r\n\r\n\r\n\r\n## This enables the tt_content.list item to display the guestbook:\r\nplugin.tt_guest >\r\nplugin.tt_guest = USER\r\nplugin.tt_guest {\r\n  userFunc = user_guest->main_guestbook\r\n\r\n  templateFile = {$plugin.tt_guest.file.templateFile}\r\n  pid_list.field = pages\r\n  code.field = select_key\r\n\r\n  # Global wraps\r\n  wrap1.wrap = {$plugin.tt_guest.wrap1}\r\n  wrap2.wrap = {$plugin.tt_guest.wrap2}\r\n  color1.wrap = {$plugin.tt_guest.color1}\r\n  color2.wrap = {$plugin.tt_guest.color2}\r\n  color3.wrap = {$plugin.tt_guest.color3}\r\n\r\n  # Change/add to format the content:\r\n  age_stdWrap.age = 1\r\n  date_stdWrap.strftime= %e-%m-%y\r\n  time_stdWrap.strftime= %H:%M:%S\r\n  author_stdWrap.case = upper\r\n  www_stdWrap.typolink.parameter.field = www\r\n  email_stdWrap.typolink.parameter.field = cr_email\r\n  requireRecords = 1\r\n\r\n  	# Change/add to format the submit-form\r\n  postform < styles.content.mailform\r\n  postform.data >\r\n  postform.dataArray {\r\n    10.label = Header:\r\n    10.type = *data[tt_guest][NEW][title]=input \r\n    20.label = Your comment:\r\n    20.type = *data[tt_guest][NEW][note]=textarea\r\n    30.label = Name:\r\n    30.type = *data[tt_guest][NEW][cr_name]=input \r\n    40.label = Email:\r\n    40.type = *data[tt_guest][NEW][cr_email]=input\r\n    50.label = Homepage:\r\n    50.type = *data[tt_guest][NEW][www]=input \r\n    60.type = formtype_db=submit\r\n    60.value = Add yourself\r\n  }\r\n  postform.no_cache=1\r\n}\r\n\r\n## This enables the tt_content.record item to display guestbook items:\r\ntt_guest >\r\ntt_guest = < plugin.tt_guest\r\ntt_guest.displayCurrentRecord = 1\r\n', 'PLUGIN: Guestbook\r\nAUTHOR: Kasper Skrhj, kasper@typo3.com\r\n\r\n\r\nHow to activate this:\r\n- Include this static template in your template.\r\n\r\nExpects static template \'styles.content (default)\' to be included.', '');

INSERT INTO static_template VALUES ('68', '0', '1024790859', '988325351', 'plugin.meta [DEPRECIATED]', '', 'plugin.meta >\r\nplugin.meta {\r\n    # cat=plugin.meta; type=string; label= Description: Write a short abstract for your website.\r\n  description = \r\n    # cat=plugin.meta; type=string; label= Keywords: Enter a comma separated list of keywords.\r\n  keywords = \r\n    # cat=plugin.meta; type=string; label= Robots: Use for instance these codes: Index all pages: "all".  Index no pages: "none". Only this page: "index,nofollow".  Only subpages: "noindex,follow"\r\n  robots = \r\n    # cat=plugin.meta; type=string; label= Copyright info: Enter copyright information, eg. "Me Myself and I, 2001. All rights reserved."\r\n  copyright = \r\n    # cat=plugin.meta; type=string; label= Reply-to email\r\n  email = \r\n    # cat=plugin.meta; type=string; label= Author: Enter name of author.\r\n  author = \r\n    # cat=plugin.meta; type=options[,Arabic=ar,Chinese=zh,Danish=dk,Dutch=nl,English=en,Finnish=fi,French=fr,German=de,Greek=el,Hebrew=he,Icelandic=is,Italian=it,Japanese=ja,Norwegian=no,Polish=pl,Portuguese=pt,Russian=ru,Spanish=es,Swedish=sv,Turkish=tr,Multi language=mul]; label= Language: Select language of the content.\r\n  language = \r\n    # cat=plugin.meta; type=string; label= Distribution\r\n  distribution = \r\n    # cat=plugin.meta; type=options[,General,Mature,14 years,Restricted]; label= Rating\r\n  rating = \r\n    # cat=plugin.meta; type=int+; label= Revisit after: Number of days between search engine visits.\r\n  revisit = \r\n    # cat=plugin.meta; type=boolean; label= Always include global: \r\n  includeGlobal = 0\r\n}\r\n', 'plugin.meta >\r\nplugin.meta = PHP_SCRIPT\r\nplugin.meta {\r\n  file = media/scripts/meta.php\r\n  global {\r\n    description = {$plugin.meta.description}\r\n    description_2 = {$styles.content.meta.site_description}\r\n    keywords = {$plugin.meta.keywords}\r\n    keywords_2 = {$styles.content.meta.site_keywords}\r\n    robots = {$plugin.meta.robots}\r\n    copyright = {$plugin.meta.copyright}\r\n    email = {$plugin.meta.email}\r\n    author = {$plugin.meta.author}\r\n    language = {$plugin.meta.language}\r\n    distribution = {$plugin.meta.distribution}\r\n    rating = {$plugin.meta.rating}\r\n    revisit = {$plugin.meta.revisit}\r\n  }\r\n  local {\r\n      // stdWrap, "loads" the local description (if any) for the page\r\n    description.field = description\r\n      // stdWrap, "loads" the local keywords (if any) for the page\r\n    keywords.field = keywords\r\n  }\r\n  flags {\r\n     // Boolean, This includes the keywords/description set in styles.content (default) for backwards compatibility (if not set, keywords_2/description_2 is simply unset). \r\n    useSecondaryDescKey = 1\r\n     // Boolean, This includes the global description (from above), even if local description is found for a page\r\n    alwaysGlobalDescription = {$plugin.meta.includeGlobal}\r\n     // Boolean, This includes the global keywords (from above), even if local keywords are found for a page\r\n    alwaysGlobalKeywords = {$plugin.meta.includeGlobal}\r\n     // Boolean, This enables the "Dublin Core" standard meta-tags.\r\n    DC =1\r\n  }\r\n}\r\n', 'PLUGIN: plugin.meta\r\nAUTHOR: Kasper Skrhj, kasper@typo3.com\r\n\r\n\r\nThis template is used to include an extended set of META-tags on your pages. By using this, you should disable the default meta-tags used.\r\n\r\nUse:\r\nIf your PAGE object is "page" then simply make a reference to the object from the .headerData CARRAY of the PAGE object.\r\n\r\nExample, (in Setup-field of you template, provided that PAGE object is "page"):\r\n\r\npage.headerData.10 =  <plugin.meta\r\npage.meta >', '');

INSERT INTO static_template VALUES ('69', '0', '1017228481', '994368675', 'template; CANDIDATE', '43,68', '# Template specific\r\nfile {\r\n    # cat=basic/file; type=file[IMAGE_EXT]; label=Top image, left:  Image in the top of the content column. Page title may be printed on this image.\r\n  left_bar = media/uploads/candidate/bar_left.gif\r\n    # cat=basic/file; type=file[IMAGE_EXT]; label=Top image, right:  Image in the top of the right column.\r\n  right_bar = media/uploads/candidate/bar_right.gif\r\n    # cat=basic/typo; type=file[ttf]; label=Top image, Font file: TrueType (ttf) font for printing the page title on the top image (left).\r\n  left_bar_fontFile = \r\n    # cat=basic/file; type=file[IMAGE_EXT]; label=Background image: Background image of the page.\r\n  background = \r\n    # cat=basic/file; type=file[IMAGE_EXT]; label=Right column bar: If you enter an image here, it will substitute the \'titlefield\' below the right column top image\r\n  right_subbar = \r\n}\r\n\r\n  # cat=basic/dims; type=offset; label=Page offset: This value defines the margin in pixels from the left and top of the browser window\r\npage.offset = ,20\r\n  # cat=basic; type=options[left,right,center]; label=Alignment of the page: Alignment of the page-table\r\npage.align = center\r\n  # cat=basic/dims; type=int+; label=Content width: This value defines the width in pixels of the table cell where the content is displayed\r\npage.width = 400\r\n  # cat=basic/dims; type=offset; label=Content offset: This value defines the margin in pixels from the left and top of main content table cell\r\npage.contentOffset = 0,0\r\n\r\npagetitle {\r\n    #cat=page/h; type=boolean; label=Page Title, enable: If you set this option, the page title (overruled by the subtitle if present) will be printed right after the menu\r\n  enable = 0\r\n    #cat=page/h; type=wrap; label=Page Title, wrap: Wrapping for the page title after menu\r\n  wrap = <font face="arial,helvetica" size=4 color="#000099"><b>|</b></font><br>\r\n    #cat=page/h; type=int+; label=Page Title, Space before:\r\n  spaceBefore = 7\r\n    #cat=page/h; type=int+; label=Page Title, Space after:\r\n  spaceAfter = 20\r\n}\r\n\r\n\r\nleftBar {\r\n    #cat=basic/typo; type=boolean; label=Enable title on Left Image: If you set this option, the page title (overruled by the subtitle if present) will be printed on the Left Image\r\n  enable = 1\r\n    #cat=basic/typo; type=int+; label=Top image, font size: \r\n  fontSize = 34\r\n    #cat=basic/typo; type=color; label=Top image, font color: \r\n  fontColor = white\r\n    #cat=basic/typo; type=offset; label=Top image, font offset:\r\n  offset = 0,33\r\n    # cat=basic/typo; type=options[left,right,center]; label=Top image, font alignment: \r\n  align = center\r\n}\r\n\r\n  # cat=page/d; type=string; label=Right column title: This is the text string shown in the field right below the top image in the right column\r\npage.titleValue = Link to Typo3.com\r\n  # cat=page/d; type=wrap; label=Right column title, wrap: This is the wrap around the right column title\r\npage.titleWrap = <a href="http://www.typo3.com" target="_top"><font size="1" face="verdana,arial" color="white"><b> | </b></font></a>\r\n\r\n  #cat=page/color; type=color; label=Background color, content frame: \r\npage.contentBgColor = \r\n\r\n  #cat=page/enable; type=boolean; label=Expand Left Image: If you set this option, the left image will expand all over the two columns and the right image will not appear\r\npage.leftImageExpand = 0\r\n\r\n  #cat=page/e; type=color; label=Gap, line color: \r\ngap.linecolor = #000033\r\ngap.width {\r\n    #cat=page/e; type=int+; label=Gap, line width: \r\n  line = 1\r\n    #cat=page/e; type=int+; label=Gap, right space: \r\n  right = 18\r\n    #cat=page/e; type=int+; label=Gap, left space: \r\n  left = 18\r\n}\r\n\r\n\r\nmenu {\r\n    #cat=menu/enable; type=boolean; label=Enable the menu: If you set this option, the menu will appear\r\n  enable = 1\r\n    #cat=menu/color; type=color; label=Background color:\r\n  bgcolor = #000099\r\n    #cat=menu/dims; type=int+; label=Gap between elements: The gap between the menu elements.\r\n  gapwidth=9\r\n    #cat=menu/dims; type=int+; label=Height of each item: The height in pixels of each menu item. This includes the header field in the right column so it follows the menu height!\r\n  itemHeight=1\r\n    #cat=menu/dims; type=int+; label=Space before menu:\r\n  spaceBefore = 7\r\n    #cat=menu/dims; type=int+; label=Space after menu:\r\n  spaceAfter = 20\r\n    #cat=menu/typo; type=wrap; label=Menu item wrap:\r\n  wrap = <font size="1" face="verdana,arial" color="white"><b> | </b></font>\r\n}\r\n\r\n\r\nrightCol {\r\n    #cat=right; type=boolean; label=Enable Right Column: Check this option to enable the right column\r\n  enable = 1\r\n    #cat=right/color; type=color; label=Right column bgColor: This is the background color of content in the right column.\r\n  bgColor = \r\n    #cat=right/dims; type=int+; label=Max Image Width: This indicates that maximum number of pixels (width) a block of images inserted as content is allowed to consume. NOTE: This is specific for the right column! The figure should approx. match the width of the image in the top of this column!\r\n  maxImageWidth = 200\r\n    #cat=right/typo; type=small; label=Bodytext font face\r\n  textStyle_face = Verdana\r\n    #cat=right/typo; type=int[1-5]; label=Bodytext font size\r\n  textStyle_size = 1\r\n    #cat=right/typo; type=color; label=Bodytext font color\r\n  textStyle_color = black\r\n    #cat=right/typo; type=int[1-5]; label=Default Header type: Enter the number of the header layout to be used by default. \r\n  defaultHeaderType = 2\r\n    # cat=right; type=comment; label= Left instead of right?: Click this option, if you want the right column to appear to the left instead.\r\n  leftComment = #\r\n    # cat=right; type=int+; label=Content width: This value defines the width in pixels of the table cell where the content is displayed\r\n  width = 200\r\n}\r\n\r\n# General:\r\n\r\ncontent.wrap.header1 = <div align="center"><font face="verdana" size=4 color="#000099"><b> | </b></font></div>\r\ncontent.wrap.header2 = <font face="verdana" size=4 color="#000099"><b> | </b></font>\r\ncontent.wrap.header3 = <div align="center"><font face="verdana" size=5 color="#000099"><b> | </b></font></div>\r\nstyles.content.imgtext {\r\n  borderColor = #000099\r\n  borderThick = 4\r\n}\r\ncontent.bulletImages.default = <img src="media/uploads/candidate/dot_round.gif">\r\nstyles.content.links.wrap = <b> | </b>\r\n\r\n\r\n# THIS IS NORMALLY SET IN STANDARD TEMLATES:\r\n\r\n# Image widths (remember contentwidth!)\r\nstyles.content.imgtext.maxW = 400\r\n\r\n\r\n# Targets\r\nPAGE_TARGET =\r\ncontent.pageFrameObj = \r\n\r\n  # cat=basic/color/a; type=color; label=Background color: This is the <body>-tag background color of the page.\r\nbgCol = white\r\n  # cat=basic/color/l; type=color; label=Link-color: Link color defined in <body>-tag\r\nlink = #000099\r\n  # cat=basic/color/l; type=color; label=Active Link-color: Active link color defined in <body>-tag\r\nalink = #000099\r\n  # cat=basic/color/l; type=color; label=Visited Link-color: Visited link color defined in <body>-tag\r\nvlink = red\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n## TSConstantsEditor Config\r\nTSConstantEditor.basic {\r\n  header = Standard Template "CANDIDATE"\r\n  description = CANDIDATE is a single-page template with two content columns and a text-based level 1 menu below the top image in the main column.\r\n  bulletlist = Right column: You can disable this column or choose to display it on the left instead. // Right column bar: Text or image. The height is depending on the menu height in the main column. (6) // Top image: The page title (or subtitle if present) is printed on this image. You can disable that. Also you can choose to let the top image expand over both the right column and the main column. In addition the image may vary from section to section on the site as the first image attached to a page on the first menulevel will be used instead. (4,7)// Gap between columns: You can disable it, control the widths and color used. // Right column top image: You can define an image to be shown in the top of the right column. (5) // Text menu: You can disable it and choose to display the page title below it. \r\n  image = gfx/CANDIDATE_basic.gif\r\n  1=page.offset\r\n  2=page.contentOffset\r\n  3=page.width,styles.content.imgtext.maxW\r\n  4=file.left_bar\r\n  5=file.right_bar\r\n  6=file.right_subbar\r\n  7=file.left_bar_fontFile,leftBar.enable,leftBar.fontSize,leftBar.fontColor,leftBar.offset,leftBar.align\r\n  8=file.background,bgCol\r\n}\r\nTSConstantEditor.page {\r\n  header = Standard Template "CANDIDATE"\r\n  image = gfx/CANDIDATE_page.gif\r\n  1=page.leftImageExpand\r\n  2=page.contentBgColor\r\n  3=page.titleValue,page.titleWrap\r\n  4=pagetitle.enable,pagetitle.wrap,pagetitle.spaceBefore,pagetitle.spaceAfter\r\n  5=gap.linecolor,gap.width.line,gap.width.right,gap.width.left\r\n}\r\nTSConstantEditor.menu {\r\n  header = Standard Template "CANDIDATE"\r\n  description = Text based, horizontal menu\r\n  bulletlist = \r\n  image = gfx/CANDIDATE_menu.gif\r\n  1=menu.gapwidth\r\n  2=menu.itemHeight\r\n  3=menu.spaceBefore\r\n  4=menu.spaceAfter\r\n}', '\r\n# Page title\r\ntemp.pageTitle = TEXT\r\ntemp.pageTitle {\r\n  if.isTrue = {$pagetitle.enable}\r\n  field = title\r\n  override.field = subtitle\r\n  fontTag = {$pagetitle.wrap}\r\n  space = {$pagetitle.spaceBefore} | {$pagetitle.spaceAfter}\r\n}\r\n\r\n\r\n## The header:\r\ntemp.header = IMAGE\r\ntemp.header.wrap = |<br>\r\ntemp.header.file = GIFBUILDER\r\ntemp.header.file {\r\n  XY = [5.w], [5.h]\r\n  5 = IMAGE\r\n\r\n  5.file = {$file.left_bar}\r\n  5.file.import = uploads/media/\r\n  5.file.import.data = levelmedia : 1\r\n  5.file.import.listNum=0\r\n\r\n  10 = TEXT\r\n  10.text.field = nav_title // title\r\n  10.text.override.field = subtitle\r\n  10.text.if.isTrue = {$leftBar.enable}\r\n  10.fontFile = {$file.left_bar_fontFile}\r\n  10.fontSize = {$leftBar.fontSize}\r\n  10.fontColor = {$leftBar.fontColor}\r\n  10.offset = {$leftBar.offset}\r\n  10.align = {$leftBar.align}\r\n  10.niceText = 1\r\n}\r\n\r\n## The textmenu\r\ntemp.tmenu = HMENU\r\ntemp.tmenu.if.isTrue = {$menu.enable}\r\ntemp.tmenu.entryLevel = 0\r\ntemp.tmenu.stdWrap.space = {$menu.spaceBefore} | {$menu.spaceAfter}\r\ntemp.tmenu.1 = TMENU\r\ntemp.tmenu.1.wrap = <table border=0 cellpadding=1 cellspacing=0 width=100%><tr> | </tr></table>\r\ntemp.tmenu.1.NO {\r\n  ATagBeforeWrap = 1\r\n  allWrap = |*||*|     <td bgcolor="{$menu.bgcolor}" align="center" width="10%">  | </td><td width="1%"><img src="clear.gif" width="{$menu.gapwidth}" height={$menu.itemHeight}></td>     ||     <td bgcolor="{$menu.bgcolor}" align="center" width="10%">  | </td>\r\n  linkWrap = {$menu.wrap}\r\n}\r\ntemp.tmenu.1.ACT < temp.tmenu.1.NO\r\ntemp.tmenu.1.ACT = 1\r\ntemp.tmenu.1.ACT.stdWrap.wrap = <u> |</u>\r\n\r\n## titlefield\r\ntemp.titlefield = IMAGE\r\ntemp.titlefield.file = {$file.right_subbar}\r\ntemp.titlefield.wrap = | <br>\r\ntemp.titlefield.stdWrap.ifEmpty = {$page.titleValue}\r\ntemp.titlefield.stdWrap.ifEmpty.wrap = <table border=0 cellpadding=0 cellspacing=0 width=100%><tr><td bgcolor="{$menu.bgcolor}" align="center">{$page.titleWrap}</td><td bgcolor="{$menu.bgcolor}"><img src="clear.gif" width="1" height={$menu.itemHeight}></td></tr></table>\r\ntemp.titlefield.stdWrap.space = {$menu.spaceBefore} | {$menu.spaceAfter}\r\n\r\n\r\n## PAGE:\r\npage = PAGE\r\npage.typeNum = 0\r\npage.bodyTag < styles.content.bodyTag\r\npage.noLinkUnderline = 1\r\npage.bodyTagMargins = 0\r\npage.bgImg = {$file.background}\r\npage.wrap = <table border=0 cellpadding=0 cellspacing=0 width="1"><tr> |</tr></table>\r\npage.stdWrap {\r\n  append < temp.header\r\n  append.wrap = |<br>\r\n  append.if.isTrue = {$page.leftImageExpand}\r\n  append.stdWrap.wrapAlign = {$page.align}\r\n\r\n  offsetWrap = {$page.offset}\r\n  offsetWrap.stdWrap.wrapAlign = {$page.align}\r\n}\r\n\r\npage.10 = COA\r\npage.10.wrap = <td valign=top bgcolor="{$page.contentBgColor}"> | </td>\r\npage.10 {\r\n  5 < temp.header\r\n  5.wrap = |<br>\r\n  5.if.isFalse = {$page.leftImageExpand}\r\n  10 < temp.tmenu\r\n  12 < temp.pageTitle\r\n  20 = COA\r\n  20.stdWrap.offsetWrap = {$page.contentOffset}\r\n  20.10 = TEXT\r\n  20.10.value = <img src="clear.gif" width="{$page.width}" height=1><br>\r\n  20.15 < styles.content.editPanelPage\r\n  20.20 < styles.content.get\r\n  20.30 < styles.content.lastUpdate\r\n  20.40 < styles.content.typo3_powered\r\n}\r\n\r\npage.20 = TEXT\r\npage.20.value = <td><img src="clear.gif" width="{$gap.width.left}" height=1></td>\r\npage.20.if.isTrue = {$gap.width.left}\r\npage.21 = TEXT\r\npage.21.value = <td bgcolor="{$gap.linecolor}"><img src="clear.gif" width="{$gap.width.line}" height=1></td>\r\npage.21.if.isTrue = {$gap.width.line}\r\npage.22 = TEXT\r\npage.22.value = <td><img src="clear.gif" width="{$gap.width.right}" height=1></td>\r\npage.22.if.isTrue = {$gap.width.right}\r\n\r\npage.30 = COA\r\npage.30.if.isTrue = {$rightCol.enable}\r\npage.30.wrap = <td valign=top bgcolor="{$rightCol.bgColor}"><img src="clear.gif" width="{$rightCol.width}" height=1><br> | </td>\r\npage.30 {\r\n  5 = IMAGE\r\n  5.file = {$file.right_bar}\r\n  5.wrap = | <br>\r\n  5.if.isFalse = {$page.leftImageExpand}\r\n  10 < temp.titlefield\r\n\r\n  15 = LOAD_REGISTER\r\n  15.maxImageWidth = {$rightCol.maxImageWidth}\r\n  15.textStyle_face = {$rightCol.textStyle_face}\r\n  15.textStyle_size = {$rightCol.textStyle_size}\r\n  15.textStyle_color = {$rightCol.textStyle_color}\r\n  15.defaultHeaderType = {$rightCol.defaultHeaderType}\r\n  15.pageColor = {$rightCol.bgColor}\r\n\r\n  20 < styles.content.getBorder\r\n  25 = RESTORE_REGISTER\r\n}\r\n\r\n\r\n\r\n{$rightCol.leftComment} page.5 < page.30\r\n{$rightCol.leftComment} page.6 < page.20\r\n{$rightCol.leftComment} page.7 < page.21\r\n{$rightCol.leftComment} page.8 < page.22\r\n{$rightCol.leftComment} page.30 >\r\n{$rightCol.leftComment} page.20 >\r\n{$rightCol.leftComment} page.21 >\r\n{$rightCol.leftComment} page.22 >\r\n\r\n\r\npage.config.admPanel = 1\r\nconfig.intTarget = {$PAGE_TARGET}\r\npage.headerData.999 < plugin.meta\r\n', '', '');

INSERT INTO static_template VALUES ('70', '0', '1017228502', '994368694', 'template; RE', '19,43,68', 'topFrameHeight = 91\r\nleftFrameWidth = 197\r\n\r\nbackground.file {\r\n    # cat=basic/file/t; type=file[IMAGE_EXT]; label=Left frame background image:\r\n  left = media/uploads/re/left_background.gif\r\n    # cat=basic/file/t; type=file[IMAGE_EXT]; label=Top frame background image:\r\n  top = media/uploads/re/top_background.gif\r\n    # cat=basic/file/t; type=file[IMAGE_EXT]; label=Page frame background image:\r\n  page = \r\n}\r\n\r\n\r\ntopmenu {\r\n    # cat=menu/file; type=int+; label=Background image width: Enter the width of the menu background image (pixels)\r\n  bgImgWidth = 674\r\n    # cat=menu/file; type=file[IMAGE_EXT]; label=Expanding Background Image: The text of the menu items are printed on this background image which expands horizontally behind the items.\r\n  file.background = media/uploads/re/menuback.gif\r\n    # cat=menu/typo; type=file[ttf]; label=Font file: TrueType (ttf) font for the menu. \r\n  file.font = \r\n    # cat=menu/typo; type=int[1-40]; label=Font size: Font size on menu items\r\n  fontSize = 13\r\n    # cat=menu/typo; type=color; label=Font color: Font color on menu items\r\n  fontColor = #006699\r\n    # cat=menu/typo; type=offset; label=Text offset: Enter the offset of the text from the upper-left corner on the menu items\r\n  offset =  11,12\r\n    # cat=menu/typo; type=int+; label=Font spacing: Letter spacing of the menu items\r\n  spacing = 2\r\n\r\n    # cat=menu/typo/zaa; type=boolean; label=Rollover: Click this option if you want a rollover (mouseover) effect on the menu items.\r\n  RO = 1\r\n    # cat=menu/typo/zab; type=color; label=Rollover, font color: Enter the rollover font color of the text on the menu item.\r\n  RO.fontColor = #DF0029\r\n    # cat=menu/typo/zba; type=boolean; label=Active: Click this option if you want menu items to be active. \'Active\' means that you\'ve clicked the item and are now in that section of the website.\r\n  ACT = 1\r\n    # cat=menu/typo/zbb; type=color; label=Active, font color: Enter the active font color of the text on the menu item.\r\n  ACT.fontColor = #DF0029\r\n\r\n    # cat=menu/dims; type=int+; label=Space between items: \r\n  itemDistance = 25\r\n    # cat=menu//x; type=int[0-256]; label=Reduce colors: Number of indexed colors on menu items. If your menu items end up with weird colors, you can try to enter a higher number of colors. But the graphics-files will also increase their filesize.\r\n  reduceColors = 16\r\n}\r\n\r\n  # cat=top/dims; type=offset; label=Menu offset:\r\ntop.menuOffset = 0,0\r\n  # cat=top/dims; type=offset; label=Logo offset:\r\ntop.logoOffset = 200,0\r\n  # cat=top/file/t; type=file[IMAGE_EXT]; label=Top logo:\r\ntop.file.logo = media/uploads/re/my_logo.gif\r\n  # cat=top/file/t; type=file[IMAGE_EXT]; label=Left-of-menu image:\r\ntop.file.leftOfMenu = media/uploads/re/top_leftofmenu.gif\r\n  # cat=top/dims; type=offset; label=Offset, Left-of-menu image:\r\ntop.leftOfMenuOffset = 0,0\r\n\r\n\r\nleftmenu {\r\n    # cat=leftmenu/file; type=file[IMAGE_EXT]; label=Background image: This is the background image of a normal mode menu item\r\n  file.background = media/uploads/re/leftmenu_no.gif\r\n    # cat=leftmenu/file/z8; type=file[IMAGE_EXT]; label=Background image, Rollover: This is the background image of a rollover mode menu item\r\n  RO.file.background = media/uploads/re/leftmenu_ro.gif\r\n    # cat=leftmenu/file/z9; type=file[IMAGE_EXT]; label=Background image, Active: This is the background image of a active mode menu item\r\n  ACT.file.background = media/uploads/re/leftmenu_act.gif\r\n\r\n    # cat=leftmenu/typo; type=file[ttf]; label=Font file: TrueType (ttf) font for the menu. \r\n  file.font = \r\n    # cat=leftmenu/typo; type=int[1-40]; label=Font size: Font size on menu items\r\n  fontSize = 12\r\n    # cat=leftmenu/typo; type=color; label=Font color: Font color on menu items\r\n  fontColor = #E9F6FB\r\n    # cat=leftmenu/typo; type=int+; label=Font spacing: Letter spacing of the menu items\r\n  spacing = 0\r\n    # cat=leftmenu/typo; type=offset; label=Text offset: Enter the offset of the text from the upper-left corner on the menu items\r\n  offset =  -33,14\r\n    # cat=leftmenu/typo; type=options[left,right,center]; label=Alignment of text: Alignment of text on the menu items\r\n  align = right\r\n\r\n\r\n    # cat=leftmenu/typo/zaa; type=boolean; label=Rollover: Click this option if you want a rollover (mouseover) effect on the menu items.\r\n  RO = 1\r\n    # cat=leftmenu/typo/zab; type=color; label=Rollover, font color: Enter the rollover font color of the text on the menu item.\r\n  RO.fontColor = #E9F6FB\r\n    # cat=leftmenu/typo/zba; type=boolean; label=Active: Click this option if you want menu items to be active. \'Active\' means that you\'ve clicked the item and are now in that section of the website.\r\n  ACT = 1\r\n    # cat=leftmenu/typo/zbb; type=color; label=Active, font color: Enter the active font color of the text on the menu item.\r\n  ACT.fontColor = #E9F6FB\r\n\r\n    # cat=leftmenu//x; type=int[0-256]; label=Reduce colors: Number of indexed colors on menu items. If your menu items end up with weird colors, you can try to enter a higher number of colors. But the graphics-files will also increase their filesize.\r\n  reduceColors = 16\r\n}\r\n\r\n  # cat=leftmenu/dims; type=offset; label=Offset, whole menu: Enter the offset of the whole menu-block from upper-left corner of the left frame.\r\nleft.offset = 0,0\r\n  # cat=leftmenu/file; type=file[IMAGE_EXT]; label=After menu image: This is an image placed after the menu-items. It doesn\'t appear if the menu is empty.\r\nleft.file.afterMenuImage = media/uploads/re/menu_afterimage.gif\r\n\r\nleft.menuWrap = <table border=0 cellpadding=0 cellspacing=0> |</table>\r\nleft.itemWrap = <tr><td>|</td></tr>\r\n\r\n\r\n\r\n  # cat=basic/file/t; type=file[css]; label=Page frame stylesheet:\r\npage.file.stylesheet = \r\n  # cat=basic/dims; type=offset; label= Page offset: Offset of the whole page-table from upper-left corner of the browser-window\r\npage.offset = 0,0\r\n  # cat=basic/dims; type=int+; label=Content width: This value defines the width in pixels of the table cell where the content is displayed\r\npage.contentWidth = 600\r\n\r\n\r\n\r\n  # cat=basic/color/a; type=color; label=Background color: This is the <body>-tag background color of the page.\r\nbgCol = white\r\n  # cat=basic/color/l; type=color; label=Link-color: Link color defined in <body>-tag\r\nlink = \r\n  # cat=basic/color/l; type=color; label=Active Link-color: Active link color defined in <body>-tag\r\nalink = \r\n  # cat=basic/color/l; type=color; label=Visited Link-color: Visited link color defined in <body>-tag\r\nvlink = \r\n\r\nPAGE_TARGET = page\r\ncontent.pageFrameObj = page\r\nstyles.content.imgtext.maxW = 600\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n## TSConstantsEditor Config\r\nTSConstantEditor.basic {\r\n  header = Standard Template "RE"\r\n  description = This is a frame-based template. There\'s a top frame with a graphical menu, 1. level. There\'s a left frame with a graphical menu, 2. level. And there\'s a page frame for the content. You can define rollover and active states for the menu items.\r\n  bulletlist = 3 frames, top, left and page. // Top logo image. // Background images in all frames (optional). // Select background images and colors for the left menu for normal, rollover and active state. // Select font color for normal, rollover and active state of the top menu. // Set an image below the left menu. \r\n  image = gfx/RE_basic.gif\r\n  1=leftFrameWidth\r\n  2=topFrameHeight\r\n  3=page.offset\r\n  4=page.contentWidth,styles.content.imgtext.maxW\r\n  5=background.file.left\r\n  6=background.file.top\r\n  7=background.file.page,page.file.stylesheet,bgCol\r\n}\r\nTSConstantEditor.menu {\r\n  header = Standard Template "RE"\r\n  description = Primary menu in top frame (level 1).\r\n  bulletlist = Rollover and Active state colors. // Background image, which expands continually behind the items on the menu.\r\n  image = gfx/RE_menu.gif\r\n  1=topmenu.file.background,topmenu.bgImgWidth\r\n  2=topmenu.itemDistance\r\n  3=topmenu.RO,topmenu.RO.fontColor\r\n  4=topmenu.file.font,topmenu.fontSize,topmenu.fontColor,topmenu.offset,topmenu.spacing\r\n}\r\nTSConstantEditor.top {\r\n  header = Standard Template "RE"\r\n  description = Top frame features\r\n  bulletlist = Menu with horizontally expanding background image. // Logo image // Image to the left of the menu.\r\n  image = gfx/RE_top.gif\r\n  1=top.menuOffset\r\n  2=top.logoOffset\r\n  3=top.leftOfMenuOffset\r\n  4=top.file.logo\r\n  5=top.file.leftOfMenu\r\n}\r\nTSConstantEditor.leftmenu {\r\n  header = Standard Template "RE"\r\n  description = Secondary menu in left frame (level 2).\r\n  bulletlist = Rollover and Active state background images. // Image after menu. // Offset in the left frame.\r\n  image = gfx/RE_leftmenu.gif\r\n  1=left.offset\r\n  2=leftmenu.file.background,leftmenu.file.font,leftmenu.fontSize,leftmenu.fontColor,leftmenu.spacing,leftmenu.offset,leftmenu.align\r\n  3=leftmenu.RO.file.background,leftmenu.RO.fontColor,leftmenu.RO\r\n  4=left.file.afterMenuImage\r\n}\r\n', '# Topmenu\r\ntemp.topmenu = HMENU\r\ntemp.topmenu.stdWrap.offsetWrap = {$top.menuOffset}\r\ntemp.topmenu.1 = GMENU\r\ntemp.topmenu.1.target = _top\r\ntemp.topmenu.1.applyTotalW = 5\r\ntemp.topmenu.1.max = {$topmenu.bgImgWidth}\r\ntemp.topmenu.1.min = {$topmenu.bgImgWidth}\r\ntemp.topmenu.1.NO {\r\n  XY = [10.w]+{$topmenu.itemDistance},  [5.h]\r\n  backColor = red\r\n  reduceColors = {$topmenu.reduceColors}\r\n  5 = IMAGE\r\n  5.file = {$topmenu.file.background}\r\n\r\n  10 = TEXT\r\n  10.text.field = nav_title // title\r\n  10.offset ={$topmenu.offset}\r\n  10.fontFile = {$topmenu.file.font}\r\n  10.fontColor = {$topmenu.fontColor}\r\n  10.fontSize = {$topmenu.fontSize}\r\n  10.niceText = 1\r\n  10.spacing = {$topmenu.spacing}\r\n}\r\ntemp.topmenu.1.RO < temp.topmenu.1.NO\r\ntemp.topmenu.1.RO = {$topmenu.RO}\r\ntemp.topmenu.1.RO {\r\n  10.fontColor = {$topmenu.RO.fontColor}\r\n}\r\ntemp.topmenu.1.ACT < temp.topmenu.1.NO\r\ntemp.topmenu.1.ACT = {$topmenu.ACT}\r\ntemp.topmenu.1.ACT {\r\n  10.fontColor = {$topmenu.ACT.fontColor}\r\n}\r\n\r\n\r\n# Leftmenu\r\ntemp.leftmenu = HMENU\r\ntemp.leftmenu.stdWrap.required = 1\r\ntemp.leftmenu.stdWrap.offsetWrap = {$left.offset} \r\ntemp.leftmenu.stdWrap {\r\n  prepend = IMAGE\r\n  prepend.file = {$left.file.afterMenuImage}\r\n  prepend.wrap = {$left.itemWrap}\r\n  wrap3 = {$left.menuWrap}\r\n}\r\ntemp.leftmenu.entryLevel = 1\r\ntemp.leftmenu.1 = GMENU\r\ntemp.leftmenu.1.target = frameset2\r\ntemp.leftmenu.1.NO {\r\n  wrap= {$left.itemWrap}\r\n  XY = [5.w],  [5.h]\r\n  backColor = red\r\n  reduceColors = {$leftmenu.reduceColors}\r\n  5 = IMAGE\r\n  5.file = {$leftmenu.file.background}\r\n\r\n  10 = TEXT\r\n  10.text.field = nav_title // title\r\n  10.offset ={$leftmenu.offset}\r\n  10.fontFile = {$leftmenu.file.font}\r\n  10.fontColor = {$leftmenu.fontColor}\r\n  10.fontSize = {$leftmenu.fontSize}\r\n  10.align = {$leftmenu.align}\r\n  10.niceText = 1\r\n  10.spacing = {$leftmenu.spacing}\r\n}\r\ntemp.leftmenu.1.RO < temp.leftmenu.1.NO\r\ntemp.leftmenu.1.RO = {$leftmenu.RO}\r\ntemp.leftmenu.1.RO {\r\n  5.file = {$leftmenu.RO.file.background}\r\n  10.fontColor = {$leftmenu.RO.fontColor}\r\n}\r\ntemp.leftmenu.1.ACT < temp.leftmenu.1.NO\r\ntemp.leftmenu.1.ACT = {$leftmenu.ACT}\r\ntemp.leftmenu.1.ACT {\r\n  5.file = {$leftmenu.ACT.file.background}\r\n  10.fontColor = {$leftmenu.ACT.fontColor}\r\n}\r\n\r\n\r\n# LEFT FRAME, menu\r\nleft.bgImg = {$background.file.left}\r\nleft.bodyTagMargins = 0\r\nleft.10 < temp.leftmenu\r\n\r\n# TOP FRAME, menu\r\ntop.bgImg = {$background.file.top}\r\ntop.bodyTagMargins = 0\r\ntop.wrap = <table border=0 cellpadding=0 cellspacing=0> | </table>\r\ntop.5 = HTML\r\ntop.5.value = <tr><td colspan=2 valign=top>\r\ntop.10 = IMAGE\r\ntop.10.file = {$top.file.logo}\r\ntop.10.stdWrap.offsetWrap = {$top.logoOffset}\r\ntop.10.linkWrap = <a href="index.php?{0}" target="_top"> |</a>\r\ntop.15 = HTML\r\ntop.15.value = </td></tr><tr><td valign=top>\r\ntop.20 = IMAGE\r\ntop.20.file = {$top.file.leftOfMenu}\r\ntop.20.stdWrap.offsetWrap = {$top.leftOfMenuOffset}\r\ntop.25 = HTML\r\ntop.25.value = </td><td valign=top>\r\ntop.30 < temp.topmenu\r\ntop.35 = HTML\r\ntop.35.value = </td></tr>\r\n\r\n# PAGE\r\npage.bgImg = {$background.file.page}\r\npage.stylesheet = {$page.file.stylesheet}\r\npage.bodyTag < styles.content.bodyTag\r\npage.10 = CTABLE\r\npage.10 {\r\n  offset = {$page.offset}\r\n  c.5 < styles.content.editPanelPage\r\n  c.10 < styles.content.get\r\n  c.30 < styles.content.lastUpdate\r\n  c.40 < styles.content.typo3_powered\r\n\r\n  cWidth = {$page.contentWidth}\r\n  tableParams = border=0 cellpadding=0 cellspacing=0 width={$page.contentWidth}\r\n}\r\n\r\n\r\n\r\n\r\npage.config.admPanel = 1\r\nconfig.intTarget = {$PAGE_TARGET}\r\npage.headerData.999 < plugin.meta\r\nframeset.headerData.999 < plugin.meta\r\n\r\n', '', '');

INSERT INTO static_template VALUES ('71', '0', '1005269189', '994370877', 'template; NEWSLETTER', '43,68,82', 'styles.content.imgtext.comment_CSL-LinkList = \r\ncontent.defaultHeaderType = 5\r\n\r\n  # cat=basic/file/t; type=file[css]; label=Page stylesheet:\r\npage.file.css = media/uploads/newsletter/style.css\r\n  # cat=basic/file/t; type=file[html,htm,txt,tmpl]; label=Page template file:\r\npage.file.template = media/uploads/newsletter/template.html\r\n  # cat=basic/file/t; type=file[IMAGE_EXT]; label=Top frame logo image:\r\npage.file.toplogo =  media/uploads/newsletter/toplogo.gif\r\n\r\n\r\n  # cat=basic/color/a; type=color; label=Header frame color, right:\r\nright.frameCol = #90A8BF\r\n  # cat=basic/color/a; type=color; label=Bottom line color:\r\nlineCol = #90A8BF\r\n  # cat=basic//; type=string; label=Copyright email\r\ncopyright = stuff@stuff.dk\r\n\r\n  # cat=basic/color/a; type=color; label=Background color: This is the <body>-tag background color of the page.\r\nbgCol = #eaf6f6\r\n  # cat=basic/color/l; type=color; label=Link-color: Link color defined in <body>-tag\r\nlink = #90a8bf\r\n  # cat=basic/color/l; type=color; label=Active Link-color: Active link color defined in <body>-tag\r\nalink = #cccccc\r\n  # cat=basic/color/l; type=color; label=Visited Link-color: Visited link color defined in <body>-tag\r\nvlink = #90a8bf\r\n  # cat=basic/color/l; type=color; label=Text-color: Text color as defined in <body>-tag\r\ntext = #666666\r\n\r\n\r\nPAGE_TARGET = \r\ncontent.pageFrameObj = \r\nstyles.content.imgtext.maxW = 400\r\n  # cat=basic/dims; type=int+; label=Content width: This value defines the width in pixels of the table where the content is displayed in the page-frame\r\ncontentWidth = 400\r\n\r\n\r\n\r\n\r\n\r\n\r\n## TSConstantsEditor Config\r\nTSConstantEditor.basic {\r\n  header = Standard Template "NEWSLETTER"\r\n  description = This is a single-page template with no menu! It\'s designed to be used as a newsletter template sent as an HTML-email with the internal Direct Mail module. It\'s based on an HTML-template file, so the main layout of the page is depending on the content of that template. One thing excluded from the template is the top logo which is defined directly in TypoScript.\r\n  bulletlist = HTML-template file based. Redesign it with your favourite HTML-editor! // Features content in two columns (optional). // By default headers in the margin column are based on header layout 5, which is an image-header. \r\n  image = gfx/NEWSLETTER_basic.gif\r\n  1=page.file.css,page.file.template\r\n  2=page.file.toplogo\r\n  3=bgCol\r\n}', 'config.insertDmailerBoundaries = 1\r\n\r\n\r\n# Redefining header type 5\r\n\r\nlib.stdheader.10.5.file.10.antialias = 0\r\nlib.stdheader.10.5.file.10.fontColor = #666666\r\nlib.stdheader.10.5.file.XY = 400 ,30\r\nlib.stdheader.10.5.file.10.fontSize = 12\r\nlib.stdheader.10.5.file.10.offset = 6,18\r\nlib.stdheader.10.5.file.XY = 400 ,27\r\nlib.stdheader.10.5.file {\r\n  20 = BOX\r\n  20.dimensions = 0,0,400,1\r\n  20.color = {$right.frameCol}\r\n  21 < .20\r\n  21.align = , b\r\n  22 < .20\r\n  22.dimensions = 0,0,1,40\r\n  23 < .22\r\n  23.align = r\r\n}\r\nlib.stdheader.10.4 >\r\nlib.stdheader.10.4 < lib.stdheader.10.5\r\nlib.stdheader.10.4.file.XY = 185,27\r\n\r\n\r\n\r\n# Setting up page\r\n\r\npage = PAGE\r\npage.typeNum = 0\r\npage.stylesheet = {$page.file.css}\r\npage.hoverStyle = COLOR: #cccccc; TEXT-DECORATION: underline\r\npage.bodyTag = <body text={$text} vLink={$vlink} aLink={$alink} link={$link} bgColor={$bgCol} leftMargin=0 topMargin=5>\r\n\r\npage.stdWrap.offsetWrap = 10,0\r\npage.5 = IMAGE\r\npage.5.wrap = |<br>\r\npage.5.file = {$page.file.toplogo}\r\n\r\npage.10 = TEMPLATE\r\npage.10 {\r\n  template = FILE\r\n  template.file = {$page.file.template}\r\n  marks.CONTENT_NORMAL = COA\r\n  marks.CONTENT_NORMAL {\r\n    5 < styles.content.editPanelPage\r\n    10 < styles.content.get\r\n    30 < styles.content.lastUpdate\r\n    40 < styles.content.typo3_powered\r\n  }\r\n  marks.CONTENT_MARGIN = COA\r\n  marks.CONTENT_MARGIN {\r\n    10 = LOAD_REGISTER\r\n    10.defaultHeaderType = 4\r\n    20 < styles.content.getBorder\r\n    30 = RESTORE_REGISTER\r\n  }\r\n  marks.LINECOLOR = TEXT\r\n  marks.LINECOLOR.value = {$lineCol}\r\n  marks.COPYRIGHT = TEXT\r\n  marks.COPYRIGHT.value = {$copyright}\r\n}\r\n\r\n\r\npage.config.admPanel = 1\r\nconfig.intTarget = {$PAGE_TARGET}\r\npage.headerData.999 < plugin.meta\r\n\r\n## HTML mail ping:\r\npage.20 = TEXT\r\npage.20.value = <img src=t3lib/gfx/dmailerping.gif width=1 height=1 dmailerping=1>\r\n\r\n## Plain text setup:\r\nlib.alt_plaintext.renderObj.header.2 {\r\n  preLineChar = \r\n  postLineChar >\r\n  preLineChar >\r\n  preLineLen >\r\n  postLineLen >\r\n  preBlanks = 0\r\n}\r\nalt_plaintext.10.marks.CONTENT >\r\nalt_plaintext.10.marks.CONTENT = COA\r\nalt_plaintext.10.marks.CONTENT {\r\n  10 < styles.content.get\r\n  10.renderObj = < lib.alt_plaintext.renderObj\r\n  20 < styles.content.getBorder\r\n  20.renderObj = < lib.alt_plaintext.renderObj\r\n}\r\n\r\nalt_plaintext_test.10.marks.CONTENT < alt_plaintext.10.marks.CONTENT', '', '');

INSERT INTO static_template VALUES ('72', '0', '1017228520', '994370877', 'template; HYPER', '13,43,68', 'background.file {\r\n    # cat=basic/file/t; type=file[IMAGE_EXT]; label=Top frame background image:\r\n  top = media/uploads/hyper/background_top.gif\r\n    # cat=basic/file/t; type=file[IMAGE_EXT]; label=Page frame background image:\r\n  page = media/uploads/hyper/background_main.gif\r\n}\r\n  # cat=basic/file/t; type=file[IMAGE_EXT]; label=Top frame logo image:\r\ntop.logo = media/uploads/hyper/title.gif\r\n  # cat=basic/file/t; type=file[css]; label=Page frame stylesheet:\r\npage.css = media/uploads/hyper/sprache.css\r\n\r\ntopFrameHeight = 150\r\n\r\ntopmenu1 {\r\n    # cat=menu/dims; type=int+; label=Height of menu items (pixels)\r\n  height = 15\r\n    # cat=menu/dims; type=int+; label=Additional width: The menu items are getting their width from the title. This number is added to that making room for a margin.\r\n  addWidth = 20\r\n    # cat=menu/dims; type=int+; label=Space between items: \r\n  itemSpace = 30\r\n\r\n    # cat=menu/dims; type=int+; label=Y-offset of the pop-up layers: This is the distance in pixels from the top of the browser window to the menu pop-up layers\r\n  yOffset = 38\r\n    # cat=menu/dims; type=int+; label=X-offset of the pop-up layers: This is the distance in pixels from the left of the browser window to the menu pop-up layers\r\n  xOffset = 231\r\n\r\n    # cat=menu/color; type=color; label=Menu background color\r\n  bgCol = black\r\n    # cat=menu/color; type=color; label=Menu transparent color: Setting this to the same as the \'Menu background color\' will render the menu items transparent. This is looking best if you don\'t use antialiasing on the text.\r\n  bgCol_transp = black\r\n\r\n    # cat=menu/typo; type=file[ttf]; label=Font file: TrueType (ttf) font for the menu. Good fonts non-antialiased are Mini-7 (size 7) or verdana (size 10)\r\n  file.fontFile = \r\n    # cat=menu/typo; type=int[1-40]; label=Font size: Font size on menu items\r\n  fontSize = 10\r\n    # cat=menu/typo; type=color; label=Font color: Font color on menu items\r\n  fontColor = white\r\n    # cat=menu/typo; type=offset; label=Text offset: Enter the offset of the text from the upper-left corner on the menu items\r\n  fontOffset = 10,10\r\n    # cat=menu/typo; type=options[,upper,lower]; label=Text case: You can force the text on the menu items to be either lowercase or uppercase.\r\n  case =\r\n    # cat=menu/typo; type=boolean; label= Antialias: If this option is set, the fonts will be rendered antialiased. Default is \'off\' because of the transparent background.\r\n  antialias = 0\r\n\r\n    # cat=menu/color; type=color; label=Box Linecolor: Line color of the box making a border on each item\r\n  boxColor = #60C82F\r\n\r\n    # cat=menu/typo; type=boolean; label=Rollover: Click this option if you want a rollover (mouseover) effect on the menu items.\r\n  RO = 1\r\n    # cat=menu/typo; type=color; label=Rollover, background color:\r\n  RO.bgColor = #60C82F\r\n    # cat=menu/color; type=color; label=Rollover, Box Linecolor: Line color of the box making a border on each item.\r\n  RO.boxColor = white\r\n    # cat=menu/typo; type=color; label=Rollover, font color: Enter the rollover font color of the text on the menu item.\r\n  RO.fontColor = white\r\n}\r\n\r\n\r\n\r\n\r\n\r\ntopmenu2 {\r\n\r\n    # cat=menu2/; type=wrap; label=Wrap of the whole menu: This is by default a table with a white background color\r\n  menuWrap = <table border=0 cellpadding=2 cellspacing=0 bgColor=white><tr><td>   <table border=0 cellspacing=0 cellpadding=0 bgColor=#2F302F><tr><td>|</td></tr></table>  </td></tr></table>\r\n    # cat=menu2/; type=wrap; label=Wrap of each menu item: \r\n  itemWrap = | <br>\r\n\r\n    # cat=menu2/dims; type=int+; label=Height of menu items (pixels)\r\n  height = 12\r\n    # cat=menu2/dims; type=int+; label=Additional width: The menu items are getting their width from the page title. This number is added to that making room for margin.\r\n  addWidth = 5\r\n\r\n    # cat=menu2/color; type=color; label=Menu background color\r\n  bgCol = #2F302F\r\n    # cat=menu2/color; type=color; label=Menu transparent color: Setting this to the same as the \'Menu background color\' will render the menu items transparent. This is looking best if you don\'t use antialiasing on the text.\r\n  bgCol_transp = \r\n\r\n    # cat=menu2/typo; type=file[ttf]; label=Font file: TrueType (ttf) font for the menu. Good fonts non-antialiased are Mini-7 (size 7) or verdana (size 10)\r\n  file.fontFile = \r\n    # cat=menu2/typo; type=int[1-40]; label=Font size: Font size on menu items\r\n  fontSize = 10\r\n    # cat=menu2/typo; type=color; label=Font color: Font color on menu items\r\n  fontColor = #60C82F\r\n    # cat=menu2/typo; type=offset; label=Text offset: Enter the offset of the text from the upper-left corner on the menu items\r\n  fontOffset = 1,8\r\n    # cat=menu2/typo; type=options[,upper,lower]; label=Text case: You can force the text on the menu items to be either lowercase or uppercase.\r\n  case =\r\n    # cat=menu2/typo; type=boolean; label= Antialias: If this option is set, the fonts will be rendered antialiased. Default is \'off\' because of the transparent background.\r\n  antialias = 0\r\n\r\n    # cat=menu2/color; type=color; label=Linecolor: Line color between the items\r\n  lineColor = #606760\r\n\r\n    # cat=menu2/typo; type=boolean; label=Rollover: Click this option if you want a rollover (mouseover) effect on the menu items.\r\n  RO = 1\r\n    # cat=menu2/typo; type=color; label=Rollover, background color:\r\n  RO.bgColor = #60C82F\r\n    # cat=menu2/color; type=color; label=Rollover, Linecolor: Line color between the items\r\n  RO.lineColor = white\r\n    # cat=menu2/typo; type=color; label=Rollover, font color: Enter the rollover font color of the text on the menu item.\r\n  RO.fontColor = white\r\n}\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\ntoptitle{\r\n    # cat=toptitle/enable; type=boolean; label=Show the top title image?: If you unset this option, the top title image is not shown.\r\n  enable = 1\r\n    # cat=toptitle/dims; type=offset; label=Image offset: Enter the offset of the image\r\n  offset = 57,17\r\n    # cat=toptitle/dims; type=int+; label=Height of top title image (pixels)\r\n  height = 13\r\n    # cat=toptitle/dims; type=int+; label=Width of top title image (pixels)\r\n  width = 370\r\n\r\n    # cat=toptitle/color; type=color; label=Background color\r\n  bgCol = white\r\n    # cat=toptitle/color; type=color; label=Transparent color: Setting this to the same as the \'Menu background color\' will render the image transparent. This is looking best if you don\'t use antialiasing on the text.\r\n  bgCol_transp = white\r\n\r\n    # cat=toptitle/typo; type=file[ttf]; label=Font file: TrueType (ttf) font for the image. Good fonts non-antialiased are Mini-7 (size 7) or verdana (size 10)\r\n  file.fontFile = \r\n    # cat=toptitle/typo; type=int[1-40]; label=Font size: Font size on menu items\r\n  fontSize = 14\r\n    # cat=toptitle/typo; type=color; label=Font color: Font color on menu items\r\n  fontColor = #909790\r\n    # cat=toptitle/typo; type=offset; label=Text offset: Enter the offset of the text from the upper-left corner on the image\r\n  fontOffset = 0,9\r\n    # cat=toptitle/typo; type=options[,upper,lower]; label=Text case: You can force the text on the image to be either lowercase or uppercase.\r\n  case =\r\n    # cat=toptitle/typo; type=boolean; label= Antialias: If this option is set, the fonts will be rendered antialiased. Default is \'off\' because of the transparent background.\r\n  antialias = 0\r\n}\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n  # cat=basic/dims; type=int+; label=Content width: This value defines the width in pixels of the table where the content is displayed in the page-frame\r\ncontentWidth = 560\r\nstyles.content.imgtext.maxW = 560\r\n\r\n  # cat=basic/color/a; type=color; label=Background color: This is the <body>-tag background color of the page.\r\nbgCol = #333333\r\n  # cat=basic/color/l; type=color; label=Link-color: Link color defined in <body>-tag\r\nlink = \r\n  # cat=basic/color/l; type=color; label=Active Link-color: Active link color defined in <body>-tag\r\nalink = \r\n  # cat=basic/color/l; type=color; label=Visited Link-color: Visited link color defined in <body>-tag\r\nvlink = \r\n\r\n  # cat=page/dims; type=offset; label=Content field offset: Enter the offset of the content cell from the upper-left corner of the page frame\r\npage.offset = 30,40\r\n  # cat=page/color/x; type=color; label=Content cell background color: This is the color of the content cell\r\npage.contentCellBgCol = #333333\r\n\r\n  # cat=page/dims; type=offset; label=Top frame offset: Enter the offset of the content in the top frame. This includes logo and menu\r\ntop.offset = 0,25\r\n  # cat=page/dims; type=offset; label=Distance to menu: Enter the distance in pixels from the rigth edge of the logo to the menu\r\ntop.distFromLogoToMenu = 231\r\n\r\n\r\nPAGE_TARGET = page\r\ncontent.pageFrameObj = page\r\n\r\n\r\n\r\n\r\n\r\n\r\n## TSConstantsEditor Config\r\nTSConstantEditor.basic {\r\n  header = Standard Template "HYPER"\r\n  description = Hyper is a frame-based template with a top frame containing the navigation and a page frame with the content in one column. The top navigation is based on DHTML layers. In addition the page title is rendered on an image and you can define a top logo.\r\n  bulletlist = DHTML graphical menu in top frame. // Top logo. (5) // Top page title image. // Define background images. (3,4) // Prepared for css stylesheet.\r\n  image = gfx/HYPER_basic.gif\r\n  1=topFrameHeight\r\n  2=contentWidth,styles.content.imgtext.maxW\r\n  3=background.file.top\r\n  4=background.file.page,page.css\r\n  5=top.logo\r\n}\r\nTSConstantEditor.page {\r\n  header = Standard Template "HYPER"\r\n  description = \r\n  bulletlist = \r\n  image = gfx/HYPER_page.gif\r\n  1=top.offset\r\n  2=top.distFromLogoToMenu\r\n  3=page.offset\r\n  4=page.contentCellBgCol\r\n}\r\nTSConstantEditor.toptitle {\r\n  header = Standard Template "HYPER"\r\n  description = This is a gif/png image with the page title printed on it. A special feature is that this image has a transparent background by default. That means you should use a font like Verdana, Arial or Mini7-series non-antialiased to get the best display.\r\n  bulletlist = Define TrueType font, size and color // Define antialiased / non-antialiased setting // Define letter case to upper- or lowercase\r\n  image = gfx/HYPER_toptitle.gif\r\n  1=toptitle.offset\r\n  2=toptitle.width\r\n  3=toptitle.height\r\n  4=toptitle.file.fontFile,toptitle.fontSize,toptitle.fontColor,toptitle.fontOffset,toptitle.case,toptitle.antialias,toptitle.bgCol,toptitle.bgCol_transp\r\n}\r\nTSConstantEditor.menu {\r\n  header = Standard Template "HYPER"\r\n  description = DHTML based graphical menu. This menu is featuring a 1-2 pixels border in selectable colors. It uses no background images. The menu is designed to work transparent on a page background.\r\n  bulletlist = Define border color for normal and rollover state.// Define background color for rollover state. // Define TrueType font, size and color // Define antialiased / non-antialiased setting // Define letter case to upper- or lowercase\r\n  image = gfx/HYPER_menu.gif\r\n  1=topmenu1.height\r\n  2=topmenu1.itemSpace\r\n  3=topmenu1.addWidth\r\n  4=topmenu1.yOffset,topmenu1.xOffset\r\n  5=topmenu1.file.fontFile,topmenu1.fontSize,topmenu1.fontColor,topmenu1.fontOffset,topmenu1.case,topmenu1.antialias,topmenu1.bgCol,topmenu1.bgCol_transp,topmenu1.boxColor\r\n  6=topmenu1.RO,topmenu1.RO.bgColor,topmenu1.RO.fontColor,topmenu1.RO.boxColor\r\n}\r\nTSConstantEditor.menu2 {\r\n  header = Standard Template "HYPER"\r\n  description = DHTML based graphical menu layers. This is the second level of the menu.\r\n  bulletlist = Define color of separator line. // Define border color for normal and rollover state.// Define background color for rollover state. // Define TrueType font, size and color // Define antialiased / non-antialiased setting // Define letter case to upper- or lowercase\r\n  image = gfx/HYPER_menu2.gif\r\n  1=topmenu2.height\r\n  2=topmenu2.file.fontFile,topmenu2.fontSize,topmenu2.fontColor,topmenu2.fontOffset,topmenu2.case,topmenu2.antialias,topmenu2.bgCol,topmenu2.bgCol_transp,\r\n  3=topmenu2.RO,topmenu2.RO.bgColor,topmenu2.RO.fontColor\r\n  4=topmenu2.lineColor,topmenu2.RO.lineColor\r\n}\r\n', 'includeLibs.gmenu_layers = media/scripts/gmenu_layers.php\r\n\r\n\r\n# Defining\r\n\r\ntemp.topmenu1 = HMENU\r\ntemp.topmenu1.wrap = <nobr> | </nobr>\r\ntemp.topmenu1.1 = GMENU_LAYERS\r\ntemp.topmenu1.1.target = _top\r\ntemp.topmenu1.1.NO {\r\n  wrap =  | <img src=clear.gif width={$topmenu1.itemSpace} height=1>\r\n  XY = [10.w]+{$topmenu1.addWidth},{$topmenu1.height}\r\n  transparentColor = {$topmenu1.bgCol_transp}\r\n  backColor = {$topmenu1.bgCol}\r\n\r\n  10 = TEXT\r\n  10.text.field = nav_title // title\r\n  10.text.case = {$topmenu1.case}\r\n  10.fontFile = {$topmenu1.file.fontFile}\r\n  10.fontSize = {$topmenu1.fontSize}\r\n  10.fontColor = {$topmenu1.fontColor}\r\n  10.antiAlias = {$topmenu1.antialias}\r\n  10.offset = {$topmenu1.fontOffset}\r\n\r\n  20 = BOX\r\n  20.color = {$topmenu1.boxColor}\r\n  20.dimensions = 1,1,[10.w]+{$topmenu1.addWidth}-2,1\r\n\r\n  21 = BOX\r\n  21.color = {$topmenu1.boxColor}\r\n  21.dimensions = 1,-1,[10.w]+{$topmenu1.addWidth}-2,1\r\n  21.align = ,b\r\n\r\n  22 = BOX\r\n  22.color = {$topmenu1.boxColor}\r\n  22.dimensions = 1,1,1,{$topmenu1.height}-2\r\n\r\n  23 = BOX\r\n  23.color = {$topmenu1.boxColor}\r\n  23.dimensions = -1,1,1,{$topmenu1.height}-2\r\n  23.align= r\r\n}\r\ntemp.topmenu1.1.RO  < temp.topmenu1.1.NO\r\ntemp.topmenu1.1.RO = {$topmenu1.RO}\r\ntemp.topmenu1.1.RO {\r\n\r\n  7 = BOX\r\n  7.color = {$topmenu1.RO.bgColor}\r\n  7.dimensions = 0,0,[10.w]+{$topmenu1.addWidth},{$topmenu1.height}\r\n\r\n  10.fontColor = {$topmenu1.RO.fontColor}\r\n\r\n  20 = BOX\r\n  20.color = {$topmenu1.RO.boxColor}\r\n  20.dimensions = 0,0,[10.w]+{$topmenu1.addWidth},2\r\n\r\n  21 = BOX\r\n  21.color = {$topmenu1.RO.boxColor}\r\n  21.dimensions = 0,0,[10.w]+{$topmenu1.addWidth},2\r\n  21.align = ,b\r\n\r\n  22 = BOX\r\n  22.color = {$topmenu1.RO.boxColor}\r\n  22.dimensions = 0,0,2,{$topmenu1.height}\r\n\r\n  23 = BOX\r\n  23.color = {$topmenu1.RO.boxColor}\r\n  23.dimensions = 0,0,2,{$topmenu1.height}\r\n  23.align= r\r\n}\r\n\r\ntemp.topmenu1.1 {\r\n  xPosOffset = -{$topmenu1.xOffset}\r\n  lockPosition = x\r\n  expAll = 1\r\n\r\n  topOffset = {$topmenu1.yOffset}\r\n  lockPosition_adjust = {$topmenu1.itemSpace}\r\n  directionLeft = 0\r\n  lockPosition_addSelf=0\r\n}\r\n\r\ntemp.topmenu1.2 = GMENU\r\ntemp.topmenu1.2.target = _top\r\ntemp.topmenu1.2.useLargestItemX = 1\r\ntemp.topmenu1.2.wrap = {$topmenu2.menuWrap}\r\ntemp.topmenu1.2.NO {\r\n wrap = {$topmenu2.itemWrap}\r\n\r\n  transparentColor = {$topmenu2.bgCol_transp}\r\n  backColor = {$topmenu2.bgCol}\r\n\r\n  XY = |*||*| [10.w]+{$topmenu2.addWidth},{$topmenu2.height} || [10.w]+{$topmenu2.addWidth},{$topmenu2.height}-1\r\n\r\n  10 = TEXT\r\n  10.text.field = nav_title // title\r\n  10.text.case = {$topmenu2.case}\r\n  10.fontFile = {$topmenu2.file.fontFile}\r\n  10.fontSize = {$topmenu2.fontSize}\r\n  10.fontColor = {$topmenu2.fontColor}\r\n  10.antiAlias = {$topmenu2.antialias}\r\n  10.offset = {$topmenu2.fontOffset}\r\n\r\n  30 = BOX\r\n  30.color = {$topmenu2.lineColor}\r\n  30.dimensions = 0,{$topmenu2.height}-1,300,1\r\n}\r\ntemp.topmenu1.2.RO < temp.topmenu1.2.NO\r\ntemp.topmenu1.2.RO = {$topmenu2.RO}\r\ntemp.topmenu1.2.RO {\r\n  backColor = {$topmenu2.RO.bgColor}\r\n  10.fontColor = {$topmenu2.RO.fontColor}\r\n  30.color = {$topmenu2.RO.lineColor}\r\n}\r\n\r\n\r\n\r\n\r\n\r\n\r\n# Top frame page\r\n\r\ntop.bgImg = {$background.file.top}\r\ntop.bodyTag = <body bgcolor="#FFFFFF" text="#000000">\r\ntop.bodyTagMargins = 0\r\ntop.10 = CTABLE\r\ntop.10 {\r\n  tableParams = border=0 cellpadding=0 cellspacing=0\r\n  offset = {$top.offset}\r\n\r\n  lm.1 = IMAGE\r\n  lm.1.file = {$top.logo}\r\n  lm.1.wrap = |<br>\r\n  lm.1.params = vspace=4\r\n\r\n  lm.3 = CLEARGIF\r\n  lm.3.width={$top.distFromLogoToMenu}\r\n\r\n  c.1 < temp.topmenu1\r\n}\r\n\r\ntop.20 = OTABLE\r\ntop.20.offset = {$toptitle.offset}\r\ntop.20.1 = IMAGE\r\ntop.20.1.if.isTrue = {$toptitle.enable}\r\ntop.20.1.file = GIFBUILDER\r\ntop.20.1.file {\r\n    XY = {$toptitle.width},{$toptitle.height}\r\n    backColor = {$toptitle.bgCol}\r\n    transparentColor = {$toptitle.bgCol_transp}\r\n    10=TEXT\r\n    10.text.field = nav_title // title\r\n    10.text.case = {$toptitle.case}\r\n    10.fontFile = {$toptitle.file.fontFile}\r\n    10.fontSize = {$toptitle.fontSize}\r\n    10.fontColor = {$toptitle.fontColor}\r\n    10.antiAlias = {$toptitle.antialias}\r\n    10.offset = {$toptitle.fontOffset}\r\n  }\r\ntop.20.1.wrap = |<br>\r\n\r\n\r\n# Page object\r\n\r\npage.bgImg = {$background.file.page}\r\npage.bodyTagMargins = 0\r\npage.bodyTag < styles.content.bodyTag\r\npage.stylesheet = {$page.css}\r\n\r\npage.10 = CTABLE\r\npage.10.offset = {$page.offset}\r\npage.10.cWidth={$contentWidth}\r\npage.10.tableParams = border=0 cellpadding=10 cellspacing=0 width={$contentWidth}\r\npage.10.c.TDParams = bgColor={$page.contentCellBgCol}\r\npage.10.c {\r\n  5 < styles.content.editPanelPage\r\n  10 < styles.content.get\r\n  30 < styles.content.lastUpdate\r\n  40 < styles.content.typo3_powered\r\n}\r\n\r\ntt_content.textpic.20.textMargin = 90\r\n\r\n\r\n\r\n\r\npage.config.admPanel = 1\r\nconfig.intTarget = {$PAGE_TARGET}\r\npage.headerData.999 < plugin.meta\r\nframeset.headerData.999 < plugin.meta\r\n', '', '');

INSERT INTO static_template VALUES ('73', '0', '1000351645', '994370877', 'template; CrCPH', '43,68', '  # cat=basic/file; type=file[html,htm,txt,inc,tmpl]; label=Template file:  This file is very important. It\'s the total template of the website. It\'s working with markers and subparts which define where menus, content and logos go. Two of the headertypes are defined inside as well! You should take a look into the default file and follow the directions inside.\r\npage.file.template = media/uploads/crcph/main.htm\r\n\r\n\r\ncontent.headerSpace >\r\nstyles.content.imgtext.maxW = 400\r\n\r\n\r\nPAGE_TARGET = \r\ncontent.pageFrameObj = \r\n\r\n  # cat=basic/color/a; type=color; label=Background color: This is the <body>-tag background color of the page.\r\nbgCol = #D6D3CE\r\n  # cat=basic/color/l; type=color; label=Link-color: Link color defined in <body>-tag\r\nlink = #006699\r\n  # cat=basic/color/l; type=color; label=Active Link-color: Active link color defined in <body>-tag\r\nalink = #00659C\r\n  # cat=basic/color/l; type=color; label=Visited Link-color: Visited link color defined in <body>-tag\r\nvlink = #00659C\r\n\r\n\r\n## TSConstantsEditor Config\r\nTSConstantEditor.basic {\r\n  header = Standard Template "CrCPH"\r\n  description = CrCPH is a single-page template based on an HTML-template file. Thus most of the layout is done by redesigning the template file with your favorite HTML-editor! \r\n  bulletlist = Based on an actual HTML-template file and relies less on TypoScript. // Two text based menus defined directly in the HTML-template file. // The default content header is also defined directly in the HTML-template file. // Support for two columns of content.\r\n  image = gfx/CrCPH_basic.gif\r\n  1=page.file.template\r\n  2=styles.content.imgtext.maxW\r\n}', '# Setting up the main template\r\n\r\ntemp.mainTemplate = TEMPLATE\r\ntemp.mainTemplate {\r\n  template = FILE\r\n  template.file = {$page.file.template} \r\n}\r\n\r\n\r\n# Setting up Level 1 menu, based on template!\r\n\r\ntemp.L1menu = HMENU\r\ntemp.L1menu.1 = TMENU\r\ntemp.L1menu.1.NO {\r\n  before.cObject < temp.mainTemplate\r\n  before.cObject {\r\n    workOnSubpart = SUB_MENU_ITEM\r\n    marks.MENU_TITLE = TEXT\r\n    marks.MENU_TITLE.field = title\r\n    wraps.LINK = TEXT\r\n    wraps.LINK {\r\n      value = |\r\n      typolink.parameter.field = uid\r\n    }\r\n  }\r\n  doNotShowLink = 1\r\n}\r\ntemp.L1menu.1.ACT < temp.L1menu.1.NO\r\ntemp.L1menu.1.ACT = 1\r\ntemp.L1menu.1.ACT.before.cObject.workOnSubpart = SUB_MENU_ITEM_ACT\r\n\r\n\r\n# Setting up Level 2 menu, based on template!\r\n\r\ntemp.L2menuItems = HMENU\r\ntemp.L2menuItems.entryLevel = 1\r\ntemp.L2menuItems.1 = TMENU\r\ntemp.L2menuItems.1.NO {\r\n  before.cObject < temp.mainTemplate\r\n  before.cObject {\r\n    workOnSubpart = SUB_L2MENU_ITEM\r\n    marks.MENU_TITLE = TEXT\r\n    marks.MENU_TITLE.field = title\r\n    wraps.LINK = TEXT\r\n    wraps.LINK {\r\n      value = |\r\n      typolink.parameter.field = uid\r\n    }\r\n  }\r\n  doNotShowLink = 1\r\n}\r\ntemp.L2menuItems.1.ACT < temp.L2menuItems.1.NO\r\ntemp.L2menuItems.1.ACT = 1\r\ntemp.L2menuItems.1.ACT.before.cObject.workOnSubpart = SUB_L2MENU_ITEM_ACT\r\n\r\ntemp.L2menu < temp.mainTemplate\r\ntemp.L2menu {\r\n  \r\n  workOnSubpart = SUB_L2MENU\r\n  subparts.SUB_L2MENU_ALLITEMS < temp.L2menuItems\r\n}\r\n\r\n\r\n\r\n\r\n# Page object\r\n\r\npage = PAGE\r\npage.typeNum = 0\r\npage.bodyTag < styles.content.bodyTag\r\npage.bodyTagMargins=0\r\npage.headerData.20 < temp.mainTemplate\r\npage.headerData.20 {\r\n  workOnSubpart = HEADER_DATA\r\n}\r\n\r\n\r\n\r\npage.10  < temp.mainTemplate\r\npage.10 {\r\n  workOnSubpart = DOCUMENT_BODY\r\n  subparts.SUB_CONTENT = COA\r\n  subparts.SUB_CONTENT {\r\n    5 < styles.content.editPanelPage\r\n    10 < styles.content.get\r\n    30 < styles.content.lastUpdate\r\n    40 < styles.content.typo3_powered\r\n  }\r\n  subparts.SUB_CONTENT_MARGIN = COA\r\n  subparts.SUB_CONTENT_MARGIN {\r\n    5 < temp.L2menu\r\n    10 = LOAD_REGISTER\r\n    10.defaultHeaderType = 2\r\n    10.maxImageWidth = 150\r\n    20 < styles.content.getBorder\r\n    30 = RESTORE_REGISTER\r\n  }\r\n  subparts.SUB_MENU < temp.L1menu\r\n}\r\n\r\n\r\n\r\nlib.stdheader.stdWrap >\r\nlib.stdheader.10.1 >\r\nlib.stdheader.10.1  < temp.mainTemplate\r\nlib.stdheader.10.1 {\r\n  workOnSubpart = SUB_HEADER1\r\n  marks.MARK_HEADER = TEXT\r\n  marks.MARK_HEADER.field = header\r\n}\r\nlib.stdheader.10.2 >\r\nlib.stdheader.10.2 < lib.stdheader.10.1\r\nlib.stdheader.10.2.workOnSubpart = SUB_HEADER2\r\nlib.stdheader.10.stdWrap.wrap >\r\n\r\n\r\ntt_content.menu.20.1 >\r\ntt_content.menu.20.1 < temp.L2menu\r\ntt_content.menu.20.1.subparts.SUB_L2MENU_ALLITEMS {\r\n  special = directory\r\n  special.value.field = pages\r\n}\r\n\r\n\r\n\r\n# Configuration\r\n\r\nconfig.extTarget = _blank\r\nconfig.compensateFieldWidth = 0.7\r\n\r\n\r\npage.config.admPanel = 1\r\nconfig.intTarget = {$PAGE_TARGET}\r\npage.headerData.999 < plugin.meta\r\n', '', '');

INSERT INTO static_template VALUES ('74', '0', '1002640513', '997262098', 'template; GREEN', '75,43,68', 'page.file {\r\n    # cat=basic/file; type=file[html,htm,txt,inc,tmpl]; label=Template file:  This file is very important. It\'s the total template of the website. It\'s working with markers and subparts which define where menus, content and logos go. Two of the headertypes are defined inside as well! You should take a look into the default file and follow the directions inside.\r\n  template = media/uploads/green/template.tmpl\r\n    # cat=basic/file/t; type=file[IMAGE_EXT]; label=Background image: Page background image\r\n  background = media/uploads/green/background_page.png\r\n    # cat=basic/file/t; type=file[IMAGE_EXT]; label=Top image: This is the image in the upper right corner of the page. Depends on the existence of the marker, ###TOP_IMAGE###, in the template file.\r\n  topimage =  media/uploads/green/pict_top.jpg\r\n    # cat=basic/file/t; type=file[IMAGE_EXT]; label=Top logo: This is the logo in the upper left corner of the page. Depends on the existence of the marker, ###TOP_LOGO###, in the template file.\r\n  toplogo =  media/uploads/green/pict_logo.png\r\n    # cat=basic/file/t; type=file[IMAGE_EXT]; label=Cell Background image: Cell or table background image, inserted as \'background="path_to_image"\'. Depends on the existence of the marker, ###CELL_BACK###, in the template file.\r\n  cellBack = \r\n}\r\n\r\n\r\nsubmenu {\r\n    # cat=menu3/typo; type=wrap; label=Menu element font wrap: Styling wrap for the submenu item\r\n  linkWrap = <font face="arial,helvetica,sans-serif" size=1 color=white>|</font>\r\n    # cat=menu3/typo; type=wrap; label=Menu element wrap: Wrap for the entire submenu item\r\n  allWrap = <tr><td>|</td></tr>\r\n    # cat=menu3/typo; type=wrap; label=All menu wrap: This finally wraps all elements\r\n  wrap = <table border=0 cellpadding=4 cellspacing=0> | </table>\r\n    # cat=menu3/dims; type=offset; label=Menu offset: Offset of the submenu\r\n  menuOffset = 10,10\r\n    # cat=menu3/dims; type=int+; label=Submenu Cell width: This value defines the width in pixels of the tablecell where the submenu is displayed\r\n  cellWidth = 200\r\n}\r\n\r\n\r\n  # cat=basic/typo; type=wrap; label=Page title wrap: Wrap for the page title displayed above the menu. Depends on the existence of the marker, ###PAGE_TITLE###, in the template file.\r\npage.titleWrap = <font face="verdana" color="white"><b>|</b></font>\r\n  # cat=basic/typo; type=offset; label=Page title offset: Offset of the page title displayed above the menu\r\npage.offset = 10,10\r\n\r\n  # cat=basic/dims; type=offset; label=Content offset: Offset of the page content\r\npage.contentOffset = 0,10\r\n\r\n\r\n  # cat=basic/color/a; type=color; label=Background color: This is the <body>-tag background color of the page.\r\nbgCol = white\r\n  # cat=basic/color/l; type=color; label=Link-color: Link color defined in <body>-tag\r\nlink = \r\n  # cat=basic/color/l; type=color; label=Active Link-color: Active link color defined in <body>-tag\r\nalink = \r\n  # cat=basic/color/l; type=color; label=Visited Link-color: Visited link color defined in <body>-tag\r\nvlink = \r\n\r\nPAGE_TARGET = \r\ncontent.pageFrameObj = \r\nstyles.content.imgtext.maxW = 400\r\n  # cat=basic/dims; type=int+; label=Content width: This value defines the width in pixels of the table where the content is displayed in the page-frame\r\ncontentWidth = 400\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n## TSConstantsEditor Config\r\nTSConstantEditor.basic {\r\n  header = Standard Template "GREEN"\r\n  description = This is a single-page template based on an HTML-template file. This means the position of elements such as the menu, submenu, page content and logo images are defined by the position of markers in an HTML-file, you may want to edit.\r\n  bulletlist = HTML-template file based: Lets you design your site layout by your favourite HTML-editor! (1)// 1+2 level DHTML based pop-up menu. 1. level is graphical and 2. level is text based. // Rollover state on DHTML-menu. // 3. level optional menu, text based. // Page title inserted in template (optional). (7) // Top logo and top image (basically the same) may be inserted in HTML-template file. (6,5) // Optionally use of a table/tablecell background image defined from TypoScript.  \r\n  image = gfx/GREEN_basic.gif\r\n  1=page.file.template\r\n  2=page.contentOffset\r\n  3=contentWidth,styles.content.imgtext.maxW\r\n  4=page.file.background,bgCol\r\n  5=page.file.topimage\r\n  6=page.file.toplogo\r\n  7=page.titleWrap,page.offset\r\n}\r\nTSConstantEditor.menu3 {\r\n  header = Standard Template "GREEN"\r\n  description = Simple Text based menu.\r\n  bulletlist = \r\n  image = gfx/GREEN_menu3.gif\r\n  1=submenu.menuOffset\r\n  2=submenu.cellWidth\r\n  3=submenu.linkWrap,submenu.allWrap\r\n  4=submenu.wrap\r\n}', '#  Submenu (level 3!), text based\r\n\r\ntemp.submenu = HMENU\r\ntemp.submenu.entryLevel = 2\r\ntemp.submenu.1 = TMENU\r\ntemp.submenu.1.wrap = {$submenu.wrap}\r\ntemp.submenu.stdWrap.offsetWrap = {$submenu.menuOffset}\r\ntemp.submenu.1.NO {\r\n  allWrap =  {$submenu.allWrap}\r\n  linkWrap = {$submenu.linkWrap}\r\n  ATagBeforeWrap = 1\r\n}\r\n\r\n\r\n# Page object\r\n\r\npage = PAGE\r\npage.typeNum = 0\r\npage.bodyTag < styles.content.bodyTag\r\npage.bodyTagMargins = 0\r\npage.bgImg = {$page.file.background}\r\npage.noLinkUnderline = 1\r\n\r\npage.10 = TEMPLATE\r\npage.10 {\r\n  template = FILE\r\n  template.file = {$page.file.template}\r\n  workOnSubpart = DOCUMENT_BODY\r\n  marks {\r\n    TOP_LOGO = IMAGE\r\n    TOP_LOGO.file = {$page.file.toplogo}\r\n\r\n    TOP_IMAGE = IMAGE\r\n    TOP_IMAGE.file = {$page.file.topimage}\r\n\r\n    PAGE_TITLE = TEXT\r\n    PAGE_TITLE.field = title\r\n    PAGE_TITLE.wrap = {$page.titleWrap}\r\n    PAGE_TITLE.offsetWrap = {$page.offset}\r\n\r\n    MAIN_MENU < styles.gmenu_layer.green\r\n\r\n    SUB_MENU = COA\r\n    SUB_MENU.10 < temp.submenu\r\n    SUB_MENU.20 = CLEARGIF\r\n    SUB_MENU.20.width = {$submenu.cellWidth}\r\n\r\n    CONTENT = COA\r\n    CONTENT.stdWrap.offsetWrap = {$page.contentOffset}\r\n    CONTENT.5 < styles.content.editPanelPage\r\n    CONTENT.10 < styles.content.get\r\n    CONTENT.20 = CLEARGIF\r\n    CONTENT.20.width = {$contentWidth}\r\n    CONTENT.20.wrap = <br>|\r\n    CONTENT.30 < styles.content.lastUpdate\r\n    CONTENT.40 < styles.content.typo3_powered\r\n\r\n    CELL_BACK = IMG_RESOURCE\r\n    CELL_BACK.file = {$page.file.cellBack}\r\n    CELL_BACK.stdWrap.required = 1\r\n    CELL_BACK.stdWrap.wrap = background="|"\r\n  }\r\n}\r\n\r\n\r\n\r\npage.config.admPanel = 1\r\nconfig.intTarget = {$PAGE_TARGET}\r\npage.headerData.999 < plugin.meta\r\n', '', '');

INSERT INTO static_template VALUES ('75', '0', '1017228535', '997271475', 'styles.gmenu_layer.green', '', 'styles.gmenu_layer.green >\r\nstyles.gmenu_layer.green {\r\n    # cat=menu/color; type=color; label=Menu background color1: (See below)\r\n  bgCol = #7b8ca5\r\n\r\n    # cat=menu/typo; type=file[ttf]; label=Font file: TrueType (ttf) font for the menu.\r\n  file.fontFile = \r\n    # cat=menu/typo; type=int[1-40]; label=Font size: Font size on menu items\r\n  fontSize = 10\r\n    # cat=menu/typo; type=color; label=Font color: Font color on menu items\r\n  fontColor = white\r\n    # cat=menu/typo; type=offset; label=Text offset: Enter the offset of the text from the upper-left corner on the menu items\r\n  offset = 11,14\r\n    # cat=menu/typo; type=offset; label=Text offset of first item: Enter the offset of the text from the upper-left corner on the first menu item in the menu! You may want to set the x dimensions a few pixels below the general value. Keep the y-dimension the same.\r\n  firstOffset = 11,14\r\n    # cat=menu/typo; type=int[0-20]; label=Letter spacing: Enter the spacing between characters\r\n  spacing=1\r\n    # cat=menu//x; type=boolean; label=NiceText: Disable this for higher speed, but less font rendering quality.\r\n  niceText=1\r\n    # cat=menu//x; type=boolean; label=AntiAlias: Anti Alias enabling. Default is on. Disable this if you\'re using "pixel-fonts" like Mini7. (NiceText must be disabled).\r\n  antiAlias=1\r\n\r\n\r\n    # cat=menu/typo; type=boolean; label=Rollover: Click this option if you want a rollover (mouseover) effect on the menu items.\r\n  RO = 0\r\n    # cat=menu/typo; type=color; label=Rollover, font color: Enter the rollover font color of the text on the menu item.\r\n  RO.fontColor =\r\n    # cat=menu/color; type=color; label=Rollover, Menu background color: Background color of the menu-items when the mouse cursor moves over the item.\r\n  RO.bgCol = #608ca5\r\n\r\n    # cat=menu/typo; type=boolean; label=Active: Click this option if you want a \'Active\' effect on the menu items. \'Active\' means how the item looks when you\'re on that page or a subpage to that page.\r\n  ACT = 0\r\n    # cat=menu/typo; type=color; label=Active, font color: Enter the \'Active\' font color of the text on the menu item.\r\n  ACT.fontColor =\r\n    # cat=menu/color; type=color; label=Active, Menu background color:  Background color of the menu-items when \'Active\'.\r\n  ACT.bgCol = #7b7392\r\n\r\n    # cat=menu//x; type=int[1-256]; label=Reduce colors: Number of indexed colors on menu items. If your menu items end up with weird colors, you can try to enter a higher number of colors. But the graphics-files will also increase their filesize.\r\n  reduceColors = 64\r\n    # cat=menu/dims; type=int+; label=Item Height: The height in pixels of the menu items\r\n  height = 19\r\n\r\n    # cat=menu/typo; type=offset; label=Menu offset: Enter the offset of the whole menu\r\n  menuOffset = 20,0\r\n}\r\n\r\n\r\nstyles.gmenu_layer.green.file {\r\n    # cat=menu/file/x1; type=file[IMAGE_EXT]; label=Background image: This image is tiled horizontally on the background of the menu item.\r\n  background = media/uploads/green/menu_back.png\r\n    # cat=menu/file/x1; type=file[IMAGE_EXT]; label=First image: This image is placed to the left on the first menu item\r\n  first = media/uploads/green/menu_first.png\r\n    # cat=menu/file/x1; type=file[IMAGE_EXT]; label=Middle image: This image is placed to the left on all other items than the first\r\n  middle = media/uploads/green/menu_middle.png\r\n    # cat=menu/file/x1; type=file[IMAGE_EXT]; label=Last image: This image is placed to the right on the last menu item.\r\n  last = media/uploads/green/menu_last.png\r\n    # cat=menu/file/x1; type=file[IMAGE_EXT]; label=Sublevel image: This image is placed to the right before the \'Last\' image on the menu items which has a submenu!\r\n  sublevel = media/uploads/green/menu_sublevel.png\r\n}\r\n\r\n\r\nstyles.gmenu_layer.green.layer {\r\n    # cat=menu2/dims; type=int+; label=X-offset of the pop-up layers: This is the distance in pixels from the left of the browser window to the menu pop-up layers\r\n  xPosOffset = 30\r\n    # cat=menu2/dims; type=int+; label=Y-offset of the pop-up layers: This is the distance in pixels from the top of the browser window to the menu pop-up layers\r\n  topOffset = 105\r\n    # cat=menu2/enable; type=boolean; label=Automatic hide: If set, the menu layer hides when mouse roll out of the layer area.\r\n  mouseOutHide = 0\r\n}\r\n\r\n\r\nstyles.gmenu_layer.green.2 {\r\n    # cat=menu2/typo; type=wrap; label=Menu element wrap: Wrap for the entire submenu item\r\n  allWrap = <tr><td>|</td></tr>\r\n    # cat=menu2/typo; type=wrap; label=Menu element font wrap: Styling wrap for the submenu item\r\n  linkWrap = <font face="arial,helvetica,sans-serif" size=1 color=white>|</font>\r\n    # cat=menu2/typo; type=wrap; label=All menu wrap: This finally wraps all elements\r\n  wrap = <table border=0 bgcolor="#003300" cellpadding=4 cellspacing=0> | </table>\r\n}\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n## TSConstantsEditor Config\r\nTSConstantEditor.menu {\r\n  header = GREEN GMENU_LAYER\r\n  description = Graphical level-1 menu with a \'first\', \'middle\' and \'last\' bullet. You can also define a bullet indicating if a menu-item has subitems.\r\n  bulletlist = DHTML-based layers on level 2 (text-based level 2 menu, see \'MENU2\'). // \'first\' bullet: An image which appears in the left side of the first menu item only. (3)// \'middle\' bullet: An image which appears in the right side of all menu items which are not the last item. (4)// \'last\' bullet: An image which appears in the right side of the last item in the menu only. (5)// \'sublevel\' bullet: An image which indicates the existence of sublevels. (6)// Rollover and active state colors. // Menu item background image. (2)\r\n  image = gfx/GREEN_menu.gif\r\n  1=styles.gmenu_layer.green.height\r\n  2=styles.gmenu_layer.green.file.background,styles.gmenu_layer.green.bgCol\r\n  3=styles.gmenu_layer.green.file.first\r\n  4=styles.gmenu_layer.green.file.middle\r\n  5=styles.gmenu_layer.green.file.last\r\n  6=styles.gmenu_layer.green.file.sublevel\r\n  7=styles.gmenu_layer.green.file.fontFile,styles.gmenu_layer.green.fontSize,styles.gmenu_layer.green.fontColor,styles.gmenu_layer.green.offset,styles.gmenu_layer.green.firstOffset,styles.gmenu_layer.green.spacing\r\n}\r\nTSConstantEditor.menu2 {\r\n  header = GREEN GMENU_LAYER\r\n  description = DHTML layers with level 2. Menu is text based.\r\n  bulletlist = \r\n  image = gfx/GREEN_menu2.gif\r\n  1=styles.gmenu_layer.green.layer.xPosOffset,styles.gmenu_layer.green.layer.topOffset\r\n  2=styles.gmenu_layer.green.2.allWrap,styles.gmenu_layer.green.2.linkWrap\r\n  3=styles.gmenu_layer.green.2.wrap\r\n}\r\n', 'includeLibs.gmenu_layers = media/scripts/gmenu_layers.php\r\n\r\nstyles.gmenu_layer.green >\r\nstyles.gmenu_layer.green = HMENU\r\nstyles.gmenu_layer.green.stdWrap.offsetWrap = {$styles.gmenu_layer.green.menuOffset}\r\nstyles.gmenu_layer.green.wrap = <nobr> | </nobr>\r\nstyles.gmenu_layer.green.1 = GMENU_LAYERS\r\nstyles.gmenu_layer.green.1 {\r\n  target = _top\r\n  xPosOffset = -{$styles.gmenu_layer.green.layer.xPosOffset}\r\n  lockPosition = x\r\n  expAll = 1\r\n  topOffset = {$styles.gmenu_layer.green.layer.topOffset}\r\n  hideMenuWhenNotOver = {$styles.gmenu_layer.green.layer.mouseOutHide}\r\n}\r\nstyles.gmenu_layer.green.1.NO {\r\n  XY = [10.w]+[4.w]+[5.w]+[6.w]+[7.w] , {$styles.gmenu_layer.green.height}\r\n  backColor = {$styles.gmenu_layer.green.bgCol} \r\n  reduceColors = {$styles.gmenu_layer.green.reduceColors}\r\n\r\n  3 = IMAGE\r\n  3.file = {$styles.gmenu_layer.green.file.background}\r\n  3.tile = 10,1\r\n\r\n  4 = IMAGE |*|_x_\r\n  4.file = {$styles.gmenu_layer.green.file.first}\r\n\r\n  5 = _x_ |*| IMAGE \r\n  5.file = {$styles.gmenu_layer.green.file.middle}\r\n\r\n#  6 = IMAGE\r\n  6.file = {$styles.gmenu_layer.green.file.sublevel}\r\n  6.align = r,t\r\n  6.offset = -[7.w]\r\n\r\n  7 = _x_|*|_x_|*| IMAGE\r\n  7.file = {$styles.gmenu_layer.green.file.last}\r\n  7.align = r,t\r\n\r\n  10 = TEXT\r\n  10.text.field = nav_title // title\r\n  10.offset =  {$styles.gmenu_layer.green.firstOffset}||{$styles.gmenu_layer.green.offset}|*||*| \r\n  10.fontColor = {$styles.gmenu_layer.green.fontColor}\r\n  10.niceText = {$styles.gmenu_layer.green.niceText}\r\n  10.antiAlias = {$styles.gmenu_layer.green.antiAlias}\r\n  10.fontSize = {$styles.gmenu_layer.green.fontSize}\r\n  10.spacing = {$styles.gmenu_layer.green.spacing}\r\n  10.fontFile = {$styles.gmenu_layer.green.file.fontFile}\r\n}\r\n\r\nstyles.gmenu_layer.green.1.RO < styles.gmenu_layer.green.1.NO\r\nstyles.gmenu_layer.green.1.RO = {$styles.gmenu_layer.green.RO}\r\nstyles.gmenu_layer.green.1.RO {\r\n  backColor = {$styles.gmenu_layer.green.RO.bgCol}\r\n  10.fontColor = {$styles.gmenu_layer.green.RO.fontColor}\r\n}\r\n\r\nstyles.gmenu_layer.green.1.ACT < styles.gmenu_layer.green.1.NO\r\nstyles.gmenu_layer.green.1.ACT = {$styles.gmenu_layer.green.ACT}\r\nstyles.gmenu_layer.green.1.ACT {\r\n  backColor = {$styles.gmenu_layer.green.ACT.bgCol}\r\n  10.fontColor = {$styles.gmenu_layer.green.ACT.fontColor}\r\n}\r\n\r\nstyles.gmenu_layer.green.1 {\r\n  IFSUB < .NO\r\n  IFSUB = 1\r\n  IFSUB.6 = IMAGE\r\n\r\n  IFSUBRO < .RO\r\n  IFSUBRO.6 = IMAGE\r\n}\r\n\r\n\r\n\r\n# Submenu on layers:\r\n\r\nstyles.gmenu_layer.green.2 = TMENU\r\nstyles.gmenu_layer.green.2.wrap = {$styles.gmenu_layer.green.2.wrap}\r\nstyles.gmenu_layer.green.2.NO {\r\n  allWrap =  {$styles.gmenu_layer.green.2.allWrap}\r\n  linkWrap = {$styles.gmenu_layer.green.2.linkWrap}\r\n  ATagBeforeWrap = 1\r\n}\r\n', '', '');

INSERT INTO static_template VALUES ('77', '0', '1032385772', '999602864', 'cSet stylesheet', '', 'content.RTE_compliant.mode = stylesheets\r\n\r\ncontent.wrap {\r\n  header = \r\n  header1 = <h1>|</h1>\r\n  header2 = <h2>|</h2>\r\n  header3 = <h3>|</h3>\r\n  header4 = <h4>|</h4>\r\n  subheader1 = <div class=subhead1>|</div>\r\n  subheader2 = <div class=subhead2>|</div>\r\n  subheader3 = <div class=subhead3>|</div>\r\n  headerDate = <div class=headerdate>|</div>\r\n  bodytext =\r\n}\r\ncontent {\r\n  defaultHeaderType = 2\r\n  defaultSubHeaderType = 3\r\n  menuWrap = <div class=celmenu>|</div>\r\n  subMenuWrap = <div class=celsubmenu>|</div>\r\n  menuAllWrap =\r\n  break.bodytext = \r\n  break.bodytext_parseFunc = <br>\r\n}\r\n\r\nstyles.content {\r\n  imgtext.captionWrap = <div class=caption>|</div>\r\n  bulletlist.altWrap = <div class=bulletlist>|</div>\r\n  table.altWrap = <div class=table>|</div>\r\n  textStyle.altWrap = <p>|</p>\r\n  uploads.wrap = \r\n  uploads.sizeWrap = <div class=filelinkssize>|</div>\r\n  uploads.labelWrap = <div class=filelinks>|</div>\r\n  links.sword = <span class=searchmarkup>|</span>\r\n}\r\nstyles.sitemap.text.totalWrap = \r\n\r\ncontent.headerSpace = 0|0\r\ncontent.space = 0|0\r\n\r\n\r\n# cat=content/file; type=file[css]; label=Style sheet: The default stylesheet is media/scripts/defaultstylesheet.css. Make a copy of this file, edit the copy and upload it as resource here!\r\ncontent.stylesheet.file = media/scripts/defaultstylesheet.css\r\n\r\n\r\n# REMOVING categories for constants:\r\n\r\nstyles.content.textStyle {\r\n    # cat=\r\n  face = \r\n    # cat=\r\n  size = \r\n    # cat=\r\n  color = \r\n}\r\n', '# Setting Style sheet\r\npage.headerData.10 = FILE\r\npage.stylesheet = {$content.stylesheet.file}\r\n\r\n\r\n\r\n# REMOVING <br> wraps for the menu content element and header\r\ntt_content.text.20.parseFunc.tags.typohead.stdWrap.wrap = \r\n\r\ntt_content.menu = COA \r\ntt_content.menu { \r\n  20 { \r\n    3.renderObj { \r\n      wrap2 = \r\n    } \r\n    4.1.NO { \r\n      afterWrap = {$content.subMenuWrap} \r\n    } \r\n    7.1.wrap = \r\n    7.2.wrap = \r\n  } \r\n} \r\n\r\n# Setting stylesheet for Content Element sitemap\r\ntt_content.menu.20.2 {\r\n  1.NO.allWrap = \r\n  1.NO.linkWrap = <div class=sitemap1>|</div>\r\n  2.NO.allWrap = \r\n  2.NO.linkWrap = <div class=sitemap2>|</div>\r\n  3.NO.allWrap = \r\n  3.NO.linkWrap = <div class=sitemap3>|</div>\r\n  4.NO.allWrap = \r\n  4.NO.linkWrap = <div class=sitemap4>|</div>\r\n}\r\n\r\n\r\n# Overriding some Setup values for tt_content elements:\r\ntt_content {\r\n  menu.20.3.renderObj.wrap = \r\n  menu.20.3.renderObj.innerWrap = <div class=celmenu>|</div>\r\n\r\n  mailform.20.labelWrap.wrap = <span class=mailform>|</span>\r\n  mailform.20.commentWrap.wrap = <span class=mailformcomment>|</span>\r\n  mailform.20.COMMENT.layout = <tr><td colspan=3>###COMMENT###</td></tr>\r\n  mailform.20.radioWrap.wrap = <span class=mailformradio>|</span>\r\n  mailform.20.REQ.labelWrap.wrap = <span class=mailformrequired>|</span>\r\n\r\n  search.30.labelWrap.wrap = <div class=searchform>|</div>\r\n\r\n  login.20.labelWrap.wrap = <div class=loginform>|</div>\r\n  login.20.REQ.labelWrap.wrap = <div class=loginform>|</div>\r\n}\r\n\r\n\r\n# Search result:\r\ntt_content.search.20 {\r\n  renderObj.10.textStyle >\r\n  renderObj.10.fontTag >\r\n  renderObj.10.innerWrap = <div class=searchresultpagetitle>|</div>\r\n  renderObj.20.stdWrap.textStyle >\r\n  renderObj.20.stdWrap.innerWrap = <div class=searchresultcontent>|</div>\r\n  layout.10.fontTag = <div class=searchresultheader>|</div>\r\n  layout.20.fontTag = <div class=searchresultheader>|</div>\r\n  noResultObj.10.fontTag  = <div class=searchresultpagetitle>|</div>\r\n}\r\n\r\n\r\n\r\n[globalVar=LIT:0<{$content.RTE_compliant}]\r\n[else]\r\n# Configuration of general text rendering:\r\ntt_content.text.20.parseFunc.nonTypoTagStdWrap {\r\n  trim=1\r\n  required=1\r\n  textStyle < tt_content.text.20.textStyle\r\n}\r\ntt_content.text.20.textStyle >\r\ntt_content.text.20.parseFunc.tags.typolist.breakoutTypoTagContent = 1\r\ntt_content.text.20.parseFunc.tags.typohead.breakoutTypoTagContent = 1\r\n[global]\r\n', 'This static_template should be included as an alternative to the traditional "cSet (default)".\r\nMost wraps are substituted with either traditional HTML-tags or <div> tags with class-definitions refering to the default stylesheet in media/scripts/defaultstylesheet.css\r\nBodytext is wrapped in <p>-tags and double-line-breaks are substituted with </p><p> while ordinary linebreaks are substituted with a regular <br> tag.', '');

INSERT INTO static_template VALUES ('78', '0', '999808378', '999705238', 'plugin.alt.print (98)', '', 'plugin.alt.print >\r\nplugin.alt.print {\r\n    # cat=plugin.print/file; type=file[html,htm,tmpl,txt]; label= Template File for printversion: &type=98 defines a printer friendly design for page content. This template file lets you define the position of page titles and the content.\r\n  file.template = media/scripts/printversion_content.tmpl\r\n    # cat=plugin.print/file; type=file[css]; label= Printerfriendly CSS stylesheet\r\n  file.stylesheet = \r\n}\r\n', 'alt_print >\r\nalt_print = PAGE\r\nalt_print.typeNum=98\r\nalt_print.stylesheet = {$plugin.alt.print.file.stylesheet}\r\n\r\nalt_print.10 = TEMPLATE\r\nalt_print.10 {\r\n  template = FILE\r\n  template.file = {$plugin.alt.print.file.template}\r\n  marks {\r\n    PAGE_TITLE = TEXT\r\n    PAGE_TITLE.field = title\r\n    PAGE_SUBTITLE = TEXT\r\n    PAGE_SUBTITLE.field = subtitle\r\n    PAGE_AUTHOR = TEXT\r\n    PAGE_AUTHOR.field = author\r\n    PAGE_AUTHOR.required=1\r\n    PAGE_AUTHOR.typolink.parameter.field = author_email\r\n    PAGE_UID = TEXT\r\n    PAGE_UID.field = uid\r\n    CONTENT < styles.content.get\r\n  }\r\n  workOnSubpart = DOCUMENT_BODY\r\n}\r\n', 'Creates a PAGE object, alt_printversion, with typeNum = 98. This page displays the \'normal\' page content along with the page title and page author in a printer friendly display for printing.\r\n\r\nJust include this static template directly in your template, and you\'re up\'n\'running.\r\n\r\nDepends on \'content (default)\' or similar to be already included.', '');

INSERT INTO static_template VALUES ('79', '0', '1024790859', '999707349', 'plugin.tt_news [DEPRECIATED]', '', 'plugin.tt_news {\r\n    # cat=plugin.news/file; type=file[html,htm,tmpl,txt]; label= Template File: HTML-template file for display of news. See media/scripts/news_template.tmpl for an example\r\n  file.templateFile = media/scripts/news_template.tmpl\r\n\r\n    # cat=plugin.news/typo; type=wrap; label= Wrap 1: This wrap is used in the template-file.\r\n  wrap1 = <font face="verdana" size=2 color=black> | </font>\r\n    # cat=plugin.news/typo; type=wrap; label= Wrap 2: This wrap is used in the template-file.\r\n  wrap2 = <font face="verdana" size=1 color=black> | </font>\r\n\r\n    # cat=plugin.news/color; type=color; label= Color 1: This bgcolor is used in the template-file.\r\n  color1 = #cccccc\r\n    # cat=plugin.news/color; type=color; label= Color 2: This bgcolor is used in the template-file.\r\n  color2 = #999999\r\n    # cat=plugin.news/color; type=color; label= Color 3: This bgcolor is used in the template-file.\r\n  color3 = #333333\r\n\r\n    # cat=plugin.news//; type=int+; label= "Single" pid: The PID of the page on which single news items should be displayed\r\n  PIDitemDisplay = 0\r\n    # cat=plugin.news//; type=int+; label= "Back" pid: The PID of the page to go back to from single news item display screen.\r\n  backPid = 0\r\n}\r\n', '## Includes the newsLib:\r\nincludeLibs.ts_news = media/scripts/newsLib.inc\r\n\r\n## This enables the tt_content.list item to display the news:\r\nplugin.tt_news >\r\nplugin.tt_news = USER\r\nplugin.tt_news {\r\n  userFunc = user_news->main_news\r\n\r\n  templateFile = {$plugin.tt_news.file.templateFile}\r\n  pid_list.field = pages\r\n  recursive.field = recursive\r\n  code.field = select_key\r\n\r\n  # Global wraps\r\n  wrap1.wrap = {$plugin.tt_news.wrap1}\r\n  wrap2.wrap = {$plugin.tt_news.wrap2}\r\n  color1.wrap = {$plugin.tt_news.color1}\r\n  color2.wrap = {$plugin.tt_news.color2}\r\n  color3.wrap = {$plugin.tt_news.color3}\r\n\r\n  # Rendering of news items\r\n  general_stdWrap {\r\n    brTag = <br>\r\n    parseFunc < styles.content.parseFunc\r\n  }\r\n\r\n  limit = 10\r\n  latestLimit = 5\r\n  backPid = {$plugin.tt_news.backPid}\r\n  PIDsearch = \r\n  PIDitemDisplay = {$plugin.tt_news.PIDitemDisplay}\r\n  datetimeDaysToArchive = 0\r\n  enableArchiveDate = 1\r\n  pageTypoLink.parameter.current = 1\r\n  archiveTypoLink.parameter = {$plugin.tt_news.backPid}\r\n\r\n  archiveMenuNoEmpty = 1\r\n  archiveTitleCObject = COA\r\n  archiveTitleCObject {\r\n    10 = TEXT\r\n    10.field = start\r\n    10.strftime = %B - %Y\r\n  }\r\n\r\n  getRelatedCObject = COA\r\n  getRelatedCObject {\r\n    10 = TEXT\r\n    10.field = title\r\n    10.typolink.parameter = {$plugin.tt_news.PIDitemDisplay}\r\n    10.typolink.additionalParams.field = uid \r\n    10.typolink.additionalParams.wrap = &tt_news=|\r\n    20 = TEXT\r\n    20.field = datetime\r\n    20.strftime = %e-%m-%y %H:%M\r\n    20.wrap = <font size=1> - | </font>\r\n  }\r\n\r\n  # SINGLE news\r\n  displaySingle {\r\n    age_stdWrap.age = 1\r\n    date_stdWrap.strftime= %e-%m-%y\r\n    time_stdWrap.strftime= %H:%M\r\n    title_stdWrap.wrap = <b>|</b>\r\n    author_stdWrap.case = upper\r\n    author_stdWrap.typolink.parameter.field = author_email\r\n\r\n    imageCount=2\r\n    imageWrapIfAny = <table border=0 cellpadding=0 cellspacing=0 align=right width=1><tr><td><img src=clear.gif width=15 height=1></td><td>|</td></tr></table>\r\n\r\n    email_stdWrap.setContentToCurrent = 1\r\n    email_stdWrap.typolink.parameter.current = 1\r\n    caption_stdWrap.wrap = <font color=navy face=verdana size=1>|</font><br><br>\r\n    caption_stdWrap.trim=1\r\n    caption_stdWrap.required=1\r\n  }\r\n  displaySingle.image {\r\n    file.maxW = 200\r\n    wrap = | <br>\r\n    imageLinkWrap = 1\r\n    imageLinkWrap {\r\n      enable = 1\r\n      bodyTag = <body bgColor=black>\r\n      wrap = <a href="javascript:close();"> | </a>\r\n      width = 400m\r\n      height = 400\r\n\r\n      JSwindow = 1\r\n      JSwindow.newWindow = 1\r\n      JSwindow.expand = 17,20\r\n    }\r\n    stdWrap.spaceAfter=10\r\n  }\r\n\r\n  # LATEST news\r\n  displayLatest {\r\n    age_stdWrap.age = 1\r\n    date_stdWrap.strftime= %e-%m-%y\r\n    time_stdWrap.strftime= %H:%M\r\n    caption_stdWrap.if.directReturn = 0\r\n    subheader_stdWrap.crop = 100 | ...\r\n    subheader_stdWrap.ifEmpty.field = bodytext\r\n    imageCount=1\r\n    imageWrapIfAny = <table border=0 cellpadding=0 cellspacing=0 align=left><tr><td>|</td><td><img src=clear.gif width=5 height=1></td></tr></table>\r\n  }\r\n  displayLatest.image < displaySingle.image\r\n  displayLatest.image {\r\n    file.maxW = 50\r\n    imageLinkWrap = 0\r\n    stdWrap.spaceAfter=5\r\n  }\r\n\r\n  # LIST news\r\n  displayList {\r\n    age_stdWrap.age = 1\r\n    date_stdWrap.strftime= %A %e. of %B\r\n    time_stdWrap.strftime= %e-%m-%y %H:%M\r\n    caption_stdWrap.wrap = <font face=verdana size=1 color=navy>|</font><br>\r\n    caption_stdWrap.trim=1\r\n    caption_stdWrap.required=1\r\n    subheader_stdWrap.crop = 300 | ...\r\n    subheader_stdWrap.ifEmpty.field = bodytext\r\n    imageCount=1\r\n    imageWrapIfAny = \r\n  }\r\n  displayList.image < displaySingle.image\r\n  displayList.image {\r\n    file.maxW = 150\r\n    imageLinkWrap = 1\r\n    stdWrap.spaceAfter=5\r\n  }\r\n}\r\n\r\n\r\n\r\n## This enables the tt_content.record item to display news items:\r\ntt_news >\r\ntt_news = < plugin.tt_news\r\ntt_news.displayCurrentRecord = 1\r\n', 'PLUGIN: News module\r\nAUTHOR: Kasper Skrhj, kasper@typo3.com\r\n\r\n', '');

INSERT INTO static_template VALUES ('80', '0', '1024790859', '999707887', 'plugin.tt_board_tree [DEPRECIATED]', '81', 'plugin.tt_board_tree{\r\n    # cat=plugin.board/file; type=file[html,htm,tmpl,txt]; label= Template File, tree style: This is a simple textfile where the board is defined with HTML-code. To see an example with explanations, take a look at media/scripts/board_template.tmpl which is the default.\r\n  file.templateFile = media/scripts/board_template2.tmpl\r\n\r\n    # cat=plugin.board/typo; type=wrap; label= Tree-style, Wrap 1: This wrap is used in the template-file.\r\n  wrap1 = <font face="verdana" size=2 color=black> | </font>\r\n    # cat=plugin.board/typo; type=wrap; label= Tree-style, Wrap 2: This wrap is used in the template-file.\r\n  wrap2 = <font face="verdana" size=1 color=black> | </font>\r\n    # cat=plugin.board/typo; type=wrap; label= Tree-style, Wrap 3: This wrap is used in the template-file.\r\n  wrap3 = <font face="verdana" size=2 color=white> | </font>\r\n\r\n    # cat=plugin.board/color; type=color; label= Tree-style, Color 1: This bgcolor is used in the template-file.\r\n  color1 = #cccccc\r\n    # cat=plugin.board/color; type=color; label= Tree-style, Color 2: This bgcolor is used in the template-file.\r\n  color2 = #999999\r\n    # cat=plugin.board/color; type=color; label= Tree-style, Color 3: This bgcolor is used in the template-file.\r\n  color3 = #333333\r\n    # cat=plugin.board/color; type=color; label= Tree-style, Color 4: This bgcolor is used in the template-file.\r\n  color4 = #333366\r\n}\r\n', '\r\n## This enables the tt_content.list item to display the forum, tree style:\r\nplugin.tt_board_tree < temp.tt_board.shared\r\nplugin.tt_board_tree {\r\n  templateFile = {$plugin.tt_board_tree.file.templateFile}\r\n\r\n  # Global wraps\r\n  wrap1.wrap = {$plugin.tt_board_tree.wrap1}\r\n  wrap2.wrap = {$plugin.tt_board_tree.wrap2}\r\n  wrap3.wrap = {$plugin.tt_board_tree.wrap3}\r\n  color1.wrap = {$plugin.tt_board_tree.color1}\r\n  color2.wrap = {$plugin.tt_board_tree.color2}\r\n  color3.wrap = {$plugin.tt_board_tree.color3}\r\n  color4.wrap = {$plugin.tt_board_tree.color4}\r\n\r\n    # Allows for elements to be viewed one at a time and not parent+childs\r\n  view_thread.single = 1\r\n    # Allows for the forum to draw as a tree\r\n  tree=1\r\n\r\n  list_forums.author_stdWrap.wrap = by |\r\n  list_threads.post_content_stdWrap.crop = 200 | ...\r\n  list_forums.post_content_stdWrap.crop = 200 | ...\r\n\r\n  iconCode=1\r\n  iconCode.joinBottom = <img src="media/scripts/joinbottom.gif" width=18 height=16 align="top">\r\n  iconCode.join = <img src="media/scripts/join.gif" width=18 height=16 align="top">\r\n  iconCode.line = <img src="media/scripts/line.gif" width=18 height=16 align="top">\r\n  iconCode.blank = <img src="media/scripts/blank.gif" width=18 height=16 align="top">\r\n  iconCode.thread = <img src="media/scripts/thread.gif" width=18 height=16 align="top">\r\n  iconCode.end = <img src="media/scripts/end.gif" width=18 height=16 align="top">\r\n\r\n  list_threads.post_title_stdWrap.wrap = <font face="verdana,arial" size=1 color=black> | </font>\r\n  list_threads.post_title_stdWrap.crop = 20 | ...\r\n  list_threads.post_author_stdWrap.wrap = <font face="verdana,arial" size=1 color=black> | </font>\r\n  list_threads.post_author_stdWrap.crop = 20|...\r\n  list_threads.thread_limit = 30\r\n\r\n  thread_tree < .list_threads\r\n}\r\n\r\n## This enables the record item to display guestbook items\r\ntt_board >\r\ntt_board = < plugin.tt_board_tree\r\ntt_board.displayCurrentRecord = 1\r\n\r\n', 'PLUGIN: Board/Forum Tree style\r\nAUTHOR: Kasper Skrhj, kasper@typo3.com\r\n\r\n', '');

INSERT INTO static_template VALUES ('81', '0', '1024790859', '999708085', 'temp.tt_board (shared) [DEPRECIATED]', '', 'plugin.tt_board {\r\n    # cat=plugin.board; type=string; label= Moderator email: This email address will get a mail every time somebody submits a message to the forum.\r\n  moderatorEmail  = \r\n\r\n  numberOfRecentPosts = 0\r\n}', '## Includes the boardLib:\r\nincludeLibs.ts_board = media/scripts/boardLib.inc\r\n\r\n## Sets up the fields, that may be submitted in order to create items. Mandatory!\r\nFEData.tt_board >\r\nFEData.tt_board {\r\n  processScript = media/scripts/board_submit.inc\r\n  processScript {\r\n\r\n      # Enable notify\r\n    notify = 1\r\n      # "From" email header\r\n    notify_from = TYPO3 forum <no_reply>\r\n      # Notification message template, first line is subject.\r\n    newReply.msg = media/scripts/board_notify.txt\r\n    newReply.didWhat = has just replied to a thread at\r\n    newReply.subjectPrefix = Reply to post at\r\n    newThread.msg = media/scripts/board_notify.txt\r\n    newThread.didWhat = has just created a new thread at\r\n    newThread.subjectPrefix = New thread at\r\n\r\n    debug=0\r\n  }\r\n  default {\r\n    hidden = 0\r\n    parent = 0\r\n  }\r\n  allowNew {\r\n    subject = 1\r\n    message = 1\r\n    author=1\r\n    email=1\r\n    parent=1\r\n    notify_me=1\r\n  }\r\n  autoInsertPID = 1\r\n  doublePostCheck = doublePostCheck\r\n}\r\n\r\n\r\n\r\n\r\n\r\ntemp.tt_board.shared = USER\r\ntemp.tt_board.shared {\r\n  userFunc = user_board->main_board\r\n\r\n  pid_list.field = pages\r\n  code.field = select_key\r\n\r\n  date_stdWrap.strftime= %e-%m-%y\r\n  time_stdWrap.strftime= %H:%M:%S\r\n  age_stdWrap.age = 1\r\n  allowCaching = 1\r\n  \r\n  list_categories {\r\n    cache_timeout = 120\r\n  }\r\n  list_forums {\r\n    author_stdWrap.wrap = by |\r\n    cache_timeout = 300\r\n    post_content_stdWrap.crop = 130 | ...\r\n    numberOfRecentPosts = {$plugin.tt_board.numberOfRecentPosts}\r\n  }\r\n  list_threads {\r\n    post_content_stdWrap.crop = 130 | ...\r\n    thread_limit = 20\r\n  }\r\n\r\n  postform < styles.content.mailform\r\n  postform.data >\r\n  postform.dataArray {\r\n    10.label = Subject:\r\n    10.type = *data[tt_board][NEW][subject]=input,60\r\n    20.label = Message:\r\n    20.type =  *data[tt_board][NEW][message]=textarea,60\r\n    30.label = Name:\r\n    30.type = *data[tt_board][NEW][author]=input,40\r\n    40.label = Email:\r\n    40.type = *data[tt_board][NEW][email]=input,40\r\n    50.label = Notify me<br>by reply: \r\n    50.type = data[tt_board][NEW][notify_me]=check\r\n    60.type = formtype_db=submit\r\n    60.value = Post Reply\r\n  }\r\n  postform.no_cache=1\r\n\r\n  postform_newThread < .postform\r\n  postform_newThread.dataArray.60.value = Post New Thread\r\n\r\n  moderatorEmail_newThread = {$plugin.tt_board.moderatorEmail}\r\n  moderatorEmail = {$plugin.tt_board.moderatorEmail}\r\n}\r\n', 'This static template is shared by the plugin.tt_board_tree and plugin.tt_board_list static templates.', '');

INSERT INTO static_template VALUES ('84', '0', '999789390', '999781309', 'plugin.alt.xmlnewsfeed (89)', '', 'plugin.alt.xmlnewsfeed >\r\nplugin.alt.xmlnewsfeed {\r\n    # cat=plugin.tt_news/file; \r\n\r\n}\r\n', '\r\n## Set up page/type number:\r\nalt_xmlnewsfeed >\r\nalt_xmlnewsfeed = PAGE\r\nalt_xmlnewsfeed {\r\n  typeNum=89\r\n  config.disableAllHeaderCode = 1\r\n  config.additionalHeaders = Content-type: text/xml\r\n\r\n  ## Includes the newsLib:\r\n  includeLibs.ts_news = media/scripts/newsLib.inc\r\n\r\n  ## This enables the tt_content.list item to display the news:\r\n  10 = USER\r\n  10 {\r\n    userFunc = user_news->main_xmlnewsfeed\r\n  }\r\n}\r\n', 'Creates a PAGE object, alt_xmlnewsfeed, with typeNum = 89. \r\nThis delivers the tt_news elements as an XML-feed depending on parameters passed to the script.\r\n\r\nJust include this static template directly in your template, and you\'re up\'n\'running.\r\n\r\nDepends on \'content (default)\' or similar to be already included.', '');

INSERT INTO static_template VALUES ('85', '0', '999829329', '999782684', 'plugin.alt.wap (97)', '', 'plugin.alt.wap >\r\nplugin.alt.wap {\r\n\r\n}\r\n', '## Set up page/type:\r\nalt_wap >\r\nalt_wap = PAGE\r\nalt_wap {\r\n  typeNum=97\r\n  config.disableAllHeaderCode = 1\r\n  config.additionalHeaders = Content-type: text/vnd.wap.wml\r\n\r\n  ## Includes the newsLib:\r\n  includeLibs.alt_wap = media/scripts/wapversionLib.inc\r\n\r\n  ## Inserting the USER cObject for WAP/XML rendering\r\n  10 = USER\r\n  10 {\r\n    userFunc = user_wapversion->main_wapversion\r\n    debug=0\r\n    preTitle = T3WAP\r\n    navLabels.prev = Prev\r\n    navLabels.next = Next\r\n    navLabels.up = Up\r\n  }\r\n}\r\n\r\n## Setting up a testpage at type 197\r\nalt_wap_test < alt_wap\r\nalt_wap_test {\r\n  typeNum=197\r\n  config.additionalHeaders = \r\n  10.debug = 1\r\n}\r\n\r\n## If the browser is a WAP-device,\r\n[device=wap]\r\n\r\nalt_wap.typeNum=0\r\n\r\n[global]\r\n', 'Creates a PAGE object, alt_wap, with typeNum = 97. This page displays the page in WAP format.\r\n\r\nStill experimental.\r\n\r\nJust include this static template directly in your template, and you\'re up\'n\'running.', '');

INSERT INTO static_template VALUES ('88', '0', '1024790859', '1001579560', 'plugin.tt_rating [DEPRECIATED]', '', 'plugin.tt_rating >\r\nplugin.tt_rating {\r\n\r\n    # cat=plugin.rating/typo; type=; label= Vote form data.\r\n  formdata = | *data[tt_rating][EDIT][rating]=select |0 - No rating=0, 5 - Excellent=5, 4 - Good=4, 3 - Fair=3, 2 - Needs improvement=2, 1 - Unsatisfactory=1 ||  | formtype_db = submit | Rate this\r\n\r\n# cat=plugin.rating/typo; type=; label= Text to show if not rated yet.\r\n  notRatedMsg = This object is not rated yet.\r\n    # cat=plugin.rating/typo; type=; label= Text to show if just rated.\r\n  submittedRatingMsg = Your submitted rating was ###SUBMITTED_RATING###.\r\n    # cat=plugin.rating/typo; type=; label= Text to show if user try to rate twice.\r\n  doubleRatingMsg = You have already rated this item.\r\n    # cat=plugin.rating/typo; type=; label= Text to show below your rating bar.\r\n  textOutput = &nbsp;&nbsp;<b>###RATING###</b> (###VOTES### votes)\r\n\r\n    # cat=plugin.rating/typo; type=int; label= The highest possible rating. (examples: 5 [best] - 1 [worst], 1 [best] - 6 [worst])\r\n  highestRating = 5\r\n    # cat=plugin.rating/typo; type=int; label= The lowest possible rating.\r\n  lowestRating = 1\r\n    # cat=plugin.rating/typo; type=int; label= For statistics logging. This should match the steps in the form.\r\n  ratingStatSteps = 5\r\n    # cat=plugin.rating/typo; type=int; label= The length of a bar or similiar.\r\n  ratingOutputWidth = 110\r\n\r\n    # cat=plugin.rating/typo; type=; label= Image used to render the \'good\' bar.\r\n  goodImgFile = media/scripts/star.gif\r\n    # cat=plugin.rating/typo; type=; label= Image used to render the \'bad\' bar.\r\n  badImgFile = media/scripts/star_grey.gif\r\n    # cat=plugin.rating/typo; type=; label= Image used to mask both images against the background color.\r\n  maskImgFile = media/scripts/star_mask.gif\r\n    # cat=plugin.rating/typo; type=int[1-20]; label= Should the image be tiled (example: 5 stars). If not set it to 1.\r\n  tileImg = 5\r\n}', '## Includes the ratingLib:\r\nincludeLibs.ts_rating = media/scripts/ratingLib.inc\r\n\r\n## Sets up the fields, that may be submitted in order to create items. Mandatory!\r\nFEData.tt_rating {\r\n  processScript = media/scripts/rating_submit.inc\r\n  allowEdit {\r\n    rating = 1\r\n\r\n    // following items are used to pass configuration to fe_tce.php, They are NOT fields\r\n    // this may change later\r\n    minRating = 1\r\n    maxRating = 1\r\n    ratingStatSteps = 1\r\n  }\r\n}\r\n\r\n\r\n## This enables the tt_content.list item to display rating:\r\nplugin.tt_rating >\r\nplugin.tt_rating = USER\r\nplugin.tt_rating {\r\n  userFunc = user_rating->main_rating\r\n\r\n  pid_list.field = pages\r\n  code.field = select_key\r\n\r\n  allowNew = 1\r\n  defaultCode = RESULT,VOTEFORM\r\n    //used if you allowNew\r\n  defaultTitle = rating\r\n  defaultDescription =\r\n    // the best and the worst rating value\r\n  highestRating = {$plugin.tt_rating.highestRating}\r\n  lowestRating = {$plugin.tt_rating.lowestRating}\r\n    // normally the same as in your voteform\r\n  ratingStatSteps = {$plugin.tt_rating.ratingStatSteps}\r\n    // the length of a bar or similiar\r\n  ratingOutputWidth = {$plugin.tt_rating.ratingOutputWidth}\r\n\r\n  submittedRatingMsg = {$plugin.tt_rating.submittedRatingMsg}\r\n  doubleRatingMsg = {$plugin.tt_rating.doubleRatingMsg}\r\n  wrap =\r\n\r\n  voteform < styles.content.mailform\r\n  voteform.layout = <td>###FIELD###</td><td><img src="clear.gif" width={$styles.content.mailform.border}></td>\r\n  voteform.stdWrap.wrap = <table border=0 cellspacing=0 cellpadding=0><tr>  | </tr></table>\r\n  voteform.data >\r\n  voteform.data = {$plugin.tt_rating.formdata}\r\n  voteform.no_cache=1\r\n  voteform.noValueInsert=1\r\n  voteform.wrap =\r\n\r\n  noRatingObj = TEXT\r\n  noRatingObj.value = {$plugin.tt_rating.notRatedMsg}\r\n  noRatingObj.textStyle < styles.content.textStyle\r\n\r\n\r\n  renderObj = COA\r\n  renderObj.stdWrap.wrap =  | <br>\r\n  renderObj {\r\n\r\n    10 = IMAGE\r\n    10.file = GIFBUILDER\r\n    10.file {\r\n      backColor = {$cSet.pageColor}\r\n      transparentBackground = 1\r\n      XY = [10.w]*{$plugin.tt_rating.tileImg},[10.h]\r\n      10 = IMAGE\r\n      10 {\r\n        file = {$plugin.tt_rating.badImgFile}\r\n        mask = {$plugin.tt_rating.maskImgFile}\r\n        tile = {$plugin.tt_rating.tileImg},1\r\n      }\r\n      15 = WORKAREA\r\n      15.set = 0,0,###RATING_MUL###*[10.w]*{$plugin.tt_rating.tileImg},[10.h]\r\n      20 = IMAGE\r\n      20 {\r\n        file = {$plugin.tt_rating.goodImgFile}\r\n        mask = {$plugin.tt_rating.maskImgFile}\r\n        tile = {$plugin.tt_rating.tileImg},1\r\n      }\r\n    }\r\n    20 = TEXT\r\n    20.value = {$plugin.tt_rating.textOutput}\r\n    20.textStyle < styles.content.textStyle\r\n\r\n    30 = TEXT\r\n    30.value = ###RATING_MSG###\r\n    30.required = 1\r\n    30.wrap =\r\n    30.textStyle < styles.content.textStyle\r\n\r\n  }\r\n}\r\n\r\n\r\n\r\n## This enables the tt_content.record item to display rating items:\r\ntt_rating >\r\ntt_rating = < plugin.tt_rating\r\ntt_rating.displayCurrentRecord = 1\r\n', 'Author: Ren Fritz <r.fritz@colorcube.de>', '');

INSERT INTO static_template VALUES ('89', '0', '1003486832', '1001674349', 'template; GLCK', '43,68', 'page.file {\r\n    # cat=basic/file; type=file[IMAGE_EXT]; label=Background image:  Background image of the page.\r\n  bgImg = media/uploads/back1.png\r\n    # cat=basic/file; type=file[IMAGE_EXT]; label=Logo Image:  Logo image in the top of the page.\r\n  logoImg = media/uploads/testlogo.png\r\n    # cat=basic/file; type=file[IMAGE_EXT]; label=Top image:  Image on top of the normal and right column. This image is the default image. The first media file found in the rootline will substitute this!\r\n  topImg = media/uploads/topimage.png\r\n    # cat=basic/file; type=file[IMAGE_EXT]; label=Margin Logo Image:  Logo image in the top of the margin column.\r\n  marginLogoImg = media/uploads/testlogo2.png\r\n\r\n    # cat=basic/file; type=file[IMAGE_EXT]; label=Menu column background image:  Background image of the menu column.\r\n  menuBgImg = media/uploads/back2.png\r\n    # cat=basic/file; type=file[IMAGE_EXT]; label=Normal column background image:  Background image of the normal column.\r\n  normalBgImg = media/uploads/back3.png\r\n    # cat=basic/file; type=file[IMAGE_EXT]; label=Right column background image:  Background image of the right column.\r\n  rightBgImg = media/uploads/back4.png\r\n    # cat=basic/file; type=file[IMAGE_EXT]; label=Whole table background image:  Background image of the whole main content table.\r\n  wholeTableBgImg = media/uploads/back5.png\r\n}\r\npage.bgColors {\r\n    #cat=basic/color; type=color; label=Menu column bgColor: This is the background color of the table cell where the menu and logo is displayed.\r\n  menuBgColor = \r\n    #cat=basic/color; type=color; label=Content column bgColor: This is the background color of the table cell where the content is displayed. You would normally regard this as being the "page color".\r\n  normalBgColor = \r\n    #cat=basic/color; type=color; label=Right column bgColor: This is the background color of the table cell where the right column content is displayed.\r\n  rightBgColor = \r\n    #cat=basic/color; type=color; label=Whole table column bgColor: This is the background color of the whole table.\r\n  wholeTableBgColor = \r\n}\r\n\r\n\r\n// This excludes the constant "styles.content.imgtext.maxW" from being displayed in the constant editor. The reason why this is desirable is that the REGISTER is used to override this value in this template.\r\n  # cat=\r\nstyles.content.imgtext.maxW =\r\n\r\npageCol {\r\n    # cat=content,basic/cImage/a_maxw1; type=int+; label=Max Image Width: This indicates that maximum number of pixels (width) a block of images inserted as content is allowed to consume.\r\n  maxImageWidth = 343\r\n    # cat=basic/dims; type=int+; label=Content width: This value defines the width in pixels of the table cell where the content is displayed\r\n  page.width = 343\r\n    # cat=basic/dims; type=int+; label=Page table height: This is the minimum page height.\r\n  page.height = 400\r\n    # cat=basic/dims; type=offset[Left, Top, Right,,Width]; label=Content offset: This value defines the margin in pixels from the left and top of the main content table cell\r\n  page.contentOffset = 10,8,10\r\n}\r\n\r\n  # cat=basic/dims; type=offset[Left, Top, Right, Bottom]; label=Page offset: This value defines the margin in pixels from the left and top of the browser window\r\npage.offset = ,20\r\n  # cat=basic; type=options[left,right,center]; label=Alignment of the page: Alignment of the page-table\r\npage.align = center\r\n\r\n  #cat=basic/dims; type=int+; label=Content margin, left: Set the number of pixels whitespace between the content column and the menu column.\r\npage.contentMarginLeft = 10\r\n  #cat=basic/dims; type=int+; label=Content margin, right: Set the number of pixels whitespace between the content column and the right column.\r\npage.contentMarginRight = 10\r\n  #cat=basic/dims; type=int+; label=Distance Top image to Content: Sets the distance in pixels between the top image and the content columns, normal and right.\r\npage.topImgToContentDist = 10\r\n\r\n\r\n\r\nrightCol {\r\n    #cat=right,basic/enable; type=boolean; label=Enable right column: Click this option to enable the right column in the page table. This column can be used for "news" from a certain page, but inserted on all pages. You can also configure the column to hold "secondary" content specific for each page. Then content of the column-type "Border" is inserted. NOTE: This option is also found in the dedicated category "RIGHT" that configures the characteristics of this column.\r\n  enable = 1\r\n    #cat=right/enable; type=options[getNews,getBorder]; label=Column content: Choose "getNews" to select content from a specific page (defined below here) or "getBorder" to display the "Border"-content of the current page.\r\n  borderContent = getBorder\r\n    # cat=right/enable/zz; type=int+; label=News Content PID: If you selected "getNews" above, this is where you enter the page-id of the page from which to fetch the "news" content for the column.\r\n  getNewsPid = \r\n    #cat=right/dims; type=int+; label=Max Image Width: This defines that maximum number of pixels (width) a block of images inserted as content is allowed to consume. NOTE: This is specific for the right column! The figure should approx. match the width of the image in the top of this column!\r\n  maxImageWidth = 140\r\n    #cat=right/dims; type=offset[Left, Top, Right,,Width]; label=Content offset: This is the offset of the content from the upper-left corner of the table cell.\r\n  offset = 5,8,5\r\n    #cat=right/typo; type=small; label=Bodytext font face\r\n  textStyle_face =\r\n    #cat=right/typo; type=int[1-5]; label=Bodytext font size\r\n  textStyle_size =\r\n    #cat=right/typo; type=color; label=Bodytext font color\r\n  textStyle_color =\r\n    #cat=right/typo; type=int[1-5]; label=Default Header type: Enter the number of the header layout to be used by default. \r\n  defaultHeaderType =\r\n}\r\n\r\n\r\nmenuCol {\r\n    #cat=menucolumn,basic/enable; type=boolean; label=Enable menu column: Click this option to enable the menu column in the page table. This column can be used for "news" from a certain page, but inserted on all pages. You can also configure the column to hold "secondary" content specific for each page. Then content of the column-type "Left" is inserted. \r\n  enable = 1\r\n    #cat=menucolumn/enable; type=options[getNews,getLeft]; label=Column content: Choose "getNews" to select content from a specific page (defined below here) or "getLeft" to display the "Left"-content of the current page.\r\n  borderContent = getNews\r\n    #cat=menucolumn/dims; type=int+; label=Max Image Width: This indicates that maximum number of pixels (width) a block of images inserted as content is allowed to consume. NOTE: This is specific for the menu column! The figure should approx. match the width of the image in the top of this column!\r\n  maxImageWidth = 140\r\n    #cat=menucolumn/dims; type=offset[Left, Top, Right,,Width]; label=Content offset: This is the offset of the content from the upper-left corner of the table cell.\r\n  offset = 5,8,5\r\n    #cat=menucolumn/typo; type=small; label=Bodytext font face\r\n  textStyle_face =\r\n    #cat=menucolumn/typo; type=int[1-5]; label=Bodytext font size\r\n  textStyle_size =\r\n    #cat=menucolumn/typo; type=color; label=Bodytext font color\r\n  textStyle_color =\r\n    #cat=menucolumn/typo; type=int[1-5]; label=Default Header type: Enter the number of the header layout to be used by default. \r\n  defaultHeaderType =\r\n}\r\n  # cat=menucolumn/enable/zz; type=int+; label=News Content PID: If you selected "getNews" above, this is where you enter the page-id of the page from which to fetch the "news" content for the column.\r\nstyles.content.getNews.newsPid = 0\r\n\r\n\r\n\r\nmenu1 {\r\n    # cat=menu/file; type=file[IMAGE_EXT]; label=Left Bullet image\r\n  file.bullet_left = media/bull