=file[IMAGE_EXT]; label=Active, Right Bullet image\r\n  ACT.file.bullet_right = media/bullets/bullet2_h.gif\r\n\r\n    #cat=menu2/enable; type=options[underline,none]; label=Normal Underline: If set, the menu links will be underlined.\r\n  textDecor = none\r\n    #cat=menu2/enable; type=options[underline,none]; label=Roll Over Underline: If set, the menu links will be underlined on roll over.\r\n  hoverTextDecor = underline\r\n}\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\nstyles.content.imgtext.maxW = 400\r\n\r\n\r\nPAGE_TARGET = \r\ncontent.pageFrameObj = \r\n\r\n  # cat=basic/color/a; type=color; label=Background color: This is the <body>-tag background color of the page.\r\nbgCol = #D6D3CE\r\n  # cat=basic/color/l; type=color; label=Link-color: Link color defined in <body>-tag\r\nlink = #006699\r\n  # cat=basic/color/l; type=color; label=Active Link-color: Active link color defined in <body>-tag\r\nalink = #00659C\r\n  # cat=basic/color/l; type=color; label=Visited Link-color: Visited link color defined in <body>-tag\r\nvlink = #00659C\r\n\r\n\r\n\r\n\r\n\r\n\r\n## TSConstantsEditor Config\r\nTSConstantEditor.basic {\r\n  header = Standard Template "GLCK"\r\n  description = Standard template based on three columns in a table. There are first and second level textbased menus, one menu in each column. You can insert logos and content in each column and add background images to the various table cells. The template is fully programmed in TypoScript.\r\n  bulletlist =  Three columns of content each with separate background images or colors (1+14+2) // Menu column (left) and right column are optional (1+2)// 1+2 level text based menu, 1. level in menu column (left)(1) and 2. level in the right column (2) // Optional logos in the top of columns (11+13) // Optional top image (12) // Detailed adjustment of margins, widths and alignment (4+5+6+9+3+16) // Background image and color of page (10)\r\n  image = gfx/GLCK_basic.gif\r\n  1=rightCol.enable,page.file.menuBgImg,page.bgColors.menuBgColor\r\n  2=menuCol.enable,page.file.rightBgImg,page.bgColors.rightBgColor\r\n  3=page.offset\r\n  4=page.contentMarginLeft\r\n  5=page.contentMarginRight\r\n  6=page.topImgToContentDist\r\n  7=pageCol.page.width,pageCol.maxImageWidth\r\n  8=pageCol.page.height\r\n  9=pageCol.page.contentOffset\r\n  10=page.file.bgImg,bgCol\r\n  11=page.file.logoImg\r\n  12=page.file.topImg\r\n  13=page.file.marginLogoImg\r\n  14=page.file.normalBgImg,page.bgColors.normalBgColor\r\n  15=page.bgColors.wholeTableBgColor,page.file.wholeTableBgImg\r\n  16=page.align\r\n}\r\nTSConstantEditor.menu {\r\n  header = MENU, "GLCK"\r\n  description = Text based menu with stylesheet hover color and definition of active state\r\n  image = gfx/GLCK_menu.gif\r\n  1=menu1.bottomLine,menu1.lineColor\r\n  2=menu1.topLine,menu1.lineColor\r\n  3=menu1.ACT,menu1.ACT.itemStyle,menu1.ACT.itemLinkColor,menu1.ACT.itemLinkColorRO,menu1.ACT.bgColor,menu1.align\r\n  4=menu1.textDecor,menu1.NO.itemStyle,menu1.bgColor,menu1.NO.itemLinkColor,menu1.align\r\n  5=menu1.hoverTextDecor,menu1.NO.itemLinkColorRO\r\n  6=menu1.itemHeight\r\n  7=menu1.lineThickness,menu1.lineColor\r\n  8=menu1.offset\r\n  9=menu1.file.bullet_left,menu1.file.bullet_left_RO,menu1.ACT.file.bullet_left\r\n  10=menu1.file.bullet_right,menu1.file.bullet_right_RO,menu1.ACT.file.bullet_right\r\n}\r\nTSConstantEditor.menu2 {\r\n  header = MENU2, "GLCK"\r\n  description = Text based menu with stylesheet hover color and definition of active state\r\n  image = gfx/GLCK_menu.gif\r\n  1=menu2.bottomLine,menu2.lineColor\r\n  2=menu2.topLine,menu2.lineColor\r\n  3=menu2.ACT,menu2.ACT.itemStyle,menu2.ACT.itemLinkColor,menu2.ACT.itemLinkColorRO,menu2.ACT.bgColor,menu2.align\r\n  4=menu2.textDecor,menu2.NO.itemStyle,menu2.bgColor,menu2.NO.itemLinkColor,menu2.align\r\n  5=menu2.hoverTextDecor,menu2.NO.itemLinkColorRO\r\n  6=menu2.itemHeight\r\n  7=menu2.lineThickness,menu2.lineColor\r\n  8=menu2.offset\r\n  9=menu2.file.bullet_left,menu2.file.bullet_left_R