INSERT INTO static_template VALUES ('90', '0', '1024790859', '1002209598', 'plugin.tipafriend [DEPRECIATED]', '', 'plugin.tipafriend {\r\n    # cat=plugin.tipafriend/file; type=file[html,htm,tmpl,txt]; label= Template File: HTML-template file for display of the Tip-a-Friend form\r\n  file.templateFile = media/scripts/tipafriend_template.tmpl\r\n\r\n    # cat=plugin.tipafriend/typo; type=wrap; label= Wrap 1: This wrap is used in the template-file.\r\n  wrap1 = <font face="verdana" size=2 color=black> | </font>\r\n    # cat=plugin.tipafriend/typo; type=wrap; label= Wrap 2: This wrap is used in the template-file.\r\n  wrap2 = <font face="verdana" size=1 color=black> | </font>\r\n\r\n    # cat=plugin.tipafriend/color; type=color; label= Color 1: This bgcolor is used in the template-file.\r\n  color1 = #cccccc\r\n    # cat=plugin.tipafriend/color; type=color; label= Color 2: This bgcolor is used in the template-file.\r\n  color2 = #999999\r\n    # cat=plugin.tipafriend/color; type=color; label= Color 3: This bgcolor is used in the template-file.\r\n  color3 = #333333\r\n\r\n    # cat=plugin.tipafriend//; type=int+; label= PID for the Tip-form page.\r\n  PIDtipForm = 0\r\n    # cat=plugin.tipafriend/enable; type=boolean; label= Send page as HTML: If set, the mail is sent as an HTML mail.\r\n  htmlMail = 0\r\n}', '## Includes the newsLib:\r\nincludeLibs.ts_tipafriend= media/scripts/tipafriendLib.inc\r\n\r\n## This enables the tt_content.list item to display the news:\r\nplugin.tipafriend >\r\nplugin.tipafriend = USER\r\nplugin.tipafriend {\r\n  userFunc = user_tipafriend->main_tipafriend\r\n  code.field = select_key\r\n  templateFile = {$plugin.tipafriend.file.templateFile}\r\n\r\n  # Global wraps\r\n  wrap1.wrap = {$plugin.tipafriend.wrap1}\r\n  wrap2.wrap = {$plugin.tipafriend.wrap2}\r\n  color1.wrap = {$plugin.tipafriend.color1}\r\n  color2.wrap = {$plugin.tipafriend.color2}\r\n  color3.wrap = {$plugin.tipafriend.color3}\r\n\r\n  typolink.parameter = {$plugin.tipafriend.PIDtipForm}\r\n  htmlmail = {$plugin.tipafriend.htmlMail}\r\n\r\n}', 'PLUGIN: Tip-a-Friend module\r\nAUTHOR: Kasper Skrhj, kasper@typo3.com\r\n\r\n', '');

INSERT INTO static_template VALUES ('91', '0', '1024790859', '1002543181', 'plugin.feadmin.dmailsubscription [DEPRECIATED]', '', 'plugin.feadmin.dmailsubscription {\r\n    # cat=plugin.feadmin.dmailsubscription/file; type=file[html,htm,tmpl,txt]; label= Template File: HTML-template file\r\n  file.templateFile = media/scripts/fe_admin_dmailsubscrip.tmpl\r\n\r\n    # cat=plugin.feadmin.dmailsubscription//; type=string; label= Administration email: Enter the administration email address here. This email address will be the sender email and also recieve administration notes.\r\n  email = \r\n    # cat=plugin.feadmin.dmailsubscription//; type=string; label= Administration name: Enter the administration name here. If set, this will be used as the email address name in the mails sent.\r\n  emailName = \r\n\r\n    # cat=plugin.feadmin.dmailsubscription//; type=int+; label= Record PID: If the records edited/created is located in another page than the current, enter the PID of that page here.\r\n  pid = \r\n\r\n\r\n    # cat=plugin.feadmin.dmailsubscription/typo; type=wrap; label= Wrap 1: This wrap is used in the template-file.\r\n  wrap1 = <font face="verdana" size=2 color=black> | </font>\r\n    # cat=plugin.feadmin.dmailsubscription/typo; type=wrap; label= Wrap 2: This wrap is used in the template-file.\r\n  wrap2 = <font face="verdana" size=1 color=black> | </font>\r\n\r\n    # cat=plugin.feadmin.dmailsubscription/color; type=color; label= Color 1: This bgcolor is used in the template-file.\r\n  color1 = #cccccc\r\n    # cat=plugin.feadmin.dmailsubscription/color; type=color; label= Color 2: This bgcolor is used in the template-file.\r\n  color2 = #999999\r\n    # cat=plugin.feadmin.dmailsubscription/color; type=color; label= Color 3: This bgcolor is used in the template-file.\r\n  color3 = #333333\r\n\r\n}', 'plugin.feadmin.dmailsubscription >\r\nplugin.feadmin.dmailsubscription = USER_INT\r\nplugin.feadmin.dmailsubscription {\r\n  userFunc = user_feAdmin->init\r\n  includeLibs = media/scripts/fe_adminLib.inc\r\n  templateFile ={$plugin.feadmin.dmailsubscription.file.templateFile}\r\n\r\n  # Global wraps\r\n  wrap1.wrap = {$plugin.feadmin.dmailsubscription.wrap1}\r\n  wrap2.wrap = {$plugin.feadmin.dmailsubscription.wrap2}\r\n  color1.wrap = {$plugin.feadmin.dmailsubscription.color1}\r\n  color2.wrap = {$plugin.feadmin.dmailsubscription.color2}\r\n  color3.wrap = {$plugin.feadmin.dmailsubscription.color3}\r\n\r\n  // Tables + Commands\r\n  table = tt_address\r\n\r\n    // Info-mail setup\r\n  setfixed=1\r\n  setfixed.approve {\r\n    _FIELDLIST = uid\r\n    hidden = 0\r\n  }\r\n  setfixed.DELETE = 1\r\n  setfixed.DELETE._FIELDLIST = uid\r\n\r\n  delete = 1\r\n  defaultCmd=create\r\n\r\n  // Info-mail setup\r\n  infomail = 1\r\n  infomail.default {\r\n    label = FORGOTPASSWORD\r\n  }\r\n\r\n  // Edit setup\r\n  edit = 1\r\n  edit.fields = name, email, module_sys_dmail_category, module_sys_dmail_html\r\n  edit.required = name, email\r\n  edit.evalValues {\r\n    email = uniqueLocal, email\r\n  }\r\n\r\n  // Create setup\r\n  create = 1\r\n  create.preview = 0\r\n  create.fields = name, hidden, email, module_sys_dmail_category, module_sys_dmail_html\r\n  create.required = name, email\r\n  create.noSpecialLoginForm = 1\r\n    # Initially hide the user until he approves!\r\n  create.overrideValues.hidden = 1\r\n  create.evalValues {\r\n    email = uniqueLocal, email\r\n  }\r\n\r\n  authcodeFields = uid\r\n  authcodeFields.addKey = \r\n  authcodeFields.addDate = \r\n\r\n  evalErrors.email.uniqueLocal = Apparently you\'re already registered with this email address!\r\n  evalErrors.email.email = This is not a proper email address!\r\n\r\n  parseValues.module_sys_dmail_category = checkArray,setEmptyIfAbsent \r\n  parseValues.module_sys_dmail_html = setEmptyIfAbsent \r\n  pid = {$plugin.feadmin.dmailsubscription.pid}\r\n\r\n  email.from = {$plugin.feadmin.dmailsubscription.email}\r\n  email.fromName = {$plugin.feadmin.dmailsubscription.emailName}\r\n  email.admin = {$plugin.feadmin.dmailsubscription.email}\r\n  email.field = email\r\n}', 'PLUGIN: Direct-Mail subscription example\r\nAUTHOR: Kasper Skrhj, kasper@typo3.com\r\n\r\n', '');

INSERT INTO static_template VALUES ('92', '0', '1024790859', '1002543825', 'plugin.feadmin.fe_users [DEPRECIATED]', '', 'plugin.feadmin.fe_users {\r\n    # cat=plugin.feadmin.fe_users/file; type=file[html,htm,tmpl,txt]; label= Template File: HTML-template file \r\n  file.templateFile = media/scripts/fe_admin_fe_users.tmpl\r\n\r\n    # cat=plugin.feadmin.fe_users//; type=string; label= Administration email: Enter the administration email address here. This email address will be the sender email and also recieve administration notes.\r\n  email = \r\n    # cat=plugin.feadmin.fe_users//; type=string; label= Administration name: Enter the administration name here. If set, this will be used as the email address name in the mails sent.\r\n  emailName = \r\n\r\n    # cat=plugin.feadmin.fe_users//; type=int+; label= Record PID: If the records edited/created is located in another page than the current, enter the PID of that page here.\r\n  pid = \r\n    # cat=plugin.feadmin.fe_users//; type=int+; label= Usergroup override: Enter the value of the user group field. Must be a list of integers WITHOUT spaces between the comma separated values! Furthermore these integer MUST point to actual fe_groups records!\r\n  usergroupOverride = \r\n\r\n\r\n    # cat=plugin.feadmin.fe_users/typo; type=wrap; label= Wrap 1: This wrap is used in the template-file.\r\n  wrap1 = <font face="verdana" size=2 color=black> | </font>\r\n    # cat=plugin.feadmin.fe_users/typo; type=wrap; label= Wrap 2: This wrap is used in the template-file.\r\n  wrap2 = <font face="verdana" size=1 color=black> | </font>\r\n\r\n    # cat=plugin.feadmin.fe_users/color; type=color; label= Color 1: This bgcolor is used in the template-file.\r\n  color1 = #cccccc\r\n    # cat=plugin.feadmin.fe_users/color; type=color; label= Color 2: This bgcolor is used in the template-file.\r\n  color2 = #999999\r\n    # cat=plugin.feadmin.fe_users/color; type=color; label= Color 3: This bgcolor is used in the template-file.\r\n  color3 = #333333\r\n\r\n}\r\n', 'plugin.feadmin.fe_users >\r\nplugin.feadmin.fe_users = USER_INT\r\nplugin.feadmin.fe_users {\r\n  userFunc = user_feAdmin->init\r\n  includeLibs = media/scripts/fe_adminLib.inc\r\n  templateFile ={$plugin.feadmin.fe_users.file.templateFile}\r\n\r\n  # Global wraps\r\n  wrap1.wrap = {$plugin.feadmin.fe_users.wrap1}\r\n  wrap2.wrap = {$plugin.feadmin.fe_users.wrap2}\r\n  color1.wrap = {$plugin.feadmin.fe_users.color1}\r\n  color2.wrap = {$plugin.feadmin.fe_users.color2}\r\n  color3.wrap = {$plugin.feadmin.fe_users.color3}\r\n\r\n  // Tables + Commands\r\n  table=fe_users\r\n  defaultCmd=create\r\n  debug=0\r\n\r\n  setfixed=0\r\n  delete=1\r\n\r\n    // Info-mail setup\r\n  infomail = 1\r\n  infomail.default {\r\n    label = FORGOTPASSWORD\r\n  }\r\n\r\n    // Creating\r\n  create = 1\r\n  create.preview = 1\r\n  create.fields = username,password,name,email,usergroup,address,telephone,fax,title,company,zip,city,country,www\r\n  create.required = username,password,name,email\r\n  create.overrideValues {\r\n    usergroup = {$plugin.feadmin.fe_users.usergroupOverride}\r\n  }\r\n  create.evalValues {\r\n    password = twice, atLeast[5]\r\n    username = uniqueLocal, atLeast[4]\r\n    email = email\r\n  }\r\n\r\n    // Editing:\r\n  edit < .create\r\n  edit.overrideValues {\r\n    username = \r\n  }\r\n  edit.evalValues {\r\n    username = unsetEmpty\r\n  }\r\n\r\n\r\n    // Field parsing and evaluation error messages:\r\n  parseValues {\r\n    username = lower,nospace,trim\r\n    email < .username\r\n    password = trim\r\n  }\r\n  evalErrors {\r\n    password.twice = You must enter the password twice.\r\n    password.atLeast = There must be at least %s characters in your password!\r\n    email.email = The email address is not validly formed. Must contain @ and a domain\r\n    username.uniqueLocal = The username exists already. Please select another one.\r\n    username.atLeast = There must be at least %s characters in your username!\r\n  }\r\n  pid = {$plugin.feadmin.fe_users.pid}\r\n  fe_userOwnSelf = 1\r\n  fe_userEditSelf = 1\r\n\r\n  cObjects.LOGIN = < tt_content.login.20\r\n\r\n  email.from = {$plugin.feadmin.fe_users.email}\r\n  email.fromName = {$plugin.feadmin.fe_users.emailName}\r\n  email.admin = {$plugin.feadmin.fe_users.email}\r\n  email.field = email\r\n}\r\n', 'PLUGIN: FrontEnd user creation / administration\r\nAUTHOR: Kasper Skrhj, kasper@typo3.com\r\n\r\n', '');

INSERT INTO static_template VALUES ('93', '0', '1024790859', '1004049254', 'language.de (german) [DEPRECIATED]', '', '', '## Setting the TypoScript language to german:\r\nconfig.language = de\r\n\r\n\r\n# ***********************************\r\n#  Login form as used in \'content (default)\'\r\n# ***********************************\r\ntt_content.login.20.dataArray {\r\n    10.label.lang.de = Benutzername:\r\n    20.label.lang.de = Password:\r\n    40.value.lang.de = Login\r\n}\r\n\r\n[usergroup = *]\r\ntt_content.login.20.dataArray {\r\n    10.label.lang.de = Benutzername: <!--###USERNAME###-->\r\n    20 >\r\n    40.value.lang.de = Logout\r\n}\r\n[global]\r\n\r\n\r\n\r\n\r\n# ***********************\r\n#  Search dialog object\r\n# ***********************\r\n\r\ntt_content.search.30.dataArray {\r\n    10.label.lang.de = Suchen:\r\n    20.label.lang.de = in\r\n    20.valueArray {\r\n      10.label.lang.de = berschriften und Stichworten\r\n      20.label.lang.de = Seiteninhalt\r\n    }\r\n    40.value.lang.de = Suche starten\r\n}\r\n\r\n\r\n\r\n# ***********************\r\n#  Search result page object\r\n# ***********************\r\ntt_content.search.20 {\r\n  layout.10.lang.de = Suchergebnis: ###RANGELOW###-###RANGEHIGH### von \r\n###TOTAL###\r\n  noResultObj.10.lang.de = Die Suche brachte kein Ergebnis!\r\n  next.lang.de = Nchste\r\n  prev.lang.de = Vorherige\r\n}\r\n\r\n\r\n\r\n# ***********************\r\n#  Default Guestbook form\r\n# ***********************\r\nplugin.tt_guest.postform.dataArray {\r\n  10.label.lang.de = berschrift:\r\n  20.label.lang.de = Dein Kommentar:\r\n  30.label.lang.de = Name:\r\n  40.label.lang.de = Email:\r\n  50.label.lang.de = Homepage:\r\n  60.value.lang.de = Eintragen\r\n}\r\n\r\n\r\n# ***********************\r\n#  Default List Board form\r\n# ***********************\r\nplugin.tt_board_list.postform.dataArray {\r\n  10.label.lang.de = Thema:\r\n  20.label.lang.de = Mitteilung:\r\n  30.label.lang.de = Name:\r\n  40.label.lang.de = Email:\r\n  50.label.lang.de = Benachrichtigung<br>bei Antwort(en):\r\n  60.value.lang.de = Antwort absenden\r\n}\r\nplugin.tt_board_list.postform_newThread.dataArray {\r\n  10.label.lang.de = Thema:\r\n  20.label.lang.de = Mitteilung:\r\n  30.label.lang.de = Name:\r\n  40.label.lang.de = Email:\r\n  50.label.lang.de = Benachrichtigung<br>bei Antwort(en):\r\n  60.value.lang.de = Diskussion erffnen\r\n}\r\n\r\n# ***********************\r\n#  Default Tree Board form\r\n# ***********************\r\nplugin.tt_board_tree.postform.dataArray {\r\n  10.label.lang.de = Thema:\r\n  20.label.lang.de = Mitteilung:\r\n  30.label.lang.de = Name:\r\n  40.label.lang.de = Email:\r\n  50.label.lang.de = Benachrichtigung<br>bei Antwort(en):\r\n  60.value.lang.de = Antwort absenden\r\n}\r\nplugin.tt_board_tree.postform_newThread.dataArray {\r\n  10.label.lang.de = Thema:\r\n  20.label.lang.de = Mitteilung:\r\n  30.label.lang.de = Name:\r\n  40.label.lang.de = Email:\r\n  50.label.lang.de = Benachrichtigung<br>bei Antwort(en):\r\n  60.value.lang.de = Diskussion erffnen\r\n}\r\n', 'This implements german labels on these standard elements \r\n\r\nFrom \'content (default)\':\r\n\r\n- login form \r\n- search form\r\n- search result\r\n\r\nFrom \'tt_guest\':\r\n\r\n- submit form.\r\n\r\n\r\nFrom \'tt_board\':\r\n\r\n- submit forms.\r\n', '');

INSERT INTO static_template VALUES ('94', '0', '1024790859', '1004097988', 'plugin.tt_poll [DEPRECIATED]', '', 'plugin.tt_poll >\r\nplugin.tt_poll {\r\n\r\n    # cat=plugin.poll/typo; type=int; label= The length of a bar or similiar that indicates 100%.\r\n  pollOutputWidth = 250\r\n    # cat=plugin.poll/typo; type=; label= Image used to render the bar begin.\r\n  beginBarImgFile = media/scripts/pollbar-begin.gif\r\n    # cat=plugin.poll/typo; type=; label= Image used to render the bar.\r\n  mainBarImgFile = media/scripts/pollbar-main.gif\r\n    # cat=plugin.poll/typo; type=; label= Image used to render the bar end.\r\n  endBarImgFile = media/scripts/pollbar-end.gif\r\n\r\n}', '## Includes the pollLib:\r\nincludeLibs.ts_poll = media/scripts/pollLib.inc\r\n\r\n## Sets up the fields, that may be submitted in order to create items. Mandatory!\r\nFEData.tt_poll {\r\n  processScript = media/scripts/poll_submit.inc\r\n  allowEdit {\r\n    // following item is used to pass the vote to fe_tce.php, It\'s NOT a fields\r\n    vote = 1\r\n  }\r\n}\r\n\r\n\r\nplugin.tt_poll >\r\nplugin.tt_poll = USER\r\nplugin.tt_poll {\r\n  userFunc = user_poll->main_poll\r\n\r\n  code = VOTEFORM\r\n\r\n  pollOutputWidth = {$plugin.tt_poll.pollOutputWidth}\r\n\r\n  date_stdWrap.strftime= %e-%m-%y\r\n\r\n  voteform < styles.content.mailform\r\n  voteform.layout = <tr><td>###FIELD###</td></tr>\r\n  voteform.stdWrap.wrap = <table border=0 cellspacing=0 cellpadding=2 bgcolor=#000000><tr><td><table width=100% border=0 cellspacing=0 cellpadding=3 bgcolor=#ffffff> | </table></tr></td></table>\r\n  voteform.stdWrap.innerWrap.cObject = COA\r\n  voteform.stdWrap.innerWrap.cObject {\r\n    10 = TEXT\r\n    10.value = Survey\r\n    10.wrap = <tr><td align=center bgcolor=#CCCCCC>|</td></tr>\r\n    10.textStyle < styles.content.textStyle\r\n    10.textStyle.altWrap = <span class=mailform>|</span>\r\n\r\n    20 = TEXT\r\n    20.value = <b>###QUESTION###</b>\r\n    20.wrap = <tr><td>|</td></tr>\r\n    20.textStyle < styles.content.textStyle\r\n    20.textStyle.altWrap = <span class=mailform>|</span>\r\n  }\r\n  voteform.data >\r\n    // put only the submit button here!!!\r\n  voteform.dataArray {\r\n    10.type = formtype_db = submit\r\n    10.value = Vote\r\n  }\r\n\r\n  voteform.radioWrap.textStyle < styles.content.textStyle\r\n  voteform.radioWrap.textStyle.altWrap = <span class=mailform>|</span>\r\n  voteform.no_cache=1\r\n  voteform.noValueInsert=1\r\n  voteform.wrap =\r\n\r\n  resultObj = COA\r\n  resultObj.wrap = <table border=0 cellspacing=0 cellpadding=2 bgcolor=#000000><tr><td><table cellspacing=0 cellpadding=3 border=0 bgcolor=#ffffff>|</table></tr></td></table>\r\n  resultObj {\r\n    10 = TEXT\r\n    10.value = Survey Result\r\n    10.wrap = <tr><td colspan=2 align=center bgcolor=#CCCCCC>|</td></tr>\r\n    10.textStyle < styles.content.textStyle\r\n    10.textStyle.altWrap = <span class=mailform>|</span>\r\n\r\n    20 = TEXT\r\n    20.value = <b>###QUESTION###</b>\r\n    20.wrap = <tr><td colspan=2 align=center><br>|<br><br></td></tr>\r\n    20.textStyle < styles.content.textStyle\r\n    20.textStyle.altWrap = <span class=mailform>|</span>\r\n\r\n    30 = TEXT\r\n    30.value = ###RESULTITEMS###\r\n\r\n    40 = TEXT\r\n    40.value = ###TOTALVOTES### votes total\r\n    40.wrap = <tr><td colspan=2 align=center><br>|<br></td></tr>\r\n    40.textStyle < styles.content.textStyle\r\n    40.textStyle.altWrap = <span class=mailform>|</span>\r\n\r\n    50 = HTML\r\n    50.value = ###PROGRESSMSG######VOTEMSG###\r\n  }\r\n\r\n  resultItemObj = COA\r\n  resultItemObj.wrap =  <tr>|</tr>\r\n  resultItemObj {\r\n\r\n    10 = TEXT\r\n    10.value = ###ANSWER###\r\n    10.wrap = <td nowrap>&nbsp;&nbsp;|</td>\r\n    10.textStyle < styles.content.textStyle\r\n    10.textStyle.altWrap = <span class=mailform>|</span>\r\n\r\n    20 = HTML\r\n    20.value = <td nowrap><img src="{$plugin.tt_poll.beginBarImgFile}" height=14 width=7><img src="{$plugin.tt_poll.mainBarImgFile}" height=14 width=###POLLWIDTH### alt="###PERCENT### %"><img src="{$plugin.tt_poll.endBarImgFile}" height=14 width=7>\r\n\r\n    30 = TEXT\r\n    30.value = &nbsp;###PERCENT### % (###ITEMVOTES### votes)\r\n    30.wrap = |&nbsp;&nbsp;</td>\r\n    30.textStyle < styles.content.textStyle\r\n    30.textStyle.altWrap = <span class=mailform>|</span>\r\n  }\r\n\r\n  inProgressObj = TEXT\r\n  inProgressObj {\r\n    wrap = <tr><td colspan=2 align=center><br>|<br></td></tr>\r\n    value = The Survey will end at ###ENDTIME###.\r\n    textStyle < styles.content.textStyle\r\n    textStyle.altWrap = <span class=mailform>|</span>\r\n  }\r\n\r\n  finishedObj = TEXT\r\n  finishedObj {\r\n    wrap = <tr><td colspan=2 align=center><br>|<br></td></tr>\r\n    value = The Survey was finished at ###ENDTIME###.\r\n    textStyle < styles.content.textStyle\r\n    textStyle.altWrap = <span class=mailform>|</span>\r\n  }\r\n\r\n  submittedVoteObj = TEXT\r\n  submittedVoteObj {\r\n    wrap = <tr><td colspan=2 align=center><br>|<br></td></tr>\r\n    value = Your voting was: <b>"###SUBMITTEDVOTE###"</b>.\r\n    textStyle < styles.content.textStyle\r\n    textStyle.altWrap = <span class=mailform>|</span>\r\n  }\r\n}\r\n\r\n', 'Author: Ren Fritz <r.fritz@colorcube.de>', '');

INSERT INTO static_template VALUES ('95', '0', '1024790859', '1010109604', 'language.nl (dutch) [DEPRECIATED]', '', '# ***********************\r\n#  Newsletter subscription\r\n# ***********************\r\nplugin.feadmin.dmailsubscription.file.templateFile = media/scripts/fe_admin_dmailsubscrip_dk.tmpl', '## Setting the TypoScript language to dutch:\r\nconfig.language = nl\r\n\r\n\r\n# ***********************************\r\n#  Login form as used in \'content (default)\'\r\n# ***********************************\r\ntt_content.login.20.dataArray {\r\n    10.label.lang.nl = Gebruikersnaam:\r\n    20.label.lang.nl = Wachtwoord:\r\n    40.value.lang.nl = Log In\r\n}\r\n\r\n[usergroup = *]\r\ntt_content.login.20.dataArray {\r\n    10.label.lang.nl = Gebruikersnaam: <!--###USERNAME###-->\r\n    20 >\r\n    40.value.lang.nl = Log Uit\r\n}\r\n[global]\r\n\r\n\r\n\r\n\r\n# ***********************\r\n#  Search dialog object\r\n# ***********************\r\n\r\ntt_content.search.30.dataArray {\r\n    10.label.lang.nl = Zoeken:\r\n    20.label.lang.nl = in\r\n    20.valueArray {\r\n      10.label.lang.nl = Titels en kernwoorden\r\n      20.label.lang.nl = Site-inhoud\r\n    }\r\n    40.value.lang.nl = Zoeken\r\n}\r\n\r\n\r\n\r\n# ***********************\r\n#  Search result page object\r\n# ***********************\r\ntt_content.search.20 {\r\n  layout.10.lang.nl = Zoekresultaat: ###RANGELOW###-###RANGEHIGH### van ###TOTAL###\r\n  noResultObj.10.lang.nl = Helaas, Er zijn geen pagina\'s gevonden die voldoen aan de opgegeven criteria. Pas je zoekopdracht aan en probeer het nog eens!\r\n  next.lang.nl = Volgende\r\n  prev.lang.nl = Volgende\r\n}\r\n\r\n\r\n\r\n# ***********************\r\n#  Default Guestbook form\r\n# ***********************\r\nplugin.tt_guest.postform.dataArray {\r\n  10.label.lang.nl = Onderwerp:\r\n  20.label.lang.nl = Jouw commentaar:\r\n  30.label.lang.nl = Naam:\r\n  40.label.lang.nl = Email:\r\n  50.label.lang.nl = Homepage:\r\n  60.value.lang.nl = Verzenden\r\n}\r\n\r\n\r\n# ***********************\r\n#  Default List Board form\r\n# ***********************\r\nplugin.tt_board_list.postform.dataArray {\r\n  10.label.lang.nl = Thema:\r\n  20.label.lang.nl = Mededeling:\r\n  30.label.lang.nl = Naam:\r\n  40.label.lang.nl = Email:\r\n  50.label.lang.nl = Kennisgeving<br>bij antwoord:\r\n  60.value.lang.nl = Antwoord verzenden\r\n}\r\nplugin.tt_board_list.postform_newThread.dataArray {\r\n  10.label.lang.nl = Thema:\r\n  20.label.lang.nl = Mededeling:\r\n  30.label.lang.nl = Naam:\r\n  40.label.lang.nl = Email:\r\n  50.label.lang.nl = Kennisgeving<br>bij antwoord:\r\n  60.value.lang.nl = Discussie openen\r\n}\r\n\r\n    // "You must fill in these fields:"\r\nplugin.tt_board_list.postform.badMess = Je moet de volgende velden invullen:\r\nplugin.tt_board_list.postform_newThread.badMess = Je moet de volgende velden invullen:\r\n\r\n# ***********************\r\n#  Default Tree Board form\r\n# ***********************\r\nplugin.tt_board_tree.postform.dataArray {\r\n  10.label.lang.nl = Thema:\r\n  20.label.lang.nl = Mededeling:\r\n  30.label.lang.nl = Naam:\r\n  40.label.lang.nl = Email:\r\n  50.label.lang.nl = Kennisgeving<br>bij antwoord:\r\n  60.value.lang.nl = Antwoord verzenden\r\n}\r\nplugin.tt_board_tree.postform_newThread.dataArray {\r\n  10.label.lang.nl = Thema:\r\n  20.label.lang.nl = Mededeling:\r\n  30.label.lang.nl = Naam:\r\n  40.label.lang.nl = Email:\r\n  50.label.lang.nl = Kennisgeving<br>bij antwoord:\r\n  60.value.lang.nl = Discussie openen\r\n}\r\n\r\n\r\nplugin.tt_board_tree.postform.badMess = Je moet de volgende velden invullen:\r\nplugin.tt_board_tree.postform_newThread.badMess = Je moet de volgende velden invullen:\r\n\r\n\r\n\r\n# ***********************\r\n#  Newsletter subscription\r\n# ***********************\r\nplugin.feadmin.dmailsubscription.evalErrors.email {\r\n     // "Apparently you\'re already registered with this email address!\r\n  uniqueLocal = Kennelijk ben je al geregistreerd met dit emailadres!\r\n     // This is not a correct email address!\r\n  email = Dit is geen geldig emailadres!\r\n}\r\n', 'This implements dutch labels on these standard elements \r\n(Translation by ben van \'t ende / netcreators.nl, <ben@netcreators.nl>\r\n\r\nFrom \'content (default)\':\r\n\r\n- login form \r\n- search form\r\n- search result\r\n\r\nFrom \'tt_guest\':\r\n\r\n- submit form.\r\n\r\n\r\nFrom \'tt_board\':\r\n\r\n- submit forms.\r\n\r\n\r\n', '');

INSERT INTO static_template VALUES ('96', '0', '1024790859', '1010702909', 'language.fr (french) [DEPRECIATED]', '', '# ***********************\r\n#  Newsletter subscription\r\n# ***********************\r\nplugin.feadmin.dmailsubscription.file.templateFile = media/scripts/fe_admin_dmailsubscrip_dk.tmpl', '## Setting the TypoScript language to french:\r\nconfig.language = fr\r\n \r\n \r\n# ***********************************\r\n#  Login form as used in \'content (default)\'\r\n# ***********************************\r\ntt_content.login.20.dataArray {\r\n    10.label.lang.de = Nom d\'utilisateur:\r\n    20.label.lang.de = Mot de passe:\r\n    40.value.lang.de = Login\r\n}\r\n \r\n[usergroup = *]\r\ntt_content.login.20.dataArray {\r\n    10.label.lang.de = Nom d\'utilisateur: <!--###USERNAME###-->\r\n    20 >\r\n    40.value.lang.de = Logout\r\n}\r\n[global]\r\n \r\n \r\n \r\n \r\n# ***********************\r\n#  Search dialog object\r\n# ***********************\r\n \r\ntt_content.search.30.dataArray {\r\n    10.label.lang.de = Chercher:\r\n    20.label.lang.de = dans\r\n    20.valueArray {\r\n      10.label.lang.de = Titre et Mots cls\r\n      20.label.lang.de = Contenu de la page\r\n    }\r\n    40.value.lang.de = Dmarrer la recherche\r\n}\r\n \r\n \r\n \r\n# ***********************\r\n#  Search result page object\r\n# ***********************\r\ntt_content.search.20 {\r\n  layout.10.lang.de = Echelle de recherche: ###RANGELOW###-###RANGEHIGH### de ###TOTAL###\r\n  noResultObj.10.lang.de = La recherche ne donne aucun rsultat!\r\n  next.lang.de = Suivant\r\n  prev.lang.de = Prcdent\r\n}\r\n \r\n \r\n \r\n# ***********************\r\n#  Default Guestbook form\r\n# ***********************\r\nplugin.tt_guest.postform.dataArray {\r\n  10.label.lang.de = Titre:\r\n  20.label.lang.de = Ton commentaire:\r\n  30.label.lang.de = Nom:\r\n  40.label.lang.de = E-mail:\r\n  50.label.lang.de = Site Web:\r\n  60.value.lang.de = S\'inscrire\r\n}\r\n \r\n \r\n# ***********************\r\n#  Default List Board form\r\n# ***********************\r\nplugin.tt_board_list.postform.dataArray {\r\n  10.label.lang.de = Thme:\r\n  20.label.lang.de = Message:\r\n  30.label.lang.de = Nom:\r\n  40.label.lang.de = E-mail:\r\n  50.label.lang.de = Notifier<br>en cas de rponse(en):\r\n  60.value.lang.de = Envoyer la rponse\r\n}\r\nplugin.tt_board_list.postform_newThread.dataArray {\r\n  10.label.lang.de = Thme:\r\n  20.label.lang.de = Message:\r\n  30.label.lang.de = Nom:\r\n  40.label.lang.de = E-mail:\r\n  50.label.lang.de = Notifier<br>en cas de rponse (en):\r\n  60.value.lang.de = Ouvrir la discution\r\n}\r\n\r\n    // "You must fill in these fields:"\r\nplugin.tt_board_list.postform.badMess = Vous devez remplir ces champs: \r\nplugin.tt_board_list.postform_newThread.badMess = Vous devez remplir ces champs: \r\n\r\n \r\n# ***********************\r\n#  Default Tree Board form\r\n# ***********************\r\nplugin.tt_board_tree.postform.dataArray {\r\n  10.label.lang.de = Thme:\r\n  20.label.lang.de = Message:\r\n  30.label.lang.de = Nom:\r\n  40.label.lang.de = E-mail:\r\n  50.label.lang.de = Notifier<br>en cas de rponse(en):\r\n  60.value.lang.de = Envoyer la rponse\r\n}\r\nplugin.tt_board_tree.postform_newThread.dataArray {\r\n  10.label.lang.de = Thme:\r\n  20.label.lang.de = Message:\r\n  30.label.lang.de = Nom:\r\n  40.label.lang.de = E-mail:\r\n  50.label.lang.de = Notifier<br>en cas de rponse (en):\r\n  60.value.lang.de = Ouvrir la discution\r\n}\r\n\r\n\r\nplugin.tt_board_tree.postform.badMess = Vous devez remplir ces champs: \r\nplugin.tt_board_tree.postform_newThread.badMess =Vous devez remplir ces champs: \r\n\r\n\r\n\r\n# ***********************\r\n#  Newsletter subscription\r\n# ***********************\r\nplugin.feadmin.dmailsubscription.\r\nplugin.feadmin.dmailsubscription.evalErrors.email {\r\n     // "Apparently you\'re already registered with this email address!\r\n  uniqueLocal = Apparemment, vous tes dj enregistr avec cette adresse!\r\n     // This is not a correct email address!\r\n  email = Ce n\'est pas un adresse e-mail valide!\r\n}\r\n\r\n', 'This implements french labels on these standard elements \r\n(Translation by Dominique Feyer <dfeyer@techhandflesh.ch>)\r\n\r\nFrom \'content (default)\':\r\n\r\n- login form \r\n- search form\r\n- search result\r\n\r\nFrom \'tt_guest\':\r\n\r\n- submit form.\r\n\r\n\r\nFrom \'tt_board\':\r\n\r\n- submit forms.\r\n\r\n\r\n', '');

INSERT INTO static_template VALUES ('97', '0', '1024790859', '1014636622', 'language.no (norwegian) [DEPRECIATED]', '', '# ***********************\r\n#  Newsletter subscription\r\n# ***********************\r\nplugin.feadmin.dmailsubscription.file.templateFile = media/scripts/fe_admin_dmailsubscrip_dk.tmpl', '## Setting the TypoScript language to danish:\r\nconfig.language = no\r\n\r\n\r\n# ***********************************\r\n#  Login form as used in \'content (default)\'\r\n# ***********************************\r\ntt_content.login.20.dataArray {\r\n    10.label.lang.no = Brukernavn:\r\n    20.label.lang.no = Passord:\r\n    40.value.lang.no = Logg inn\r\n}\r\n\r\n[usergroup = *]\r\ntt_content.login.20.dataArray {\r\n    10.label.lang.no = Brukernavn: <!--###USERNAME###--> \r\n    20 >\r\n    40.value.lang.no = Logg ut\r\n}\r\n[global]\r\n\r\n\r\n\r\n\r\n# ***********************\r\n#  Search dialog object\r\n# ***********************\r\n\r\ntt_content.search.30.dataArray {\r\n    10.label.lang.no = Skeord:\r\n    20.label.lang.no = Sk i:\r\n    20.valueArray {\r\n      10.label.lang.no = Overskrift og nkkelord\r\n      20.label.lang.no = Sideinnhold\r\n    }\r\n    40.value.lang.no = Start sk!\r\n}\r\n\r\n\r\n\r\n# ***********************\r\n#  Search result page object\r\n# ***********************\r\ntt_content.search.20 {\r\n  layout.10.lang.no = Skeresultat: ###RANGELOW###-###RANGEHIGH### ud af ###TOTAL###\r\n  noResultObj.10.lang.no = Beklager, sket ditt gav ingen treff!\r\n  next.lang.no = Neste\r\n  prev.lang.no = Forrige\r\n}\r\n\r\n\r\n\r\n# ***********************\r\n#  Default Guestbook form\r\n# ***********************\r\nplugin.tt_guest.postform.dataArray {\r\n  10.label.lang.no = Overskrift:\r\n  20.label.lang.no = Din kommentar:\r\n  30.label.lang.no = Navn:\r\n  40.label.lang.no = E-post:\r\n  50.label.lang.no = Hjemmeside:\r\n  60.value.lang.no = Legg inn meldingen\r\n}\r\n\r\n\r\n# ***********************\r\n#  Default List Board form\r\n# ***********************\r\nplugin.tt_board_list.postform.dataArray {\r\n  10.label.lang.no = Emne:\r\n  20.label.lang.no = Melding:\r\n  30.label.lang.no = Navn:\r\n  40.label.lang.no = E-post:\r\n  50.label.lang.no = Send e-post<br>ved svar: \r\n  60.value.lang.no = Send svar\r\n}\r\nplugin.tt_board_list.postform_newThread.dataArray {\r\n  10.label.lang.no = Emne:\r\n  20.label.lang.no = Melding:\r\n  30.label.lang.no = Navn:\r\n  40.label.lang.no = E-post:\r\n  50.label.lang.no = Send e-post<br>ved svar: \r\n  60.value.lang.no = Lag ny trd\r\n}\r\nplugin.tt_board_list.postform.badMess = Du m fylle ut flgende felt:\r\nplugin.tt_board_list.postform_newThread.badMess = Du m fylle ut flgende felt:\r\n\r\n# ***********************\r\n#  Default Tree Board form\r\n# ***********************\r\nplugin.tt_board_tree.postform.dataArray {\r\n  10.label.lang.no = Emne:\r\n  20.label.lang.no = Melding:\r\n  30.label.lang.no = Navn:\r\n  40.label.lang.no = E-post:\r\n  50.label.lang.no = Send e-post<br>ved svar: \r\n  60.value.lang.no = Send svar\r\n}\r\nplugin.tt_board_tree.postform_newThread.dataArray {\r\n  10.label.lang.no = Emne:\r\n  20.label.lang.no = Melding:\r\n  30.label.lang.no = Navn:\r\n  40.label.lang.no = E-post:\r\n  50.label.lang.no = Send e-post<br>ved svar: \r\n  60.value.lang.no = Lag ny trd\r\n}\r\nplugin.tt_board_tree.postform.badMess = Du m fylle ut flgende felt:\r\nplugin.tt_board_tree.postform_newThread.badMess = Du m fylle ut flgende felt:\r\n\r\n\r\n# ***********************\r\n#  Newsletter subscription\r\n# ***********************\r\nplugin.feadmin.dmailsubscription.evalErrors.email {\r\n  uniqueLocal = Du er tilsynelatende allerede registrert med denne e-post adressen!\r\n  email = Dette er ikke en korrekt e-post adresse!\r\n}\r\n', 'This implements norwegian labels on these standard elements \r\n\r\nFrom \'content (default)\':\r\n\r\n- login form \r\n- search form\r\n- search result\r\n\r\nFrom \'tt_guest\':\r\n\r\n- submit form.\r\n\r\n\r\nFrom \'tt_bo