INSERT INTO static_tsconfig_help VALUES ('121', '263', 'dc552e3c78a7a59de68ad59044c36468', '', 'page:TCEMAIN', 'a:1:{s:4:"rows";a:3:{i:0;a:6:{s:8:"property";s:25:"table.[tablename]\ndefault";s:8:"datatype";s:19:"-&gt;TCEMAIN_tables";s:11:"description";s:23:"Options for each table.";s:7:"default";s:0:"";s:12:"column_count";i:3;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:38:"permissions.userid\npermissions.groupid";s:8:"datatype";s:4:"int+";s:11:"description";s:223:"Sets the default owner be_user/be_group uid number of new and copied records.\nDefault of be_user-owner is the creating user himself\nDefault of the group is the main group of the user (the group in the top of his group list)";s:7:"default";s:0:"";s:12:"column_count";i:3;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:56:"permissions.user\npermissions.group\npermissions.everybody";s:8:"datatype";s:24:"list of string/int[0-31]";s:11:"description";s:481:"Default permissions set for owner-user, owner-group and everybody.\nKeylist: show,edit,delete,new,editcontent\nAlternatively you can specify an integer from 0 to 31 indicating which bits corresponding to the keylist should be set. (Bits in keylist: show=1,edit=2,delete=4,new=8,editcontent=16)\n&nbsp;\nDefaults:\n&quot;user&quot; =&gt; &quot;show,edit,delete,new,editcontent&quot;,\n&quot;group&quot; =&gt; &quot;show,edit,new,editcontent&quot;,\n&quot;everybody&quot; =&gt; &quot;&quot;";s:7:"default";s:0:"";s:12:"column_count";i:3;s:16:"is_propertyTable";i:1;}}}', '');

INSERT INTO static_tsconfig_help VALUES ('122', '263', '2b4f24e6146e08deb57fd71d9c3cd7df', '', 'page:TCEMAIN.default/TCEMAIN.table.(tablename)/-&gt;TCEMAIN_tables', 'a:1:{s:4:"rows";a:4:{i:0;a:6:{s:8:"property";s:19:"history.keepEntries";s:8:"datatype";s:4:"int+";s:11:"description";s:86:"Max entries in the sys_history for this table. Range 0-200. Zero turns the history of.";s:7:"default";s:0:"";s:12:"column_count";i:3;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:18:"history.maxAgeDays";s:8:"datatype";s:4:"int+";s:11:"description";s:152:"The number of days elements are in the history at most. Takes precedence over keepEntries.\nDefault is 7 days. Range 0-200. Zero turns the magAgeDays of.";s:7:"default";s:0:"";s:12:"column_count";i:3;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:20:"disablePrependAtCopy";s:8:"datatype";s:7:"boolean";s:11:"description";s:57:"Disables the &quot;prependAtCopy&quot; feature (from TCA)";s:7:"default";s:0:"";s:12:"column_count";i:3;s:16:"is_propertyTable";i:1;}i:3;a:6:{s:8:"property";s:17:"disableHideAtCopy";s:8:"datatype";s:7:"boolean";s:11:"description";s:54:"Disables the &quot;hideAtCopy&quot; feature (from TCA)";s:7:"default";s:0:"";s:12:"column_count";i:3;s:16:"is_propertyTable";i:1;}}}', '');

INSERT INTO static_tsconfig_help VALUES ('123', '263', '635aff2d727b50d7cdaaaf6a4144318e', '', 'page:TCEFORM', 'a:1:{s:4:"rows";a:1:{i:0;a:6:{s:8:"property";s:64:"[tablename].[field].[...]\n[tablename].[field].types.[type].[...]";s:8:"datatype";s:0:"";s:11:"description";s:489:"These objects contain additional configuration of the TCEFORM interface. &nbsp;For the properties available, refer to the table below. This is a description of how you can customize in general and override for specific types.\n\'TCEFORM.[<i>tablename</i>].[<i>field</i>].[...]\' &nbsp;configures the field in TCEFORM for all types.\n\'TCEFORM.[<i>tablename</i>].[<i>field</i>].types.[<i>type</i>].[...]\' configures the field in TCEFORM in case the \'type\'-value of the field matches <i>type</i>.";s:7:"default";s:0:"";s:12:"column_count";i:2;s:16:"is_propertyTable";i:1;}}}', '');

INSERT INTO static_tsconfig_help VALUES ('124', '263', 'aa7f13fe687c77a191a431405d3f9631', '', 'page:TCEFORM.(tablename).(field)/TCEFORM.(tablename).(field).types.(type)', 'a:1:{s:4:"rows";a:12:{i:0;a:6:{s:8:"property";s:8:"disabled";s:8:"datatype";s:7:"boolean";s:11:"description";s:34:"If set, the field is not rendered.";s:7:"default";s:0:"";s:12:"column_count";i:3;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:11:"removeItems";s:8:"datatype";s:14:"list of values";s:11:"description";s:137:"<i>(applies to select-types)</i>\n<i>&nbsp;</i>\nThis removes the items from the list which has a value found in this comma list of values.";s:7:"default";s:0:"";s:12:"column_count";i:3;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:20:"addItems.[itemValue]";s:8:"datatype";s:48:"string (label)\ndivided by | for system languages";s:11:"description";s:226:"<i>(applies to select-types)</i>\n<i>&nbsp;</i>\nThis will add elements to the list. Notice that the added elements might be removed if the selector represents records. In that case only still existing records will be preserved.";s:7:"default";s:0:"";s:12:"column_count";i:3;s:16:"is_propertyTable";i:1;}i:3;a:6:{s:8:"property";s:29:"disableNoMatchingValueElement";s:8:"datatype";s:7:"boolean";s:11:"description";s:128:"<i>(applies to select-types)</i>\n<i>&nbsp;</i>\nIf set, the element CURRENT VALUE IS NOT AVAILABLE will not be added to the list.";s:7:"default";s:0:"";s:12:"column_count";i:3;s:16:"is_propertyTable";i:1;}i:4;a:6:{s:8:"property";s:21:"noMatchingValue_label";s:8:"datatype";s:41:"string,\ndivided by | for system languages";s:11:"description";s:344:"<i>(applies to select-types)</i>\n<i>&nbsp;</i>\nAllows for an alternative label the &quot;noMatchingValue&quot; element. Notice that an empty value will be shown! If you want to unset this option, you must erase the TypoScript node by eg. &quot;.... noMatchingValue_label &gt; &quot;\nThe string is divided by | according to the system languages.";s:7:"default";s:0:"";s:12:"column_count";i:3;s:16:"is_propertyTable";i:1;}i:5;a:6:{s:8:"property";s:22:"altLabels.[item_value]";s:8:"datatype";s:41:"string,\ndivided by | for system languages";s:11:"description";s:236:"<i>(applies to select-types)</i>\n<i>&nbsp;</i>\nThis allows you to enter alternative labels for the items in the list. \nSee also &quot;.noMatchingValue_label&quot; for syntax.\nThe string is divided by | according to the system languages.";s:7:"default";s:0:"";s:12:"column_count";i:3;s:16:"is_propertyTable";i:1;}i:6;a:6:{s:8:"property";s:16:"PAGE_TSCONFIG_ID";s:8:"datatype";s:7:"integer";s:11:"description";s:283:"<i>(applies to select-types with foreign table)</i>\n<i>&nbsp;</i>\nWhen the select-types are used with foreign-table the where-query has four markers (see description of $TCA in the &quot;Inside Typo3&quot; document.) The value of three of these markers may be set from Page TSconfig.";s:7:"default";s:0:"";s:12:"column_count";i:3;s:16:"is_propertyTable";i:1;}i:7;a:6:{s:8:"property";s:20:"PAGE_TSCONFIG_IDLIST";s:8:"datatype";s:22:"comma list of integers";s:11:"description";s:75:"<i>(applies to select-types with foreign table)</i>\n<i>&nbsp;</i>\nSee above";s:7:"default";s:0:"";s:12:"column_count";i:3;s:16:"is_propertyTable";i:1;}i:8;a:6:{s:8:"property";s:17:"PAGE_TSCONFIG_STR";s:8:"datatype";s:6:"string";s:11:"description";s:75:"<i>(applies to select-types with foreign table)</i>\n<i>&nbsp;</i>\nSee above";s:7:"default";s:0:"";s:12:"column_count";i:3;s:16:"is_propertyTable";i:1;}i:9;a:6:{s:8:"property";s:19:"itemsProcFunc.[...]";s:8:"datatype";s:8:"(custom)";s:11:"description";s:182:"<i>(applies to select-types with itemsProcFunc)</i>\n<i>&nbsp;</i>\nThe properties of this key is passed on to the itemsProcFunc in the parameter array by the key &quot;TSconfig&quot;.";s:7:"default";s:0:"";s:12:"column_count";i:3;s:16:"is_propertyTable";i:1;}i:10;a:6:{s:8:"property";s:18:"RTEfullScreenWidth";s:8:"datatype";s:6:"int+/%";s:11:"description";s:263:"<i>(applies for RTE text fields only with the RTE wizard configured)</i>\n<i>&nbsp;</i>\nThe width of the RTE full screen display. If nothing is set, the whole width is used which means &quot;100%&quot;. If you set an integer value, that indicates the pixels width.";s:7:"default";s:0:"";s:12:"column_count";i:3;s:16:"is_propertyTable";i:1;}i:11;a:6:{s:8:"property";s:15:"linkTitleToSelf";s:8:"datatype";s:7:"boolean";s:11:"description";s:266:"<i>(all fields)</i>\n<i>&nbsp;</i>\nIf set, then the title of the field in the forms links to alt_doc.php editing ONLY that field. \nWorks for existing records only - not new records.\n&nbsp;\nExtra property:\n.returnUrl = boolean; if set, the nthe return URL is also set.";s:7:"default";s:0:"";s:12:"column_count";i:3;s:16:"is_propertyTable";i:1;}}}', '');

