INSERT INTO static_tsconfig_help VALUES ('321', '16', '255d01ae1b0d5003500f17aeccc21a14', '', 'nbsp;', 'a:1:{s:4:"rows";a:6:{i:0;a:6:{s:8:"property";s:15:"specConfs.[pid]";s:8:"datatype";s:1:"-";s:11:"description";s:908:"&quot;specConfs&quot; is an array of objects with properties that can customize certain behaviours of the display of a result row depending on it\'s position in the rootline. For instance you can define that all results which links to pages in a branch from page id 123 should have another page icon displayed. Of you can add a suffix to the class names so you can style that section differently.\n&nbsp;\n<b>Examples:</b>\nIf a page &quot;Contact&quot; is found in a search for &quot;address&quot; and that &quot;Contact&quot; page is in the rootline &quot;Frontpage [ID=23] &gt; About us [ID=45] &gt; Contact [ID=77]&quot; then you should set the pid value to either &quot;77&quot; or &quot;45&quot;. If &quot;45&quot; then all subpages including the &quot;About us&quot; page will have similar configuration.\nIf the pid value is set to 0 (zero) it will apply to all pages.\n&nbsp;\nPlease see the options below.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:24:"specConfs.[pid].pageIcon";s:8:"datatype";s:18:"-&gt;IMAGE cObject";s:11:"description";s:22:"Alternative page icon.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:25:"specConfs.[pid].CSSsuffix";s:8:"datatype";s:6:"string";s:11:"description";s:311:"A string that will be appended to the class-names of all the class-attributes used within the result row presentation. The prefix will be like this:\n&nbsp;\n<b>Example:</b>\nIf &quot;...CSSsuffix = doc&quot; then eg. the class name &quot;tx-indexedsearch-title&quot; will be &quot;tx-indexedsearch-title-doc&quot;";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:3;a:6:{s:8:"property";s:18:"search.rootPidList";s:8:"datatype";s:11:"list of int";s:11:"description";s:299:"A list of integer which should be root-pages to search from. Thus you can search multiple branches of the page tree by setting this property to a list of page id numbers.\nIf this value is set to less than zero (eg. -1) searching will happen in ALL of the page tree with no regard to branches at all.";s:7:"default";s:24:"The current root-page id";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:4;a:6:{s:8:"property";s:32:"search.detect_sys_domain_records";s:8:"datatype";s:7:"boolean";s:11:"description";s:86:"If set, then the search results are linked to the proper domains where they are found.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:5;a:6:{s:8:"property";s:39:"search.detect_sys_domain_records.target";s:8:"datatype";s:6:"string";s:11:"description";s:25:"Target for external URLs.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');

INSERT INTO static_tsconfig_help VALUES ('323', '323', '315ccebcea83a604b0f60107bbf6460a', '', 'page:mod.web_txdanewslettersubscriptionM1; beuser:mod.web_txdanewslettersubscriptionM1', 'a:1:{s:4:"rows";a:3:{i:0;a:6:{s:8:"property";s:15:"menu.function.1";s:8:"datatype";s:7:"boolean";s:11:"description";s:81:"If set to 0 (zero) the &quot;Newsletter categories&quot; item will be unavailable";s:7:"default";s:1:"1";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:15:"menu.function.2";s:8:"datatype";s:7:"boolean";s:11:"description";s:75:"If set to 0 (zero) the &quot;Subscriber list&quot; item will be unavailable";s:7:"default";s:1:"1";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:18:"fe_users_fieldList";s:8:"datatype";s:15:"list of strings";s:11:"description";s:229:"A list of fe_users fields to list in the backend for selection. The fields cound be from this list. Fields can only be listed once:\n&nbsp;\nusername,name,address,zip,city,telephone\n&nbsp;\nThe field, &quot;email&quot; is mandatory.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');

INSERT INTO static_tsconfig_help VALUES ('324', '323', '9b339e8e0dc059aaad2c3d88fbda4eb3', '', 'tsref:plugin.tx_danewslettersubscription_pi1', 'a:1:{s:4:"rows";a:4:{i:0;a:6:{s:8:"property";s:13:"sorting_field";s:8:"datatype";s:6:"string";s:11:"description";s:153:"Allows you to set a sorting field for the listing of news categories.\nAllows values from the list;\nsorting,starttime,endtime,uid,crdate,title,description";s:7:"default";s:7:"sorting";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:12:"sorting_desc";s:8:"datatype";s:7:"boolean";s:11:"description";s:38:"If set, sorting is in descending order";s:7:"default";s:1:"0";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:4:"mode";s:8:"datatype";s:6:"string";s:11:"description";s:370:"Defines the mode. Can be set to blank, &quot;dual&quot; or &quot;login&quot;.\nThe default means that people will have to enter their email address. They will receive an email with a link back to the site which will allow them to subscribe or edit subscription.\nIf &quot;login&quot; then the subscription works for login users only.\nIf &quot;dual&quot; then it uses both.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:3;a:6:{s:8:"property";s:16:"mode.noAuthEmail";s:8:"datatype";s:3:"int";s:11:"description";s:421:"Default is 0 (zero)\n&nbsp;\n<b>1:</b>\nIf set non-subscribed users are NOT sent an email but directly guided to the subscription form. Thus there is NOT safety for users submitted correct email addresses or their own address for that sake.\nHowever if a user is already subscribed he will receive an email with a link to the page.\n<b>2:</b>\nLike 1, but where the editing of an existing profile does not require login either.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');

DROP TABLE IF EXISTS sys_tabledescr;

CREATE TABLE sys_tabledescr (
  tablename varchar(40) DEFAULT '' NOT NULL,
  fieldname varchar(40) DEFAULT '' NOT NULL,
  description tinytext NOT NULL,
  details text NOT NULL,
  syntax text NOT NULL,
  image tinytext NOT NULL,
  image_descr tinytext NOT NULL,
  seeAlso text NOT NULL,
  serial int(11) DEFAULT '0' NOT NULL,
  tstamp int(11) DEFAULT '0' NOT NULL,
  PRIMARY KEY (tablename,fieldname)
);

INSERT INTO sys_tabledescr VALUES ('pages', 'title', 'Enter the title of the page. This is normally shown in the website navigation.', '', 'You must enter a page title. The field is required.', '', '', '', '0', '1011029579');

INSERT INTO sys_tabledescr VALUES ('pages', 'layout', 'Select a layout for the page. Any effect depends on the website template.', 'This option may not have any impact on the page design since it depends on how (and if) the front end template uses it. In most default templates it\'s not used.', '', '', '', '', '0', '1011029579');

INSERT INTO sys_tabledescr VALUES ('pages', 'doktype', 'Select the page type. This affects whether the page represents a visible webpage or is used for other purposes.', '\'Standard\' and \'Advanced\' types are regular webpages, just differing in the number of options. \r\n\'External URL\' and \'Shortcut\' types allows you to let a page be a shortcut to either an external URL or in the latter case a page in the page tree.\r\n\'Not in menu\' is also a regular page, but will not be shown in the navigation.\r\n\'Backend User Section\' is an option which requires a backend user to be logged in for the page to be visible.\r\n				\r\nSpecial types:\r\n\'Spacer\' is only meaningfull if your frontend navigation is programmed to use it. In this case it does not represent a page with content but is intended to be a non-clickable placeholder in the navigation.\r\n\'SysFolder\' is a page type which cannot be shown as a webpage but is rather acting as a storage for records of your choice.\r\n\'Recycler\' is a garbage can. Pages and records in a recycler are not visible in the frontend.\r\n\r\n<b>Notice:</b> Each type usually has a specific icon attached. Also certain types may not be available for a user (so you may experience that some of the options is not available for you!). And finally each type is configured to allow only certain table records in the page (SysFolder will allow any record if you have any problems).', '', '', '', 'pages:shortcut,\r\npages:url', '0', '1011029579');

INSERT INTO sys_tabledescr VALUES ('pages', 'hidden', 'If this is checked, the page will be hidden on the website.', 'The \'Hidden\', \'Start\', \'Stop\' and \'Access\' options are all about limiting access to pages and thus they are related in functionality. In any case where a page is not visible due to one of these options, the page will not show up in the site navigation.\r\nNotice, these options are related <em>only</em> to the page for which they are set. If you want their effect to cover the subpages as well, you must enable the \'Include subpages\' option.\r\nWhen you\'re logged in as a backend user you can preview hidden pages if you have read access to them.', '', 'hidden_page.gif', 'The page icon of a hidden page is dimmed and a red X is added.', 'pages:starttime,pages:endtime,pages:fe_group,pages:extendToSubpages', '0', '1011029579');

INSERT INTO sys_tabledescr VALUES ('pages', 'starttime', 'The \'Start\' time determines the date from which the page will be visible online. Use this to \'publish\' the page on a certain date.If \'Start\' time is not set, the page will be online instantly (unless the page is hidden otherwise).', '', 'Format is DD-MM-YYYY. You may enter the value in other ways - it will be evaluated immediately.\r\nIf you insert eg. a \'d\' the current date is inserted. You can also prepend any value in the field with eg. \'+10\' which will add 10 days to the current value. For instance setting the value to \'d+10\' would select a date 10 days from now.', '', '', 'pages:endtime,pages:hidden', '0', '1011029579');

INSERT INTO sys_tabledescr VALUES ('pages', '', 'A \'Page\' record usually represents a webpage in Typo3. All pages has an id-number by which they can be linked and referenced. The \'Page\' record does not itself contain the content of the page - for this purpose you should create \'Page content\' records.', 'Depending on the \'Type\' of the page, it may also represent a general storage for database elements in Typo3. In that case it is not necessarily available as a webpage but only internally in the page tree as a place to store items such as users, subscriptions etc.\r\nThe pages table is the very backbone in Typo3. All records editable by the mainstream modules in Typo3 must \'belong\' to a page. It\'s exactly like files and folders on your computers harddrive. \r\nThe pages are organized in a tree structure which is not only a very handy way of organizing in general but also a optimal reflection of how you should organize the pages on your website. And thus you\'ll normally find that the page tree is a reflection of the website navigation itself.\r\n\r\nTechnically all database elements has a field \'uid\' which is a unique identification number. Further they must have a field \'pid\' which holds the uid-number of the page (page id) to which they belong. If the \'pid\' field is zero the record is found in the so called \'root\'. Only administrators are allowed access to the root and furthermore table records must be configured to either belonging to a page or being found in the root.', '', '', '', 'tt_content', '0', '1011031225');

INSERT INTO sys_tabledescr VALUES ('pages', 'endtime', 'The \'Stop\' time is the date from which the page will not be online anymore.', '', 'See Page / Start (click below)', '', '', 'pages:starttime', '0', '1011029861');

INSERT INTO sys_tabledescr VALUES ('pages', 'fe_group', 'If \'Access\' is set to a usergroup name, only website users which are members of the selected usergroup will be able to view the page when they are logged in.The special option \'Hide at login\' means the page will <em>not</em> be visible for website use', '', '', '', '', '', '0', '1011029579');

INSERT INTO sys_tabledescr VALUES ('pages', 'extendToSubpages', 'By default the Start, Stop, Access and Hidden options are effective <em>only</em> for the page on which they are set. If the option \'Include subpages\' is checked for the page, then their effect is inherited by all subpages to that page.', 'Consider a scenario where you want to create a whole section of subpages only visible for users which are logged in. Setting the \'Access\' option for the top page in the section would affect access to that page only. So the page would not show up in the navigation unless you are logged in. \r\nHowever the subpages would still be visible if one could guess the id-number of the pages and enter it manually in the URL. But setting this option would extend the effect of the top page setting to cover all subpages as well.', '', '', '', 'pages:hidden,\r\npages:starttime,\r\npages:endtime,\r\npages:fe_group', '0', '1011029579');

INSERT INTO sys_tabledescr VALUES ('pages', 'alias', 'An \'alias\' is a unique identification string for a page. You can use this string instead of the id-number to refer to the page.', 'Ultimately a page is identified by its id-number. For instance you may refer to a page in Typo3 by \'.../?id=123\'. If the page with id \'123\' has the alias \'one_two_three\', then the URL \'.../?id=one_two_three\' would display exactly the same page, because \'one_two_three\' is an alias for the id \'123\'.\r\nNormally you should assign aliases only to a limited number of pages, preferably those you need to link to a lot. In most Typo3 configurations you would be able to use the syntax \'.. &lt;link one_two_three&gt;go to the page&lt;/link&gt; ..\' to create a link to the page. This is easier to remember.\r\nAnother use is if you need to change which page such a link is refering to. In that case you can delete the alias for one page and enter it for another and all links pointing to the alias would now refer to another id-number.', 'An alias can contain only lowercase alphanumeric characters and must be unique. \r\nPlease notice: If the entered value was not unique, it will automatically be prepended with a number. ', '', '', '', '0', '1011029579');

INSERT INTO sys_tabledescr VALUES ('pages', 'target', 'The HTML target of menu items linking to this page.', 'Set this value only to overrule the default link, which should be configured correctly for most pages in the website TypoScript template.', '', '', '', '', '0', '1011029579');

INSERT INTO sys_tabledescr VALUES ('pages', 'no_cache', 'This option disables server caching of the page.', 'Normally pages in Typo3 are cached on the server after being constructed for the first time. This means a page is displayed much faster the second time it\'s requested. \r\nFor most pages this presents no problems - rather it\'s boosting the performance of the website.\r\nHowever you may need to disable caching for a page if it contains dynamic content which must not be cached under any circumstances. Normally the PHP-code rendering such content is programmed to disable the caching automatically. But in any case you can force the page not to be cached via this option.', '', '', '', 'pages:cache_timeout', '0', '1011029579');

INSERT INTO sys_tabledescr VALUES ('pages', 'cache_timeout', 'Sets the cache timeout for the page.', 'By default pages last for 24 hours in the cache. After that period the page is constructed again and rewritten to the cache. Depending on your configuration, the cache may also be cleared at midnight.\r\nAnyhow you may here set another timeout time for the page in cache if you need to. \r\nThis option is used quite rarely.', '', '', '', 'pages:no_cache', '0', '1011029579');

INSERT INTO sys_tabledescr VALUES ('pages', 'no_search', 'If enabled, this option excludes the page from searching operations on the website.', '', '', '', '', '', '0', '1011029579');

INSERT INTO sys_tabledescr VALUES ('pages', 'author', 'Enter the page authors name.', 'Whether the author name is used or not depends on the template. For most templates this value is not used.\r\nIf you\'re using Typo3 pages as \'articles\' then you could use this field to display the name of the page (article) author.', '', '', '', 'pages:author_email', '0', '1011029579');

INSERT INTO sys_tabledescr VALUES ('pages', 'author_email', 'Enter the page authors email address.', '', '', '', '', 'pages:author', '0', '1011029579');

INSERT INTO sys_tabledescr VALUES ('pages', 'treeStop', 'If enabled, the loading of the page tree in the classic Typo3 Backend will not decend further from this point.', 'The classic Typo3 Backend interface loads the whole page tree during the initialization. If your website contains more than 1000 pages this will slow down both the initialization process (depending on your internet connection speed) and perhaps also your work (needs memory on the client computer).\r\nIf this option is enabled you can still decend in the page tree through the \'List\' module by clicking the page titles there. In addition you can mount specific subpages in the section of course.\r\nThe Alternative Backend page tree is not affected by this option because it uses another approach for the page tree (re-calculated for each instance of the page tree).\r\nThis option is used very rarely and should be enabled and set by admin-users only.', '', '', '', '', '0', '1011029579');

INSERT INTO sys_tabledescr VALUES ('pages', 'TSconfig', 'Page TypoScript configuration.', 'Basically \'TypoScript\' is a concept for entering values in a tree-structure. This is known especially in relation to creating templates for Typo3 websites.\r\nHowever the same principle for entering the hierarchy of values is used here to configure various features in relation to the backend, functions in modules, the Rich Text Editor etc. \r\nThe resulting \'TSconfig\' for a page is actually an accumulation of all \'TSconfig\' values from the root of the page tree and outwards to the current page. And thus all subpages are affected as well. A print of the page TSconfig is available from the \'Page TSconfig\' menu in the \'Web>Info\' module.\r\n\r\n<strong>Example:</strong>\r\nNormally the \'Web>Page\' module lets you create content in four columns, Left, Normal, Right, Border. However most websites use only a single or two columns. So some of these content columns are just taking up space and may even be confusing to users.\r\nIf you enter this string in the \'TSconfig\' field, the \'Web>Page\' module will allow you to work only in the \'Normal\' and \'Border\' column!\r\n\r\n<em>mod.web_layout.tt_content.colPos_list = 0,3</em>\r\n\r\n(Must be entered in a single line!)\r\n\r\nFor the full range of options you must refer to the the \'Adminguide\' PDF-document.', 'Basic TypoScript syntax <em>without</em> \'Conditions\' and \'Constants\'.\r\n\r\nIt\'s recommended that only admin-users are allowed access to this field!', '', '', '', '0', '1011029579');

INSERT INTO sys_tabledescr VALUES ('pages', 'module', 'Define the page as a frontend \'module\'.', 'In the backend there is a module called \'Web>Modules\' as well as a header \'Modules\' in the \'User>Task Center\'. Both functions list the available \'Frontend modules\' to which the user has access.\r\nBasically, setting this option just marks the page so the backend knows this page contains eg. a forum or is the root page of a shopping section on the website. And based on this knowledge the backend is able to display a shortcut list as well as relevant functionality in the \'Web>Modules\' module. The frontend display is - largely - not affected.\r\nSo in effect, by \'marking\' a page to be a \'Shop\' module simply means it\'ll show up in the shortcut list of modules in the backend.\r\n\r\nThere may be a little more to it than this, but for details on the subject, you must refer to the administration documentation.\r\n\r\nIt\'s recommended that only admin-users are allowed access to this field!', '', '', '', '', '0', '1011029579');

INSERT INTO sys_tabledescr VALUES ('pages', 'media', 'Attach media files related to the page.', 'This fields allows you to attach various files to the page record. \r\nIf and how these files are used depends on your template. So basically which files you attach here and in which order they must be listed is determined by your specific website template and thus you should seek instructions in the documentation for your specific website.\r\nAs an example this field could be used from the template to allow you to attach a logo-image to the page. Or the template might be programmed to expect the first file to be an image used as a link in the navigation menu. But remember, this totally depends on how this field is used in the programming of the template.', '', '', '', '', '0', '1011029579');

INSERT INTO sys_tabledescr VALUES ('pages', 'subtitle', 'Enter a subtitle if needed.', 'Whether this field is used or not depends on the template. In most cases it\'s not used.\r\nYou may use this field as you like in your own templates.', '', '', '', '', '0', '1011029579');

INSERT INTO sys_tabledescr VALUES ('pages', 'url', 'Enter the URL to which this page is supposed to link.', 'Example: \'http://www.typo3.com\' or just \'www.typo3.com\' provided the \'Type\' selector is set to \'http://\'.\r\nUse the \'Type\' selector to select a prefix for the URL.\r\n\r\nThis option is used only if the page \'Type\' is set to \'External URL\'.', '', '', '', '', '0', '1011029579');

INSERT INTO sys_tabledescr VALUES ('pages', 'shortcut', 'Insert a reference to the page to which this page is supposed to be a shortcut.', 'Click the \'Browse for records\' icon and a new window will appear in which you can easily select the page to which the shortcut is supposed to link.\r\n\r\nThis option is used only if the page \'Type\' is set to \'Shortcut\'.', '', 'page_shortcut.gif', '', '', '0', '1011029579');

INSERT INTO sys_tabledescr VALUES ('pages', 'keywords', 'Enter keywords for the page separated by commas.', 'Most Typo3 websites inserts these keywords in a meta-tag on the page. Depends on the TypoScript template.\r\nThe keywords may also be used for searching.', '', '', '', 'pages:description', '0', '1011029579');

INSERT INTO sys_tabledescr VALUES ('pages', 'description', 'Enter a short description for the page.', 'Most Typo3 websites insert this description in a meta-tag on the page. Depends on the TypoScript template.', '', '', '', 'pages:keywords\r\npages:abstract', '0', '1011029579');

INSERT INTO sys_tabledescr VALUES ('pages', 'abstract', 'Enter an abstract for the page.', 'An abstract is a short description of the page content. It\'s different from the Description field in the fact that it\'s not inserted in any meta-tags.\r\nBasically the use of the abstract depends on the TypoScript template. You may use this field for user defined purposes as you like. \r\nHowever there is one explicit use with most Typo3 websites: If you insert a content element of the type \'Menu/Sitemap\' and select the subtype \'Menu of subpages to these pages (with abstract)\' then the content of this field will be displayed as a short description in the list.', '', '', '', 'pages:description', '0', '1011029579');

INSERT INTO sys_tabledescr VALUES ('pages', 'newUntil', 'Enter the \'expiry date\' of the page.', 'The \'expiry date\' of the page has no effect on the display of the webpage. This value is rather for internal use meant as a reminder of the date when the content of this page should be revised. So if the content of the page will be outdated at a certain point in time, you could set this value to that approximate date.\r\nIn order to obtain an overview of the expiry dates of pages, use the \'Pagetree overview\' in the \'Web>Info\' module.', 'See Page / Start (click below)', '', '', 'pages:starttime', '0', '1011029861');

INSERT INTO sys_tabledescr VALUES ('pages', 'lastUpdated', 'Enter the time and date of the most recent update of this page.', 'How the value of this field is used is determined by the TypoScript template of the website. First of all a \'Last updated\' notice needs to be inserted on the pages and secondly the template must be configured to use this value. \r\nThe most common use which is found in the standard templates will automatically display the last update time and date <em>unless</em> this field specifies a value in which case this value is inserted.', 'Format is HH:MM DD-MM-YYYY. \r\nIf you insert eg. a \'d\' the current time and date is inserted. You can also prepend any value in the field with eg. \'+10\' which will add 10 days to the current value.', '', '', '', '0', '1011029579');

INSERT INTO sys_tabledescr VALUES ('sys_language', 'title', 'Enter the name of this additional system language.', '', '', '', '', '', '0', '0');

INSERT INTO sys_tabledescr VALUES ('sys_language', 'hidden', 'This option will hide the system language from being available in the backend.', '', '', '', '', '', '0', '0');

INSERT INTO sys_tabledescr VALUES ('fe_users', 'username', 'Enter the username of the website user. This is needed to login.', '', 'A username is required and must be in lowercase without spaces in it. Furthermore the username must be unique among other usernames in the page where it\'s saved. If it is not unique a number will be prepended automatically.', '', '', '', '0', '0');

INSERT INTO sys_tabledescr VALUES ('fe_users', 'password', 'Enter the password for the username above. This is needed to login.', '', 'A password is required and must be in lowercase without spaces in it.', '', '', '', '0', '0');

INSERT INTO sys_tabledescr VALUES ('fe_users', 'usergroup', 'Select a user group for the username.', 'When a user logs in he is able to view all content which is access restricted to the user <em>group(s)</em> the user is a member of. Therefore the user login primarily makes sense with regard to the member groups.', '', '', '', '', '0', '0');

INSERT INTO sys_tabledescr VALUES ('fe_users', 'lockToDomain', 'Enter the host name from which the user is forced to login.', 'A Typo3 system may have multiple domains pointing to it. Therefore this option secures that users can login only from a certain host name.', 'Either a domain name, "www.typo3.com" or an IP address, "10.34.222.83"', '', '', 'fe_groups:lockToDomain', '0', '0');

INSERT INTO sys_tabledescr VALUES ('fe_users', 'name', 'Enter the regular name of the user, both first- and surname.', '', '', '', '', '', '0', '0');

INSERT INTO sys_tabledescr VALUES ('fe_users', 'address', 'Enter the street address of the user.', '', '', '', '', '', '0', '0');

INSERT INTO sys_tabledescr VALUES ('fe_users', 'www', 'Enter the homepage URL', '', '', '', '', '', '0', '0');

INSERT INTO sys_tabledescr VALUES ('fe_users', 'title', 'Enter the job title, eg. \'Director\' or \'Accountant\'.', '', '', '', '', '', '0', '0');

INSERT INTO sys_tabledescr VALUES ('fe_users', 'company', 'Enter the name of the company in which the user is employed.', '', '', '', '', '', '0', '0');

INSERT INTO sys_tabledescr VALUES ('fe_users', 'image', 'Insert a photo of the user.', '', '', '', '', '', '0', '0');

INSERT INTO sys_tabledescr VALUES ('fe_users', 'disable', 'This option will temporarily disable the user from logging in.', '', '', '', '', 'fe_users:starttime,fe_users:endtime', '0', '0');

INSERT INTO sys_tabledescr VALUES ('fe_users', 'starttime', 'Enter the date from which the account is active.', '', '[SHARED:dateTime]', '', '', 'fe_users:disable,fe_users:endtime', '0', '0');

INSERT INTO sys_tabledescr VALUES ('fe_users', 'endtime', 'Enter the date from which the account is disabled.', '', '[SHARED:dateTime]', '', '', 'fe_users:disable,fe_users:starttime', '0', '0');

INSERT INTO sys_tabledescr VALUES ('fe_users', 'TSconfig', 'Enter additional TSconfig for the user (advanced).', 'This field allows you to enter TypoScript style configuration values for the user. Currently there are no system defined keys. If there ever will be, they will be described in detail in the administrative documentation.\r\n\r\nThe primary use of this field (as well as the \'sister\'-field in the Website usergroup table) is for custom purposes. In this case, you should use the root-key "user" for your customly defined values. Thus you don\'t risk any future system keys to interfere with your configuration.\r\n\r\nYou access the values of the configuration from your PHP include code in the frontend by calling the function <em>$GLOBALS["TSFE"]->fe_user->getUserTSconf()</em> which will return an array with the valuetree in it.\r\n\r\n<b>Warning:</b> You should use Website user/group TSconfig in your PHP-scripts ONLY if your resulting content is NOT being cached! So call it from TypoScript USER_INT cObjects or disable caching from your PHP-code by calling <em>$GLOBALS["TSFE"]->set_no_cache();</em>\r\n\r\n<b>TSconfig from Website usergroups:</b>\r\nNotice that the configuration from the field TSconfig in the member groups of the user is accumulated and prepended to the TSconfig of the user before it\'s parsed. This means for instance that a key defined in the TSconfig of the user (this field) will override the same key defined in the last (bottom) usergroup in the list which will again override the same key defined in the first (top) usergroup in the list.\r\n\r\n<b>Example:</b>\r\nLet\'s imagine you want to define for a user that his name is printed in the bottom of the webpages when he is logged in. First of all you must write a PHP-function and insert the result from it as a USER_INT cObject in the bottom of the page. But your function needs to know, whether or not this user is configured to have his name printed. So you decide to use a custom TSconfig key, \'user.printUserName\':\r\n\r\n<em>In the TSconfig field of the user or usergroup:</em>\r\n\r\nuser.printUserName = 1\r\n\r\n\r\n<em>In your PHP-function:</em>\r\n\r\n$userTSConfig = $GLOBALS["TSFE"]->fe_user->getUserTSconf();\r\nif ($userTSConfig["user."]["printUserName"]) {\r\n  return $GLOBALS["TSFE"]->fe_user->user["name"];\r\n}', 'TypoScript syntax, without conditions and constants.', '', '', 'pages:TSconfig,fe_groups:TSconfig,be_users:TSconfig,be_groups:TSconfig', '0', '0');

INSERT INTO sys_tabledescr VALUES ('fe_groups', 'hidden', 'This option disables the user group.', 'If you disable a user group all user which are members of the group will in effect not inherit any properties this group may have given them.\r\nFor instance if a page or content element was assigned access only by this group and you disable the group, noone would gain access to that page or content element.', '', '', '', 'fe_users:disable', '0', '0');

INSERT INTO sys_tabledescr VALUES ('fe_groups', 'title', 'Name of the group.', 'This title will appear as the group name in the \'Access\'-list in other records.', '', '', '', '', '0', '0');

INSERT INTO sys_tabledescr VALUES ('fe_groups', 'lockToDomain', 'Enter the host name from which the group will be available only.', 'This options may be important if you have multiple websites in the same Typo3 database but still only one main storage page for all Website users. Thus the users may log in on any of the website urls. This may be considered a feature (having a global login as a user) or a problem (in which case you should have multiple user storages anyway). However you may wish to limit the use of a Website usergroup to a specific website and thus you don\'t want the group to be enabled for the user when he logs in at another URL (still from the same database of course). By entering the host name of the website here, you restrict the group to be used only from within this domain.\r\n\r\n<b>Notice:</b> The use for this option is not critical if you have multiple Website user storages, namely one for each site.', '', '', '', 'fe_users:lockToDomain', '0', '0');

INSERT INTO sys_tabledescr VALUES ('fe_groups', 'description', 'Enter a description of the group.', 'This is for internal use and is just a way to put a note on what the group is for, why you created it and who should be members.\r\nUse it as you like.', '', '', '', '', '0', '0');

INSERT INTO sys_tabledescr VALUES ('fe_groups', 'TSconfig', 'TSconfig for the group (advanced).', 'This is the user TSconfig for the group. The values are inherited by any Website user who is a member of this group. \r\nThe configuration is subject to being overridden in other groups and by the TSconfig of the user himself.', 'TypoScript syntax, without conditions and constants.', '', '', 'fe_users:TSconfig', '0', '0');

INSERT INTO sys_tabledescr VALUES ('be_users', '', 'This is the table of backend administration users.', '', '', '', '', 'be_groups', '0', '1011030999');

INSERT INTO sys_tabledescr VALUES ('be_groups', '', 'This is the backend administration user groups available for the Backend users. These determine the permissions for the Backend users.', '', '', '', '', 'be_users', '0', '1011030999');

INSERT INTO sys_tabledescr VALUES ('sys_filemounts', '', 'Filemounts describe a filepath on the server, relative or absolute.', 'By creating a file mount record and placing a reference to it in a Backend usergroup you can allow a user access to the file mount in the File>List module. \r\nYou need to create and set at least one filemount with a folder \'_temp_\' in it if you want users to upload files through the webbrowser.\r\nFilemounts may also configure access to a path on the server to which the user has FTP-access. Just remember to set file-permissions on the server correctly so the webserver user (which PHP is running as) has at least read access to the FTP-dir.', '', '', '', 'be_users,\r\nbe_groups', '0', '1011036452');

INSERT INTO sys_tabledescr VALUES ('static_template', '', 'Static templates is a centrally distributed TypoScript library. You should never changes these values.', 'The Static templates are meant to be included by your TypoScript Template records depending on your needs. Refer to the \'TSref\' document (see below) for details on this process.\r\nNotice that by each release of Typo3 you should reinstall the Static Templates table by the install tool. See the \'Install/Upgrade\' document for details (see below).', '', '', '', 'sys_template\r\nTSref | http://www.typo3.com/doclink.php?doc=TSref.pdf\r\nInstall/Upgrade | http://www.typo3.com/doclink.php?doc=installing_upgrading.pdf', '0', '1011036452');

INSERT INTO sys_tabledescr VALUES ('sys_action', '', 'An \'Action\' is a configuration of a specific task which may be performed by assigned usergroups via the Task Center.', 'Currently an Action may be configured to create additional backend usergroups without being and admin-user and by selecting from a limited set of options. Another option is to let an action initiate a SQL SELECT-query in the database and return the result as a CSV file. \r\nActions can be assigned to a Backend usergroup and they are activated by a single click in the Task Center.', '', '', '', '', '0', '1011036452');

INSERT INTO sys_tabledescr VALUES ('sys_workflows', '', 'Workflow records defines a certain task to be done by a To-Do item in the Task Center. ', 'Basically a Workflow record lets you define a database table in which a record should be created, a review user and Backend usergroups from which the issuer and recipients of a workflow task (To-Do) is selected. It also contains information about what happens with the record attached to the workflow after it done. For instance you can configure a workflow to create the record in one page and by the event of finalizing the workflow it\'s automatically moved to another page and maybe even unhidden (published online).\r\n\r\nWorkflows are an integrated part of the Task Center\'s To-Do facility.', '', '', '', '', '0', '1011036453');

INSERT INTO sys_tabledescr VALUES ('sys_language', '', 'Defines which languages are alternatively available on each webpage.', 'The Web>Page module has a feature which allows users to edit page content divided into not only columns but also languages. The languages available for translation are determined by the number of Website Language record created.\r\nFor a webpage to be available in another language a "Alternative Page Language" record must be created on the page.', '', '', '', 'pages_language_overlay', '0', '1011036453');

INSERT INTO sys_tabledescr VALUES ('tt_content', '', 'All visible content on webpages in Typo3 is typically made up or represented with "Pagecontent" records. ', '"Pagecontent" records is ordered and can thus be rearranged. Also notice that the "Language" and "Column" selector of the "Pagecontent" records determines where and in which situation they appear. If you do not change the value of the "Language" and "Column" selectors you are most sure that the element will appear by default.', '', '', '', '', '0', '1011036453');

INSERT INTO sys_tabledescr VALUES ('tt_address', '', 'Built-in address database.', 'You may use it to build address lists in Typo3. There are in fact default TypoScript code which allows you to implement a online administration of the address information by each person in the list himself.\r\nThis table is also used by the Direct Mail system as the default subscription table for newsletters.', '', '', '', '', '0', '1011036453');

INSERT INTO sys_tabledescr VALUES ('tt_links', '', 'Built-in links database.', 'Currently there are no default use of the links table. You may implement and use it as you like through your own PHP-scripts.', '', '', '', '', '0', '1011036453');

INSERT INTO sys_tabledescr VALUES ('tt_guest', '', 'Built-in guestbook. ', 'This table is used by the default TypoScript guestbook available. All entries goes into this table and you can thus administer any guestbook through the backend.', '', '', '', 'tt_content:list_type', '0', '1011036453');

INSERT INTO sys_tabledescr VALUES ('tt_board', '', 'Built-in discussion board.', 'This table is used by the default TypoScript discussion board/list available. All entries goes into this table and you can thus administer the forums through the backend.', '', '', '', 'tt_content:list_type', '0', '1011036453');

INSERT INTO sys_tabledescr VALUES ('tt_calender', '', 'Built-in calendar. ', 'This table is meant to hold calender events displayed on the website. There is a primitive TypoScript based script available for display although you might as well use your own.', '', '', '', 'tt_content:list_type,\r\ntt_calender_cat', '0', '1011036453');

INSERT INTO sys_tabledescr VALUES ('tt_calender_cat', '', 'Built-in calendar categories.', 'Categories for the calender items.', '', '', '', 'tt_calender', '0', '1011036453');

INSERT INTO sys_tabledescr VALUES ('tt_products', '', 'Built-in shop items.', 'This table holds the products available in the default TypoScript shopping system.', '', '', '', 'tt_products_cat\r\ntt_content:list_type', '0', '1011036829');

INSERT INTO sys_tabledescr VALUES ('tt_products_cat', '', 'Built-in  shop items categories.', 'The categories are only used with the shopping system if the shop is configured to do so.', '', '', '', 'tt_products', '0', '1011036829');

INSERT INTO sys_tabledescr VALUES ('tt_news', '', 'Built-in news system.', '\'News\' items are typically those which goes on your frontpage on the websites and allows you to link down to a page with the full story. They may also represent articles (links to full pages) in the system.', '', '', '', 'tt_content:list_type,\r\ntt_news_cat', '0', '1011036829');

INSERT INTO sys_tabledescr VALUES ('tt_news_cat', '', 'Built-in news system categories.', '', '', '', '', 'tt_news', '0', '1011036829');

INSERT INTO sys_tabledescr VALUES ('sys_dmail', '', 'Direct mail campaigns ', 'The "Direct mail" module in Typo3 lets you send highly customized and personalized newsletters to subscribers either as HTML or Plain text, with or without attachments.\r\nA \'Direct mail\' record contains information about a direct mail campaign such as subject, sender, priority, attachments and whether HTML or Plain text content is allowed. Furthermore it also holds the compiled mail content which is to be sent to the subscribers.\r\nEverything regarding this module is controlled through the Web>Modules backend module.', '', '', '', 'sys_dmail_group\r\ntt_content:list_type', '0', '1011036829');

INSERT INTO sys_tabledescr VALUES ('sys_dmail_group', '', 'Defines a group of recipients of Direct mails.', 'You can compile the group of either single individuals, all Address records within a page or branch, all results or an SQL query or maybe the result from other Direct mail groups.', '', '', '', 'sys_dmail', '0', '1011036829');

INSERT INTO sys_tabledescr VALUES ('fe_users', '', 'A website login user.', 'This is users with a username and password who are allowed specific access to otherwise restricted areas on your website.', '', '', '', 'fe_groups\r\nbe_users', '0', '1011036829');

INSERT INTO sys_tabledescr VALUES ('fe_groups', '', 'Usergroups for Website users.', 'The access restriction in Typo3 is assigned to these usergroups. When a page or other element is access restricted to a group, only Website users which are members or that group will be able to view the element on the website.', '', '', '', 'fe_users', '0', '1011036829');

INSERT INTO sys_tabledescr VALUES ('sys_domain', '', 'Determines the entrypoint of a certain host name.', 'When a Typo3 database contains more than a single website, you\'ll typicall have to assign a domain name to each new websites "root-page". In practice you point all the domains to the same Typo3 database on the server, but then you insert these Domain records at the positions in the page tree where you want to direct requests to the domain. And thus pages \'outside\' that point will not be accessible from that domain.\r\nThis is like "Virtual hosts" in Apache servers.\r\n\r\n(Should probably be available for admin only.)', '', '', '', 'sys_language\r\nsys_template\r\n', '0', '1011036829');

INSERT INTO sys_tabledescr VALUES ('sys_note', '', 'Internal reminder note placed on a the page.', 'This provides a simple way to attach instructional notes to a page regarding backend related issues. Use it as you like for reminders, instructions etc. ', '', '', '', 'pages\r\n', '0', '1011036829');

INSERT INTO sys_tabledescr VALUES ('sys_template', '', 'TypoScript template record.', 'The \'Template\' records are the vital components used to set the design of the webpages. No matter how (almost) you would like to build your website, it must be initiated by a \'Template\' record placed in the root of the page tree of the site.\r\nBasically, create a \'Template\' record, enter a title and make sure to check of Clear constants, setup and Rootline. Then enter some TypoScript configuration in the Setup field and you\'re set. \r\nIt\'s very important to read how to do all this in the documents listed below! Also make sure you use the Web>TS module to do all the working with templates. It\'s a specialized tool to help you with this.', '', '', '', 'static_template\r\nTSref | http://www.typo3.com/doclink.php?doc=TSref.pdf\r\nTypoScript by Example | http://www.typo3.com/doclink.php?doc=TypoScript_by_example.pdf\r\n', '0', '1011036829');

INSERT INTO sys_tabledescr VALUES ('pages_language_overlay', '', 'Represents an alternative language for the page.', 'These records contains fields very similar to the Page records, exactly those fields which should be translated to another language in case you want a localized version of the page.\r\nCreate only ONE Alternative Page Language record of a specific language in each page. Having more than one record will leave it uncertain which of them is used when the page is displayed.\r\n\r\nBefore you can create Alternative Page Language records you should create a \'master\' language to be available in the root of the website ("Website Language")\r\nThe TypoScript template must be configured to allow other languages as well.\r\n\r\nTechnically when an alternative language is being viewed, the content of the Alternative Page Language record is simply overlaid on the actual page records thus substituting the content of the fields in the default language.', '', '', '', 'sys_language', '0', '1011036829');

INSERT INTO sys_tabledescr VALUES ('be_users', 'username', 'Enter the login name of the backend user.', 'A username is required and must be in lowercase without spaces in it. Furthermore the username must be unique. If it is not unique a number will be prepended automatically.', '', '', '', 'be_users:password', '0', '1011054587');

INSERT INTO sys_tabledescr VALUES ('be_users', 'password', 'Enter the password for the backend username above (Notice the value you enter <i>will</i> be readable in the field!).', 'The password is required. Before the password is sent to the server it\'s md5-hashed, so the password value itself is not transferred over the internet. This is true both when editing the password and when the user logs in. \r\nWhile this principle does not reveal the raw password it is <i>not</i> the same as real encryption. If you need the highest degree of security you should install the Typo3 backend on a secure server.\r\nThe password is stored in the database as an md5-hash and thus it\'s not possible to extract the original password from the database either. This means that \'lost passwords\' must be substituted with a new password for the user.', '', '', '', '', '0', '1011054667');

INSERT INTO sys_tabledescr VALUES ('be_users', 'usergroup', 'Assign backend user groups to the user.', 'The backend user groups defines the permissions which the backend user will inherit. So unless the backend user is an \'Admin\' user, he needs to be a member of one or more user groups in order to have practically any permissions assigned. The properties set in the user groups are mostly added together.\r\nThe first (top) group in the list is the group which will, by default, be the owner of pages the user creates.', '', '', '', 'be_users:TSconfig,\r\nbe_groups', '0', '1011037201');

INSERT INTO sys_tabledescr VALUES ('be_users', 'lockToDomain', 'Enter the host name from which the user is forced to login.', 'A Typo3 system may have multiple domains pointing to it. Therefore this option secures that users can login only from a certain host name.', '', '', '', 'be_groups:lockToDomain,\r\nfe_users:lockToDomain,\r\nfe_groups:lockToDomain', '0', '1011037201');

INSERT INTO sys_tabledescr VALUES ('be_users', 'db_mountpoints', 'Assign startpoints for the users page tree.', 'The page tree used my all Web-submodules to navigate must have some points-of-entry defined. Here you should insert one or more references to a page which will represent a new root page for the page tree. This is called a \'Database mount\'.\r\n\r\n<strong>Notice</strong> that backend user groups also has DB mounts which can be inherited by the user. So if you want a group of users to share a page tree, you should probably mount the page tree in the backend user group which they share instead.', '', '', '', 'be_groups:db_mountpoints,\r\nbe_users:file_mountpoints,\r\nbe_users:options\r\n', '0', '1011037201');

INSERT INTO sys_tabledescr VALUES ('be_users', 'file_mountpoints', 'Assign startpoints for the file folder tree.', 'The file folder tree is used by all File-submodules to navigate between the file folders on the webserver. In order to be able to upload <em>any</em> files the user <em>must</em> have a file folder mounted with a folder named \'_temp_\' in it (which is where uploads go by default). \r\nNotice as with \'DB mounts\' the file folder mounts may be inherited from the member groups of the user. ', '', '', '', 'be_groups:file_mountpoints,\r\nbe_users:db_mountpoints,\r\nbe_users:options', '0', '1011037201');

INSERT INTO sys_tabledescr VALUES ('be_users', 'email', 'Enter the email address of the user.', 'This address is rather important to enter because this is where messages from the system is sent.\r\n<strong>Notice</strong> the user is able to change this value by himself from within the User>Setup module.\r\n', '', '', '', 'be_users:realName', '0', '1011037201');

INSERT INTO sys_tabledescr VALUES ('be_users', 'realName', 'Enter the ordinary name of the user, eg. John Doe.', '<strong>Notice</strong> the user is able to change this value by himself from within the User>Setup module.', '', '', '', 'be_users:email', '0', '1011037201');

INSERT INTO sys_tabledescr VALUES ('be_users', 'disable', 'This option will temporarily disable the user from logging in.', '', '', '', '', 'be_users:starttime,\r\nbe_users:endtime', '0', '1011037201');

INSERT INTO sys_tabledescr VALUES ('be_users', 'admin', '\'Admin\' users has TOTAL access to the system!', '\'Admin\' can do anything Typo3 allows and this kind of user should be used only for administrative purposes. All daily handling should be done with regular users. \r\n\'Admin\' users don\'t need to be members or any backend user groups. However you should be aware that any page created by an admin user without a group will not have any owner-group assigned and thus it will probably be invisible for other users. If this becomes a problem you can easily solve it by assigning a user group to the \'Admin\' user anyway. This does of course not affect the permissions since they are unlimited, but the first group listed is by default the owner group of newly created pages.\r\n\'Admin\' users are easily recognized as they appear with a red icon instead of the ordinary blue user-icon.\r\n\r\nYou should probably not assign any other users than yourself as an \'Admin\' user.', '', '', '', '', '0', '1011037201');

INSERT INTO sys_tabledescr VALUES ('be_users', 'options', 'Select if the user should inherit page tree or folder tree mountpoints from member groups.', 'It\'s a great advantage to let users inherit mountpoints from membergroups because it makes administration of the same mountpoints for many users extremely easy. \r\nIf you don\'t check these options, you must make sure the mount points for the page tree and file folder tree is set specifically for the user.', '', '', '', 'be_users:db_mountpoints,\r\nbe_users:file_mountpoints', '0', '1011037201');

INSERT INTO sys_tabledescr VALUES ('be_users', 'fileoper_perms', 'Select file operation permissions for the user.', 'These settings relates to the functions found in the File>List module as well as general upload of files.', '', '', '', 'be_users:file_mountpoints', '0', '1011037201');

INSERT INTO sys_tabledescr VALUES ('be_users', 'starttime', 'Enter the date from which the account is active.', '', '', '', '', 'be_users:disable,\r\nbe_users:endtime,\r\npages:starttime', '0', '1011037496');

INSERT INTO sys_tabledescr VALUES ('be_users', 'endtime', 'Enter the date from which the account is disabled.', '', '', '', '', 'be_users:disable,\r\nbe_users:starttime,\r\npages:starttime', '0', '1011037496');

INSERT INTO sys_tabledescr VALUES ('be_users', 'lang', 'Select the <i>default</i> language.', 'This determines the language of the backend interface for the user. All mainstream parts available for regular users are available in the system language selected. \r\n\'Admin\'-users however will experience that the \'Admin\'-only parts of Typo3 is in english. This includes all submodules in "Tools" and the Web>Template module.\r\n\r\n<b>Notice</b> this is only the default language. As soon as the user has logged in the language must be changed through the User>Setup module.', '', '', '', '', '0', '1011054793');

INSERT INTO sys_tabledescr VALUES ('be_users', 'userMods', 'Select available backend modules for the user.', 'This determines which \'menu items\' are available for the user.\r\n\r\nNotice that the same list of modules may be configured for the backend user groups and that these will be inherited by the user in addition to the modules you select here. It\'s highly likely that you should not set any modules for the user himself but rather select the modules in the backend groups he\'s a member of. However this list provides a great way to add a single module for specific users.', '', '', '', 'be_groups:groupMods', '0', '1011037496');

INSERT INTO sys_tabledescr VALUES ('be_users', 'TSconfig', 'Enter additional TSconfig for the user (advanced).', 'This field allows you to extend the configuration of the user in severe details. A brief summary of the options include a more detailed configuration of the backend modules, setting of user specific default table field values, setting of Rich Text Editor options etc. The list will be growing by time and is fully documented in the adminstration documentation, in particular \'admin_guide.pdf\' (see link below).', '', '', '', 'pages:TSconfig,\r\nfe_users:TSconfig,\r\nbe_groups:TSconfig\r\nadmin_guide.pdf|http://www.typo3.com/doclink.php?key=admin_guide.pdf\r\n', '0', '1011037496');

INSERT INTO sys_tabledescr VALUES ('be_groups', 'title', 'Name of the Backend usergroup', '', '', '', '', '', '0', '1011037496');

INSERT INTO sys_tabledescr VALUES ('be_groups', 'db_mountpoints', 'Assign startpoints for the users page tree.', 'The page tree used my all Web-submodules to navigate must have some points-of-entry defined. Here you should insert one or more references to a page which will represent a new root page for the page tree. This is called a \'Database mount\'.\r\nDB mounts may be inherited by the users which are members of this group. This does depend on whether the user is configured to include the mounts set in the member groups. However it\'s recommended to use backend user groups like this to configure mounts. Especially if the need to be shared amoung many users.', '', '', '', 'be_groups:file_mountpoints,\r\nbe_users:db_mountpoints,\r\nbe_users:options\r\n', '0', '1011037496');

INSERT INTO sys_tabledescr VALUES ('be_groups', 'file_mountpoints', 'Assign startpoints for the file folder tree.', 'The file folder tree is used by all File-submodules to navigate between the file folders on the webserver.\r\nNotice as with \'DB mounts\' the file folder mounts may be inherited by the users which are members of this group.', '', '', '', 'be_groups:db_mountpoints,\r\nbe_users:file_mountpoints,\r\nbe_users:options\r\n', '0', '1011037496');

INSERT INTO sys_tabledescr VALUES ('be_groups', 'pagetypes_select', 'Select which \'Types\' of Pages the members may set.', 'This option limits the number of valid choices for the user when he is about to select a page type.', '', '', '', 'pages:doktype,\r\nbe_groups:inc_access_lists', '0', '1011037496');

INSERT INTO sys_tabledescr VALUES ('be_groups', 'tables_modify', 'Select which tables the members may modify.', 'An important part of setting permissions is to define which database tables a user is allowed to modify. \r\nTables allowed for modification is automatically also allowed for selection and thus you don\'t need to set tables entered here in the "Tables (listing)" box.\r\n\r\n<strong>Notice</strong> that this list adds to the fields selected in other member groups of a user.', '', '', '', 'be_groups:tables_select,\r\nbe_groups:inc_access_lists', '0', '1011037496');

INSERT INTO sys_tabledescr VALUES ('be_groups', 'tables_select', 'Select which tables the members may see in record lists (\'modify\' tables does not need to be re-entered here!).', 'This determines which tables - in addition to those selected in the "Tables (modify)" box - may be viewed and listed for the user. He is thus not able to <em>edit</em> the table - only select the records and view the content.\r\nThis list is not so very important. It\'s a pretty rare situation that a user may select tables but not modify them.', '', '', '', 'be_groups:tables_modify,\r\nbe_groups:inc_access_lists', '0', '1011037496');

INSERT INTO sys_tabledescr VALUES ('be_groups', 'non_exclude_fields', 'Certain table fields are not available by default. Those fields can be explicitly enabled for the group members here.', '"Allowed excludefields" allows you to detail the permissions granted to tables. By default all these fields are not available to users but must be specifically enabled by being selected here.\r\nOne application of this is that pages are usually hidden by default and that the hidden field is not available for a user unless he has been granted access by this list of "Allowed excludefields". So the user may create a new page, but cannot un-hide the page. Unless of course he has been assigned the "Page: Hidden" exclude field through one of his member groups.\r\nOf course it does not make any sense to add fields from tables which are not included in the list of table allowed to be modified.', '', '', '', 'be_groups:inc_access_lists', '0', '1011037496');

INSERT INTO sys_tabledescr VALUES ('be_groups', 'hidden', 'Disables a user group.', 'If you disable a user group all user which are members of the group will in effect not inherit any properties this group may have given them.', '', '', '', '', '0', '1011054897');

INSERT INTO sys_tabledescr VALUES ('be_groups', 'lockToDomain', 'Enter the host name from which the user is forced to login.', 'A Typo3 system may have multiple domains pointing to it. Therefore this option secures that users can login only from a certain host name.', '', '', '', 'be_users:lockToDomain,\r\nfe_users:lockToDomain,\r\nfe_groups:lockToDomain', '0', '1011038011');

INSERT INTO sys_tabledescr VALUES ('be_groups', 'groupMods', 'Select available backend modules for the group members.', 'This determines which \'menu items\' are available for the group members.\r\nThis list of modules is added to any modules selected in other member groups of a user as well as the corresponding field of the user himself.', '', '', '', 'be_users:userMods,\r\nbe_groups:inc_access_lists', '0', '1011038011');

INSERT INTO sys_tabledescr VALUES ('be_groups', 'inc_access_lists', 'Select whether Page type, Table, Module and Allowed excludefield access lists are enabled for this group.', '', '', '', '', 'be_groups:pagetypes_select,\r\nbe_groups:tables_modify,\r\nbe_groups:tables_select,\r\nbe_groups:groupMods,\r\nbe_groups:non_exclude_fields\r\n', '0', '1011038011');

INSERT INTO sys_tabledescr VALUES ('be_groups', 'description', 'Enter a short description of the user group, what it is for and who should be members. This is for internal use only.', '', '', '', '', 'fe_groups:description', '0', '1011038011');

INSERT INTO sys_tabledescr VALUES ('be_groups', 'TSconfig', 'Additional configuration through TypoScript style values (Advanced).', '', 'TypoScript style without conditions and constants.', '', '', 'be_users:TSconfig\r\nfe_users:TSconfig\r\nfe_groups:TSconfig\r\npages:TSconfig', '0', '1011038011');

INSERT INTO sys_tabledescr VALUES ('be_groups', 'hide_in_lists', 'This option will prevent the user group from showing up in lists, where user groups are selected.', 'This will affect the list of user groups in the Task Center To-Do and Messages parts as well as the Web>Access module.\r\nThe option is extremely useful if you have general user groups defining some global properties which all your users are members of. Then you would probably not like all those users to \'see\' each other through the membership of this group, for instance sending messages or To-Dos to each other. And this is what is option will prevent.', '', '', '', '', '0', '1011038011');

INSERT INTO sys_tabledescr VALUES ('be_groups', 'subgroup', 'Select backend user groups which are automatically included for members of this group.', 'The properties or subgroups are added to the properties of this groups and basically they will simply be added to the list of member groups of any user which is a member of this group.\r\nThis feature provides a great way to create \'Supervisor\' user groups.', '', '', '', '', '0', '1011038011');

INSERT INTO sys_tabledescr VALUES ('sys_filemounts', 'title', 'Enter a title for the filemount', '', '', '', '', '', '0', '1011038268');

INSERT INTO sys_tabledescr VALUES ('sys_filemounts', 'path', 'Enter the path of the filemount, relative or absolute depending on the settings of BASE.', 'If BASE is set to relative, the path mounted is found in the subfolder "fileadmin/" of the website. Then you should enter the subfolder in "fileadmin/" as path. For instance if you want to mount access to "fileadmin/user_uploads/all/" then enter the value "user_uploads/all" as the value of PATH.\r\nIf BASE is absolute you should enter the absolute path on the server, eg. "/home/ftp_upload" or "C:/home/ftp_upload". \r\n\r\n<strong>Notice:</strong> In any case, make sure the webserver user which PHP is running as has <em>at least</em> read-access to the path. If not, the mount will simply not appear without any warnings. \r\nIf you have problems - especially with absolute mounts - try to mount something "simple" like a relative path in fileadmin. If that is working well, try the absolute path.\r\n\r\nYour PHP-configuration may also impose other restrictions on you if safe-mode like features are enabled. Then use relative paths.', '', '', '', 'sys_filemounts:base', '0', '1011038268');

INSERT INTO sys_tabledescr VALUES ('sys_filemounts', 'hidden', 'Use this option to temporarily disable the filemount.', 'All backend users using the mount will not have access anymore. This includes \'Admin\'-users.', '', '', '', '', '0', '1011038268');

INSERT INTO sys_tabledescr VALUES ('sys_filemounts', 'base', 'Determines whether the value of the PATH field is to be recognized as an absolute path on the server or a path relative to the fileadmin/ subfolder to the website.', '', '', '', '', 'sys_filemounts:path', '0', '1011038268');

INSERT INTO sys_tabledescr VALUES ('sys_domain', 'domainName', 'Enter the host name, eg. "www.my-domain.com".', 'Before a domain record will have any effect you must make sure that the domains A-record is pointed towards the server and that your server is setup to direct requests to this Typo3 database. So basically before entering anything here, make sure a request to the domain you wish to enter arrives at the root of the current page tree. \r\nWhen you create a domain record and the host name in it (be it a real domain name or an IP address) matches the hostname of the URL, the TypoScript based front end will limit the available page to only those in the page tree from this point and outwards. In effect you will \'arrive\' at the page where this record is and furthermore users cannot see pages with ids outside this branch.\r\n\r\n<strong>Notice, </strong>if you run Typo3 from a subdirectory of the domain name, you must include the subdirectory as well, eg. "www.my-domain.org/typo3_subdir/"\r\n', '', '', '', '', '0', '1011038268');

INSERT INTO sys_tabledescr VALUES ('sys_domain', 'redirectTo', 'All requests to the domain will be redirected to this URL, if any value is entered.', 'Make sure to prepend "http://" or equally to the value! Basically a "Location: " header is just issued to this value!\r\n<strong>Warning:</strong> You cannot use this to \'redirect\' to a specific page id within the same domain as the record has! If you do that it\'ll just go into an endless loop because the redirect is redirecting to itself!\r\n\r\nGood Example:\r\nDomain: "my-domain.org"\r\nRedirect: "www.my-domain.org"\r\n\r\nThis redirects requests to what is infact another domain name, namely "www.my-domain.org"\r\n\r\nBad Example:\r\nDomain: "www.my-domain.org"\r\nRedirect: "www.my-domain.org/?id=10"\r\n\r\nThis will go into an eternal loop because the domain name has in fact not changed!\r\n\r\n\r\nVirtual directories:\r\nIf you\'re using Typo3 on a unix computer you can make \'virtual subdirectories\' to a website, still within the same database. See document list for more information. In that case you should enter the hostname with the subdirectory appended, like this: "my-domain.org/subdir/". If the combination of host name and current subdirectory matches the value, the domain record will be correctly detected and limit page access to the page tree below the Domain record.\r\n', '', '', '', '', '0', '1011038268');

INSERT INTO sys_tabledescr VALUES ('sys_domain', 'hidden', 'Use this option to temporarily disable the Domain record.', '', '', '', '', '', '0', '1011038268');

