INSERT INTO sys_tabledescr VALUES ('sys_action', 't1_copy_of_user', 'Insert a current Backend user which will be used as a template for the new users created. ', 'All values are copied to the new user, except username, password, name and email is of course overridden.\r\nIf the template user is a member of a group which is not defined among the "Groups which may be assigned through the action" that group is still set for the user and cannot be removed by the user carrying out the action.', '', '', '', '', '0', '1011038674');

