INSERT INTO sys_tabledescr VALUES ('sys_workflows', 'tablename', 'Enter the table in which the workflow should create a record when it\'s begun.', '<strong>Notice:</strong> The user/group which the workflow task is assigned to should be allowed to work on this table and furthermore have regular edit permissions set up in the Working area. In addition records from this table should be allowed to be created in the Working Area as defined by the Pagetype of the Working Area page.', '', '', '', '', '0', '1011038883');

INSERT INTO sys_tabledescr VALUES ('sys_workflows', 'working_area', 'The page where the new records from the above table are created and edited during the workflow process.', '<strong>Notice: </strong>See Workflow / Table description for information about permission setting!\r\n', '', '', '', 'sys_workflows:tablename', '0', '1011038883');

