INSERT INTO sys_tabledescr_dk VALUES ('pages', 'starttime', '\'Start\'-tiden er den dato, hvor siden vil blive synlig online. Brug dette til at \'publisere\' siden p en bestemt dato.\r\nHvis \'Start\'-tiden ikke er sat, s bliver siden vist jeblikkeligt (medmindre siden p anden vis er skjult).', '', '', '', '0', '0');

