DROP TABLE IF EXISTS sys_tabledescr_local;

CREATE TABLE sys_tabledescr_local (
  tablename varchar(40) DEFAULT '' NOT NULL,
  fieldname varchar(40) DEFAULT '' NOT NULL,
  description tinytext NOT NULL,
  details text NOT NULL,
  syntax text NOT NULL,
  image_descr tinytext NOT NULL,
  serial int(11) DEFAULT '0' NOT NULL,
  tstamp int(11) DEFAULT '0' NOT NULL,
  image tinytext NOT NULL,
  seeAlso text NOT NULL,
  mode tinyint(4) DEFAULT '0' NOT NULL,
  PRIMARY KEY (tablename,fieldname)
);